// --- analytically box-filtered checkerboard ---
float checkersTextureGradBox(vec2 p, vec2 d_x, vec2 d_y)
{
    // filter kernel - iso
    vec2 w = max(abs(d_x), abs(d_y)) + 0.01;
    // analytical integral (box filter)
    vec2 i = 2.*(abs(fract((p - .5*w) * .5) - .5)
                -abs(fract((p + .5*w) * .5) - .5)) / w;
    // xor pattern
    return .5 - .5*i.x*i.y;                  
}
// my wrapper computes derivatives
float checkerIq(vec2 p) // unscaled matches the gpu gem better
{
	return checkersTextureGradBox(p, dFdx(p), dFdy(p));
}
// rescaled; aliases less but gets excessively blurry when very close
float checkerIqx4(vec2 p)
{
	return checkersTextureGradBox(p, dFdx(p) * 4., dFdy(p) * 4.);
}
