// Gaussian filtered checkerboard
const float k = 4.; //3.; //
float checkerOneshade(vec2 p)
{
    vec2 w = clamp(vec2(fwidth(p.x), fwidth(p.y)), 1e-9, 2.); //vec2(.1,.01); // TODO dFdx, dFdy, may need to rotate to grid somehow
    p = mod(p, 2.);
    float s = 1.0 - 2. * mod(k, 2.);
    float u = 0., v = 0.;
    for (float n = -k; n <= k; ++n) {
        u += s * erf((p.x - n) / w.x);
        v += s * erf((p.y - n) / w.y);
        s = -s;
    }
    return (1. - u * v) / 2.;
}
