const float maxd = 85000.;

// halfspace
float tracePlaneY(vec3 rp, vec3 rd)
{
    return rp.y <= 0. ? 0. :
    	rd.y >= 0. ? -1. : //maxd :
    	rp.y / -rd.y;
}

void rot(inout vec2 v, float radian)
{
    float c = cos(radian), s = sin(radian);
    v *= mat2(c, s,-s, c);
}

vec3 CameraRay(vec2 p)
{
    vec2 q = (2. * p - iResolution.xy) / iResolution.y;
    vec3 rd = normalize(vec3(q / 2., 1.));
    float pitch = -.4, yaw = iTime * -.04;
    rot(rd.yz, pitch);
    rot(rd.xz, yaw);
    return rd;
}

bool key(int k) 
{
    return texelFetch(iChannel3, ivec2(k, 0), 0).x > .5;
}

void mainImage(out vec4 c, vec2 p)
{
    // a simple camera
    vec3 rp = vec3(0,1,0);
    rp.y += sin(.07 * iTime) * .99; // bob slowly up and down
    vec3 rd = CameraRay(p);

    // Y plane distance
    float d = maxd;
    float dplane = tracePlaneY(rp, rd);
    vec2 cc = vec2(1., 0.);
    if (dplane >= 0.) {
        d = min(d, dplane);
	    cc = rp.xz + rd.xz * d; // grid uv at hit point
    	cc *= 4.; // tiling
    }

    // number keys 1 thru 3 control bit of a binary number mode selector
    mode = 0;
    for (int i = 1; i < 10; ++i) 
        if (key(48 + i)) mode = i;

    // split screen at mouse click
    float splits = p.x - (iMouse.z == 0. ? .5 * iResolution.x : iMouse.x);
    bool left = splits < 0.;
    // depending on last mouse click, split screen between two different functions
    float check = left 
        ? ImageL(cc)
        : ImageR(cc);

    c.rgb = mix(C1, C2, check); // colorize

    float fog = 1.0 - exp2(-.02*d); //0.; //
    c.rgb = mix(c.rgb, Csky, fog);
    // green vertical line at split
    c.rgb = mix(c.rgb, vec3(.0,.5,.0), exp2(-2. * abs(splits)));

    c.rgb = pow(c.rgb, vec3(1./2.2)); // to srgb gamut
    c.a = 1.;
}


    // no actual lighting in this simple test,
    // but a few dot products would do it.

//  if (!(d >= 0.)) d = maxd; // handle possible nan
//  float dflat = d / rd.z;
//  float aa = 1.0 - exp2(-.03*d); // HACK
