#define CO vec3(0.0, 0.0, 5.0)

#define RME 0.01
#define RMM 10.0

vec2 uv;

float map(vec3 p) {
    float w = 2.0 * texture(iChannel0, vec2(length(uv))).x;

    float bigS = distance(vec3(0.0, 0.0, -2.0), p) - 0.25 - 0.25 * texture(iChannel0, vec2(length(uv))).x; 
    bigS += sin(w * p.x) * sin(w * p.y) * sin(w * p.z) * 0.5;

    w *= 0.5;

    float smallS1 = distance(vec3(sin(iTime/7.0), sin(iTime*2.4),  -2.0 + cos(iTime*2.0)), p) - 0.1;
    smallS1 += sin(w * p.x) * sin(w * p.y) * sin(w * p.z) * 0.25;

    float smallS2 = distance(vec3(sin(iTime*2.2), sin(iTime/3.0),  -2.0 + cos(iTime*2.0)), p) - 0.1;
    smallS2 += sin(w * p.x) * sin(w * p.y) * sin(w * p.z) * 0.25;

    float smallS3 = distance(vec3(sin(iTime*2.3), sin(iTime*2.5),  -2.0 + cos(iTime/5.0)), p) - 0.1;
    smallS3 += sin(w * p.x) * sin(w * p.y) * sin(w * p.z) * 0.25;

    return min(bigS, min(smallS1, min(smallS2, smallS3)));
}

float rayMarch(vec3 ro, vec3 rd, out vec3 hitP, out float steps){
    steps = 0.0;
    for(float d = 0.0; d < RMM;){
        ++steps;
     	float md = map(hitP = (ro + rd * d));
        if(md <= RME) return 1.0;
        d += md;
    }
    return 0.0;
}

vec3 hsv2rgb(vec3 c) {
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    uv = fragCoord/iResolution.xy - 0.5;
    uv.x *= iResolution.x / iResolution.y;

    vec3 hitPoint;
    float steps, hit = rayMarch(CO, normalize(vec3(uv, 0.0) - CO), hitPoint, steps);

    float m = texture(iChannel0, vec2(length(uv))).x;

    fragColor = vec4(0.0);
    fragColor.rgb += hsv2rgb(vec3((sin(iTime * (1.0 + hit))+1.0)/2.0, 0.5 + m, (steps / 15.0 / (1.0 + m * 2.0))));
}

// fork of pimp that ass - https://www.shadertoy.com/view/WtV3Wc - added jiggle anim + various other tweaks :)
// basically a combination of:
// - https://mathematica.stackexchange.com/a/66564 by Nikolai Mikuszeit
// - https://www.shadertoy.com/view/Ws3SDl by Inigo Quilez

#define AA 2

float pi = 3.14159265, tau = 6.2831853;

float box (in float x, in float x1, in float x2, in float a, in float b) {
	return tanh(a * (x - x1)) + tanh(-b * (x - x2));
}
float sdSphere( vec3 p, float s )
{
    return length(p)-s;
}
float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float ex (in float z, in float z0, in float s) {
    return exp(-(z - z0) * (z - z0) / s);
}

float r(in float z, in float x) {
    float s = sin (tau * x), c = cos(tau * x),
		c2 = cos (pi * x), t4z = tanh(4. * z);
    return /* body */.4 * (1.0 - .4 * ex(z, .8, .15) +
		s * s + .6 * ex(z, .8, .25) * c * c + .3 * c) *
		0.5 * (1. + t4z) + /* legs */ (1. - .2 * ex(z, -1.3, .9)) *
		0.5 * (1. - t4z) * (.5 * (1. + s * s + .3 * c) *
		(pow(abs(s), 1.3) + .08 * (1. + t4z) ) ) +
		/* improve butt */ .13 * box(c2, -.45, .45, 5., 5.) *
		box(z, -.5, .2, 4., 2.) - 0.1 * box(c2, -.008, .008, 30., 30.) *
		box(z, -.4, .25, 8., 6.) - .05 * pow(abs(sin(pi * x)), 16.) * box(z, -.55, -.35, 8., 18.);
}

// $1M question: how close are we to ParametricPlot3D[...] surface?
float sd( in vec3 p )
{
	/* shift butt belly */
	float dx = .1 * exp(-pow((p.z-.8),2.)/.6) - .18 * exp(-pow((p.z -.1),2.)/.4);

    // on the surface, we have:
    // p.x = r * cos + dx
	// p.y = r * sin

    float jiggle = p.z*1.75;
    float jsize = 0.5;
    if (iMouse.z>0.5)
    {
        jiggle+=p.y*.5;
        jsize+=.175;
    }

	dx *= 1.5+(sin(jiggle+iTime*9.65)*jsize);

	float angle = atan(p.y, p.x - dx);
	float r_expected = r(p.z, angle / tau);
//	float d1 = (.5 + .5 * smoothstep(.4,1.,p.z)) * (length(vec2(p.y, p.x - dx)) - r_expected);
	float d1 = (length(vec2(p.y, p.x - dx)) - r_expected)*0.5;

    p.x -= dx;
	float d2 = sdSphere(p+vec3(-0.35,0.4,-1.875),0.4);
	float d3 = sdSphere(p+vec3(-0.35,-0.4,-1.875),0.4);
    d2 = smin(d2,d3,0.35);
    return smin(d1,d2,0.07);
    //return min(d1,min(d2,d3));
}


float map( in vec3 pos )
{
    return sd (pos.zxy);
}

// http://iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 calcNormal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.5773;
    const float eps = 0.001;
    return normalize( e.xyy*map( pos + e.xyy*eps ) + 
					  e.yyx*map( pos + e.yyx*eps ) + 
					  e.yxy*map( pos + e.yxy*eps ) + 
					  e.xxx*map( pos + e.xxx*eps ) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     // camera movement	
	float an = sin(iTime+2.15)*0.7;	//0.5*iTime - 0.8;
    if (iMouse.z>0.5)
        an+=iMouse.x/(iResolution.x*0.25);
    //an+=3.14;

	vec3 ro = vec3( 2.55*sin(an), 0.5, -2.55*cos(an) );
    vec3 ta = vec3( 0.0, 0.5, 0.0 );
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
    vec3 tot = vec3(0.0);


    vec2 p1 = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
    float val = sin(iTime+p1.x+p1.y*20.0)*(0.8+sin(p1.y+iTime*3.0)*0.3);
    val = clamp(val,0.0,2.0);
    vec3 bcol = vec3(val,val*0.2,val*0.1);

    #if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
        #else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
        #endif

        vec3 col = bcol;
        // raymarch
        if (abs(p.x)<0.8)
        {
            // create view ray
            vec3 rd = normalize( p.x*uu + p.y*vv + 1.8*ww );

            const float tmax = 5.0;
            float t = 0.0;
            for( int i=0; i<150; i++ )
            {
                vec3 pos = ro + t*rd;
                float h = map(pos);
                if( h<0.0001 || t>tmax ) break;
                t += h;
            }
            // shading/lighting	
            if( t<tmax )
            {
                vec3 pos = ro + t*rd;
                vec3 nor = calcNormal(pos);
                float dif = clamp( dot(nor,vec3(0.7,0.6,0.4)), 0.0, 1.0 );
                float amb = 0.5 + 0.5*dot(nor,vec3(0.0,0.8,0.6));
                col = vec3(0.3,0.15,0.1)*amb + vec3(0.8,0.5,0.2)*dif;
                col += pow(dif,16.0);
            }
        }

        // gamma        
        //col = sqrt( col );
	    tot += col;
    #if AA>1
    }
    tot /= float(AA*AA);
    #endif
    // gamma        
    tot = sqrt( tot );

   tot *= 1.0 - 0.2*dot(p1,p1);		// vignette

	fragColor = vec4( tot, 1.0 );
}

float h(vec2 p)
{

	float ba = texture(iChannel0, p * 0.055).x ;    
    float b = (sin(p.x * 2.))  ;
    b *= (cos(p.y)) / 2.5 ;
    b*=  ba * sin( cos( 2.)) * abs(sin(1.) + 1.25);

    return -b - 1.2;
}


float sdPlane(vec3 pos)
{

    return pos.y - (h(pos.xz));
}

float sphereDistance(vec3 pos, float radius)
{
	// copy to local scope of SDF 
    vec3 localPos = pos;
    float localRadius = radius;
    return length(localPos) - localRadius;

}

float map(vec3 pos)
{
    vec3 q = pos;

    float radius = .5;

    float r = sin(q.x);


	//float d = sphereDistance(vec3(q.x + .5, q.y - .2 , q.z), .25);
    float rad = .75;
    rad *=  abs(sin(q.z - iTime * 5.))
        * cos(q.x + .5 * 1.5) 
        * (abs(sin(q.z * 3.5)) + abs(cos(q.y)) ); // base

    float d1 = sphereDistance(q, 1.);    

    float d2 = sdPlane(pos);


    return min(d1, d2);
}

float castRay(vec3 ro, vec3 rd)
{
    float t = 0.0;
    for(int i = 0; i < 100; i++)
    {
    	vec3 pos = ro + t * rd;

        float h = map(pos);
        if(h < 0.00001)
        {
        	break;
        }
        t += h;

        if(t > 20.0) break;

    }

    if(t > 20.0) t = -1.0;

    return t;
}

vec3 calcNormal(vec3 pos)
{
    vec2 e = vec2(0.0001, 0.0);

	return normalize(vec3(
        					map(pos+e.xyy) - map(pos-e.xyy),
        					map(pos+e.yxy) - map(pos-e.yxy),
        					map(pos+e.yyx) - map(pos-e.yyx)
    				));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 p = (2.0 * fragCoord- iResolution.xy) / iResolution.y;

	/*
vec3 ro = vec3(0.0,0.0,1.0);
   vec3 rd = normalize(vec3(p, -1.5));
	*/

    // SKY 
    float speed = 00.00001;
    float scale = 0.0015;
    vec2 sp = fragCoord * scale; 
    for(int i=1; i<12; i++){
        sp.x+=(cos(iTime * 0.25)) * 0.75/float(i)*sin(float(i)*2.*sp.y+iTime*speed)+ (iTime * 69.)/1000.;
        sp.y+=cos(iTime) * 0.15/float(i)*cos(float(i)*5.*sp.x+iTime*speed)+(iTime * .69)/1000.;
    }
    float r=cos(sp.x+sp.y+.025)*.9 + 0.33;
    float g=sin(sp.x+sp.y+1.)*.55+.5;
    float b=(sin(p.x * 1. +p.y)+cos(sp.x+(sp.y)))*.5+.28;
    vec3 color = vec3(b,g,1.0);
    color -= 0.002;

	vec3 col = vec3(color);


    // Camera Look at

    float an = iTime * .5;
    vec3 ro = vec3(3. * sin(an),.75, 5. *  cos(an));

	float cameraZoom = 1.5;
	vec3 target = vec3(0.1, 0., 0.);

	vec3 ww = normalize(target - ro);
	vec3 uu = normalize(cross(ww, vec3(0.,1.,0.)));
	vec3 vv = normalize(cross(uu, ww));

	vec3 rd = normalize(p.x * uu + p.y * vv + cameraZoom * ww);

    // Rendering part
	float t = castRay(ro, rd);


    if(t > 0.5)
    {

        vec3 pos = ro + t * rd;
        vec3 nor = calcNormal(pos);
       	vec3 sundir = normalize(vec3(0.8, 0.4, 0.2));


        float sundif = clamp(dot(nor, sundir), 0.0, 1.0);
        float sun_sha = smoothstep(castRay(pos + nor * 0.001, sundir), 0.0, 1.0);
        float sky_dif = clamp(dot(nor,vec3(0.0,1.0,0.0)), 0.0, 1.0);;

        col = vec3(1.0, 0.7, 0.5) * sundif * sun_sha * .7;
        col += vec3(.0, 0.2, .5) * sky_dif * 2.5;
    }
    // Output to screen
    fragColor = vec4(sqrt(col),1.0);
}

// GLSL textureless classic 3D noise "cnoise",
// with an RSL-style periodic variant "pnoise".
// Author:  Stefan Gustavson (stefan.gustavson@liu.se)
// Version: 2011-10-11
//
// Many thanks to Ian McEwan of Ashima Arts for the
// ideas for permutation and gradient selection.
