vec3 mod289(vec3 x)
{
  return x - floor(x * (1.0 / 289.0)) * 289.0;
}

vec4 mod289(vec4 x)
{
  return x - floor(x * (1.0 / 289.0)) * 289.0;
}

vec4 permute(vec4 x)
{
  return mod289(((x*34.0)+1.0)*x);
}

vec4 taylorInvSqrt(vec4 r)
{
  return 1.79284291400159 - 0.85373472095314 * r;
}

vec3 fade(vec3 t) {
  return t*t*t*(t*(t*6.0-15.0)+10.0);
}

// Classic Perlin noise
float cnoise(vec3 P)
{
  vec3 Pi0 = floor(P); // Integer part for indexing
  vec3 Pi1 = Pi0 + vec3(1.0); // Integer part + 1
  Pi0 = mod289(Pi0);
  Pi1 = mod289(Pi1);
  vec3 Pf0 = fract(P); // Fractional part for interpolation
  vec3 Pf1 = Pf0 - vec3(1.0); // Fractional part - 1.0
  vec4 ix = vec4(Pi0.x, Pi1.x, Pi0.x, Pi1.x);
  vec4 iy = vec4(Pi0.yy, Pi1.yy);
  vec4 iz0 = Pi0.zzzz;
  vec4 iz1 = Pi1.zzzz;

  vec4 ixy = permute(permute(ix) + iy);
  vec4 ixy0 = permute(ixy + iz0);
  vec4 ixy1 = permute(ixy + iz1);

  vec4 gx0 = ixy0 * (1.0 / 7.0);
  vec4 gy0 = fract(floor(gx0) * (1.0 / 7.0)) - 0.5;
  gx0 = fract(gx0);
  vec4 gz0 = vec4(0.5) - abs(gx0) - abs(gy0);
  vec4 sz0 = step(gz0, vec4(0.0));
  gx0 -= sz0 * (step(0.0, gx0) - 0.5);
  gy0 -= sz0 * (step(0.0, gy0) - 0.5);

  vec4 gx1 = ixy1 * (1.0 / 7.0);
  vec4 gy1 = fract(floor(gx1) * (1.0 / 7.0)) - 0.5;
  gx1 = fract(gx1);
  vec4 gz1 = vec4(0.5) - abs(gx1) - abs(gy1);
  vec4 sz1 = step(gz1, vec4(0.0));
  gx1 -= sz1 * (step(0.0, gx1) - 0.5);
  gy1 -= sz1 * (step(0.0, gy1) - 0.5);

  vec3 g000 = vec3(gx0.x,gy0.x,gz0.x);
  vec3 g100 = vec3(gx0.y,gy0.y,gz0.y);
  vec3 g010 = vec3(gx0.z,gy0.z,gz0.z);
  vec3 g110 = vec3(gx0.w,gy0.w,gz0.w);
  vec3 g001 = vec3(gx1.x,gy1.x,gz1.x);
  vec3 g101 = vec3(gx1.y,gy1.y,gz1.y);
  vec3 g011 = vec3(gx1.z,gy1.z,gz1.z);
  vec3 g111 = vec3(gx1.w,gy1.w,gz1.w);

  vec4 norm0 = taylorInvSqrt(vec4(dot(g000, g000), dot(g010, g010), dot(g100, g100), dot(g110, g110)));
  g000 *= norm0.x;
  g010 *= norm0.y;
  g100 *= norm0.z;
  g110 *= norm0.w;
  vec4 norm1 = taylorInvSqrt(vec4(dot(g001, g001), dot(g011, g011), dot(g101, g101), dot(g111, g111)));
  g001 *= norm1.x;
  g011 *= norm1.y;
  g101 *= norm1.z;
  g111 *= norm1.w;

  float n000 = dot(g000, Pf0);
  float n100 = dot(g100, vec3(Pf1.x, Pf0.yz));
  float n010 = dot(g010, vec3(Pf0.x, Pf1.y, Pf0.z));
  float n110 = dot(g110, vec3(Pf1.xy, Pf0.z));
  float n001 = dot(g001, vec3(Pf0.xy, Pf1.z));
  float n101 = dot(g101, vec3(Pf1.x, Pf0.y, Pf1.z));
  float n011 = dot(g011, vec3(Pf0.x, Pf1.yz));
  float n111 = dot(g111, Pf1);

  vec3 fade_xyz = fade(Pf0);
  vec4 n_z = mix(vec4(n000, n100, n010, n110), vec4(n001, n101, n011, n111), fade_xyz.z);
  vec2 n_yz = mix(n_z.xy, n_z.zw, fade_xyz.y);
  float n_xyz = mix(n_yz.x, n_yz.y, fade_xyz.x);
  return 2.2 * n_xyz;
}

// FBM
float fbm (in vec3 p) {
    float ret = -1.;
    float freq = 1.;
    float amp = 1.;
    for(int i=0; i<8; i++) {
	    float n = cnoise(p*freq+vec3(0,(iTime*.2),0)*2.);
        ret = ret + n * amp;
        amp *= .5;
        freq *= 1.5 + n * .1;
    }
	return ret;
}

float f(vec3 p) {
    float n = fbm(p);
    return p.y - 1. - exp(n) * .5;
}



vec3 calcNormal(vec3 p, float h) // for function f(p)
{
    const vec2 k = vec2(1,-1);
    return normalize( k.xyy*f( p + k.xyy*h ) + 
                      k.yyx*f( p + k.yyx*h ) + 
                      k.yxy*f( p + k.yxy*h ) + 
                      k.xxx*f( p + k.xxx*h ) );
}

void pR(inout vec2 p, float a) {
	p = cos(a)*p + sin(a)*vec2(p.y, -p.x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy * 2. - 1.;
    uv.x *= iResolution.x / iResolution.y;
    vec3 ro = vec3(0, 2, -4);
    vec3 rd = normalize(vec3(uv, 1.77) + vec3(0,-1.5,0));
    /*
    pR(ro.xz, iTime*.1);
    pR(rd.xz, iTime*.1);
	//*/
    float t = 0., d;


    for(int i=0; i<16; i++) {
        d = f(ro+rd*t);
        //if(d<0.) break;
        t+=d;
    }

    vec3 n = calcNormal(ro+rd*t, t*t*.0004);
    // Time varying pixel color
    vec3 col = -n.yyy *n.y * .5 + .5;
    col *= vec3(8,4,16);
	col /= t;
    col = pow(col, vec3(.4545));

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define PI 3.1415926538

float metaCalc(vec2 uv, vec3 pos)
{
    return 1.0/((pos.x*pos.x)+(pos.y*pos.y)+(pos.z*pos.z));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // init stuff
    vec3 pos = vec3(0.2+0.1*cos(iTime),0.3*cos(iTime),0.5);
    vec3 pos2 = vec3(0.2+-0.1,0.2+-0.3,0.0)*sin(iTime);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv -= 0.5; // set center/origo to vec2(0.0,0.0);    
    uv.x *= iResolution.x/iResolution.y; // how do you really do aspect ratio?  

    vec3 varCol = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // metaball
    float amplitude1 = metaCalc(uv, vec3(uv.x - pos.x,uv.y - pos.y,pos.z));
    float amplitude2 = metaCalc(uv, vec3(uv.x - pos2.x,uv.y - pos2.y, pos.z));

    float amplitude = amplitude1+amplitude2;

    if(amplitude > 6.0)
    {
       float amplitudeColor = amplitude/10.0;
       float depthColor = amplitudeColor*(pos.z*amplitude1+pos2.z*amplitude2);
       fragColor = vec4(vec3(depthColor), 0.0);  
    }
    else
    {  

        fragColor = vec4(vec3(0.0), 1.0);
    }

    // twister
    #define thick 0.002

    vec4 white = vec4(1.0,1.0,1.0,1.0);
    vec4 red = vec4(1.0,0.0,0.0,1.0);

    vec4 green = vec4(0.0,1.0,0.0,1.0);
    vec4 blue = vec4(0.0,0.0,1.0,1.0);

    float theta = sin(iTime)*uv.y*10.0;
/*
    float x0 = -0.6+0.1*sin(theta);
    float x1 = -0.6+0.1*sin(theta+90.0); // sin a+90
    float x2 = -0.6+0.1*sin(theta+180.0); // sin a+180
    float x3 = -0.6+0.1*sin(theta+270.0); // sin a+180
  */  

    float x0 = -0.6+0.1*sin(theta);
    float x1 = -0.6+0.1*sin(theta+PI*0.5); // sin a+90
    float x2 = -0.6+0.1*sin(theta+PI*1.0); // sin a+180
    float x3 = -0.6+0.1*sin(theta+PI*1.5); // sin a+270

    // from https://www.shadertoy.com/view/lsX3WM 
    if (x0 < x1) {
		if ((abs(uv.x - x0) < thick) || (abs(uv.x - x1) < thick)) {
			fragColor = white;
		} else {
			if ((uv.x > x0) && (uv.x < x1)) {
				fragColor = red-varCol.r;
			}
		}
	}
	if (x1 < x2) {
		if ((abs(uv.x - x1) < thick) || (abs(uv.x - x2) < thick)) {
			fragColor = white;
		} else {
			if ((uv.x > x1) && (uv.x < x2)) {
				fragColor = green-varCol.g;
			}
		}
	}
	if (x2 < x3) {
		if ((abs(uv.x - x2) < thick) || (abs(uv.x - x3) < thick)) {
			fragColor = white;
		} else {
			if ((uv.x > x2) && (uv.x < x3)) {
				fragColor = blue-varCol.b;
			}
		}
	}
	if (x3 < x0) {
		if ((abs(uv.x - x3) < thick) || (abs(uv.x - x0) < thick)) {
			fragColor = white;
		} else {
			if ((uv.x > x3) && (uv.x < x0)) {
				fragColor = vec4(uv,0.2+0.5*sin(iTime+135.0),1.0);
			}
		}
	}

}

float sdPlane( vec3 p )
{
	return p.y;
}

float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

//----------------------------------------------------------------------

float opS( float d1, float d2 )
{
    return max(-d2,d1);
}

vec2 opU( vec2 d1, vec2 d2 )
{
	return (d1.x<d2.x) ? d1 : d2;
}

vec3 opRep( vec3 p, vec3 c )
{
    return mod(p,c)-0.5*c;
}

vec3 opRepXZ( vec3 p, vec3 c )
{
    return vec3(mod(p.x,c.x)-0.5*c.x, p.y, mod(p.z,c.z)-0.5*c.z);
}

// polynomial smooth min (k = 0.1);
float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

// exponential smooth min (k = 32);
float smin2( float a, float b, float k )
{
    float res = exp( -k*a ) + exp( -k*b );
    return -log( res )/k;
}

vec3 opTwist( vec3 p )
{
    float  c = cos(10.0*p.y+10.0);
    float  s = sin(10.0*p.y+10.0);
    mat2   m = mat2(c,-s,s,c);
    return vec3(m*p.xz,p.y);
}

vec3 rX(in vec3 p, float a) {
	float c,s;vec3 q=p;
	c = cos(a); s = sin(a);
	return vec3(p.x, c * q.y - s * q.z, s * q.y + c * q.z);
}

vec3 rY(in vec3 p, float a) {
	float c,s;vec3 q=p;
	c = cos(a); s = sin(a);
	return vec3(c * q.x + s * q.z, p.y, -s * q.x + c * q.z);
}

//----------------------------------------------------------------------

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

vec2 map(in vec3 pos) {
    vec2 res = vec2(sdPlane(pos), 1.0);

    // bledng cubes by groups of 9
    float gap = 1.2;

    vec3 cubeSize = vec3(0.5);
    for(float i=-1.; i<=1.; i+=1.) {
        for(float j=-1.; j<=1.; j+=1.) {
            vec3 p = opRepXZ(pos, vec3(gap, 0., gap))+vec3(i*gap, 0., j*gap);
//            vec3 p = pos+vec3(i*gap, 0., j*gap);
            float rotX = iTime+pos.x*0.3;
	        float rotY = iTime+pos.z*0.3;
            vec3 p2 = rY(rX(p, rotX), rotY);

            float dst = sdBox(p2, cubeSize);

            // smooth min
            float a = res.x;
            float b = dst;
            // blending power : 0. to 1.
            float k = .5;
            float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );

		    res = vec2(mix( b, a, h ) - k*h*(1.0-h), 3.0);
        }
    }

    return res;
}

vec2 castRay( in vec3 ro, in vec3 rd )
{
    float tmin = 1.0;
    float tmax = 20.0;

#if 0
    float tp1 = (0.0-ro.y)/rd.y; if( tp1>0.0 ) tmax = min( tmax, tp1 );
    float tp2 = (1.6-ro.y)/rd.y; if( tp2>0.0 ) { if( ro.y>1.6 ) tmin = max( tmin, tp2 );
                                                 else           tmax = min( tmax, tp2 ); }
#endif

	float precis = 0.002;
    float t = tmin;
    float m = -1.0;
    for( int i=0; i<50; i++ )
    {
	    vec2 res = map( ro+rd*t );
        if( res.x<precis || t>tmax ) break;
        t += res.x;
	    m = res.y;
    }

    if( t>tmax ) m=-1.0;
    return vec2( t, m );
}


float softshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
	float res = 1.0;
    float t = mint;
    for( int i=0; i<16; i++ )
    {
		float h = map( ro + rd*t ).x;
        res = min( res, 8.0*h/t );
        t += clamp( h, 0.02, 0.10 );
        if( h<0.001 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );

}

vec3 calcNormal( in vec3 pos )
{
	vec3 eps = vec3( 0.001, 0.0, 0.0 );
	vec3 nor = vec3(
	    map(pos+eps.xyy).x - map(pos-eps.xyy).x,
	    map(pos+eps.yxy).x - map(pos-eps.yxy).x,
	    map(pos+eps.yyx).x - map(pos-eps.yyx).x );
	return normalize(nor);
}

float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float hr = 0.01 + 0.12*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = map( aopos ).x;
        occ += -(dd-hr)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 );    
}




vec3 render( in vec3 ro, in vec3 rd )
{ 
    vec3 col = vec3(0.8, 0.9, 1.0);
    vec2 res = castRay(ro,rd);
    float t = res.x;
	float m = res.y;
    if( m>-0.5 )
    {
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal( pos );
        vec3 ref = reflect( rd, nor );

        // material        
		col = -0.15 + 0.1*sin( vec3(10.25,10.08,10.50)*(m-1.0) );

        if( m<1.5 )
        {

            float f = mod( floor(5.0*pos.z) + floor(5.0*pos.x), 2.0);
            col = 0.4 + 0.1*f*vec3(1.0);
        }

        // lighitng        
        float occ = calcAO( pos, nor );
		vec3  lig = normalize( vec3(0.2, sin(0.7), 0.5) );
		float amb = clamp( 0.5+0.5*nor.y, 0.0, 1.0 );
        float dif = clamp( dot( nor, lig ), 0.0, 1.0 );
        float bac = clamp( dot( nor, normalize(vec3(-lig.x,0.0,-lig.z))), 0.0, 1.0 )*clamp( 1.0-pos.y,0.0,1.0);
        float dom = smoothstep( -0.1, 0.1, ref.y );
        float fre = pow( clamp(1.0+dot(nor,rd),0.0,1.0), 2.0 );
		float spe = pow(clamp( dot( ref, lig ), 0.0, 1.0 ),16.0);

//        dif *= softshadow( pos, lig, 0.02, 2.5 );
//        dom *= softshadow( pos, ref, 0.02, 2.5 );

		vec3 brdf = vec3(0.0);
        brdf += 1.20*dif*vec3(1.00,0.90,0.60);
		brdf += 1.20*spe*vec3(1.00,0.90,0.60)*dif;
        brdf += 0.30*amb*vec3(0.50,0.70,1.00)*occ;
        brdf += 0.40*dom*vec3(0.50,0.70,1.00)*occ;
        brdf += 0.30*bac*vec3(0.25,0.25,0.25)*occ;
        brdf += 0.40*fre*vec3(1.00,1.00,1.00)*occ;
		brdf += 0.02;
		col = col*brdf;

    	col = mix( col, vec3(0.8,0.9,1.0), 1.0-exp( -0.0005*t*t ) );

    }

	return vec3( clamp(col,0.0,1.0) );
}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

