vec2 toComplexCoords( in vec2 fragCoord )
{
    // Get integer version of coords and resolution
    vec2 xy = vec2(int(fragCoord.x), int(fragCoord.y));
    vec2 wh = vec2(int(iResolution.x), int(iResolution.y));
    vec2 ar = vec2(wh.x/wh.y, 1.0);

    // Transformations
    xy -= wh*0.5;		// Center as origin
	xy /= wh;			// To homogeneous coords
    xy *= ar;			// Fix aspect ratio
    xy /= zoom;
    xy += center;
    return xy;
}


