// GLOBALS
vec3 lightDir  = vec3(-1.0, -1.0, 1.0);
vec4 backColor = vec4(0.2, 0.1, 0.0, 1.0);

vec3 camPos = vec3(0.0, 1.5, 5.0);
vec3 camDir = vec3(0.0, -0.3, -1.0);

// SDF GLOBALS
float MARCH_START = 0.001;
int MAX_MARCHING_STEPS = 300;
float EPSILON = 0.01;
float MARCH_END = 100.0;

// function that returns just a hit: yes or no
bool hit_sphere( vec3 center, float radius, vec3 ray, vec3 camera ){

    vec3 oc = center - camera;
    float a = dot(ray, ray);
    float b = 2.0 * dot(oc, ray);
    float c = dot(oc,oc) - radius*radius;
    float discriminant = b*b - 4.0*a*c;

    return (discriminant > 0.0);
}

// hit function - returns .w = 1 if hit and hitpoint's normal in .xzy
vec4 hit_sphere_2( vec3 center, float radius, vec3 ray, vec3 camera ){

    vec4 result = vec4(0.0, 0.0, 0.0, 0.0);

    vec3 shift = camera - center;
    float a = dot(ray, ray); 
    float b = 2.0 * dot( ray, shift );
    float c = dot( shift,shift ) - radius*radius;

    float dicsriminant = b*b - 4.0*a*c;

    float t_0 = ( -b + sqrt( dicsriminant ) )/2.0*a;
    float t_1 = ( -b - sqrt( dicsriminant ) )/2.0*a;

    if( dicsriminant >= 0.0 && t_0 > 0.0){

        vec3 p0 = camera + t_0 * ray;
        result.xyz = normalize(p0-center);
        result.w = 1.0;
        return result;
    }
    else
        return result;
}

// torus SDF
float sdTorus( vec3 point, vec2 t ){

    vec2 q = vec2(length(point.xz)-t.x, point.y);

    return length(q)-t.y;
}

// ray marching function that returns distance
float rayMarch( vec3 camera, vec3 ray, vec3 objectCenter ){
    float depth = MARCH_START;

    for( int i=0; i < MAX_MARCHING_STEPS; i++){
        float distance = sdTorus( camera + depth * ray, vec2(1.2, 0.2) );

        if( distance < EPSILON ){
            return depth;
        }

        depth += distance;

        if( depth >= MARCH_END ){
            return MARCH_END;
        }
    }

    return MARCH_END;
}

vec4 rayMarch_Normal( vec3 camera, vec3 ray, vec3 objectCenter ){

    vec4 normal = vec4 (0.0, 0.0, 0.0, 0.0 );

    float ray_march_distance = rayMarch( camera, ray, vec3(0.0, 0.0, 0.0) );
        if( ray_march_distance < MARCH_END ){

        normal.w = 1.0;

        // calculate normal
        vec3 hit_point = camera + ray_march_distance * ray;
        vec3 p_normal = vec3(
            sdTorus(vec3(hit_point.x + EPSILON,hit_point.y,hit_point.z), vec2(1.2, 0.2))-sdTorus(vec3(hit_point.x - EPSILON,hit_point.y,hit_point.z), vec2(1.2, 0.2)),
            sdTorus(vec3(hit_point.x,hit_point.y + EPSILON,hit_point.z), vec2(1.2, 0.2))-sdTorus(vec3(hit_point.x,hit_point.y - EPSILON,hit_point.z), vec2(1.2, 0.2)),
            sdTorus(vec3(hit_point.x,hit_point.y,hit_point.z + EPSILON), vec2(1.2, 0.2))-sdTorus(vec3(hit_point.x,hit_point.y,hit_point.z - EPSILON), vec2(1.2, 0.2))
        );

        normal.xyz = normalize(p_normal);
        return normal;
    }

    return normal;
}

// hit function - experiment with texture lookup
vec4 hit_sphere_3( vec3 center, float radius, vec3 ray, vec3 camera ){

    vec4 result = vec4(0.0, 0.0, 0.0, 0.0);

    vec3 shift = camera - center;
    float a = dot(ray, ray); 
    float b = 2.0 * dot( ray, shift );
    float c = dot( shift,shift ) - radius*radius;

    float dicsriminant = b*b - 4.0*a*c;

    float t_0 = ( -b + sqrt( dicsriminant ) )/2.0*a;
    float t_1 = ( -b - sqrt( dicsriminant ) )/2.0*a;

    if( dicsriminant >= 0.0 && t_0 > 0.0){

        vec3 p0 = camera + t_0 * ray;

        float fi = atan(p0.z, p0.x);
        float theta = acos( p0.y / radius );
        vec4 color = texture( iChannel0, vec2(fi, theta));
        result.xyz = color.rgb;
        result.w = 1.0;
        return result;
    }
    else
        return result;
}





void mainImage( out vec4 fragColor, in vec2 fragCoord )
{ 
    //just an input test
    vec2 normCoords = iMouse.xy / iResolution.xy - vec2(0.5, 0.5);

    //just an oscilating light test
    float oscilator = 1.0*sin(iTime);

    // cosine factor for light
    float factor = 1.0;

    // coordinate vector is a vector that describes a position of a fragment/pixel on the screen
    // origin is at the center of the screen
    // positive x axis is to right
    // postive y axis is to up
    vec2 coordVector;
    coordVector = vec2(fragCoord.x, fragCoord.y) - vec2(iResolution.x/2.0, iResolution.y/2.0);    

    // view ray is our primary ray
    // rays are in world space coordinates
    // we calculate coordinates of a ray from WS camera direction and coordinate vector
    // (assuming that camera direction points at the center of the projection plane and is perpendicular to it
    vec3 viewRay = vec3( vec3(coordVector/800.0, 0.0 ) + normalize(camDir) );


    fragColor = backColor;
    vec3 oscliatingLightDir = -normalize( vec3( oscilator, lightDir.yz) );


    vec4 isHit2;

    isHit2 = hit_sphere_2( vec3(0.0 + normCoords.x*15.0, 1.0 + normCoords.y*15.0, -30.0), 5.0, normalize(viewRay), camPos);

    if( isHit2.w > 0.0 ){
        factor = clamp(dot(isHit2.xyz, oscliatingLightDir), 0.0, 1.0);
        fragColor = vec4( factor, factor, factor, 1.0 );
    }

    isHit2 = hit_sphere_2( vec3(0.0 + normCoords.x*5.0, 1.0 + normCoords.y*5.0, -15.0), 2.0, normalize(viewRay), camPos);

    if( isHit2.w > 0.0 ){
        factor = clamp(dot(isHit2.xyz, oscliatingLightDir), 0.0, 1.0);
        fragColor = vec4( factor, factor, factor, 1.0 );
    }

    isHit2 = hit_sphere_2( vec3(0.0 + normCoords.x*10.0, 1.0 + normCoords.y*10.0, -10.0), 1.0, normalize(viewRay), camPos);

    if( isHit2.w > 0.0 ){
        factor = clamp(dot(isHit2.xyz, oscliatingLightDir), 0.0, 1.0);
        fragColor = vec4( factor, factor, factor, 1.0 );
    }
    /*
    float ray_march_distance = rayMarch( camPos, normalize(viewRay), vec3(0.0, 0.0, 0.0) );
    if( ray_march_distance < MARCH_END ){
        fragColor = vec4( 1.0, 0.0, 0.0, 1.0 );
    }
    */

    vec4 ray_march_normal = rayMarch_Normal( camPos, normalize(viewRay), vec3(0.0, 0.0, 0.0) );
    if( ray_march_normal.w > 0.0 ){
        factor = clamp(dot(ray_march_normal.xyz, oscliatingLightDir), 0.0, 1.0);
        fragColor = vec4( factor * ray_march_normal.xyz, 1.0 );
    }

}

float pi = 3.14159265358979323846264;

vec3 _00 = vec3(252.0, 64.0, 136.0) / 255.0;
vec3 _01 = vec3(255.0, 237.0, 12.0) / 255.0;
vec3 _02 = vec3(151.0, 25.0, 121.0) / 255.0;
vec3 _03 = vec3(80.0, 246.0, 219.0) / 255.0;
vec3 _10 = vec3(78.0, 249.0, 177.0) / 255.0;
vec3 _11 = vec3(80.0, 121.0, 250.0) / 255.0;
vec3 _12 = vec3(180.0, 69.0, 250.0) / 255.0;
vec3 _13 = vec3(244.0, 66.0, 100.0) / 255.0;

// from gold noise
float PHI = 1.61803398874989484820459 * 00000.1; // Golden Ratio   
float PI  = 3.14159265358979323846264 * 00000.1; // PI
float SQ2 = 1.41421356237309504880169 * 10000.0; // Square Root of Two

float rand(vec2 coordinate, float seed)
{
    return fract(tan(distance(coordinate*(seed+PHI), vec2(PHI, PI)))*SQ2);
}

float test(float i, float j)
{
    if(i == j)
        return 1.0;

    return 0.0;
}

vec3 rand_col(vec2 index)
{   
    vec3 rc = vec3(0.0);

    rc += _00 * test(index.y, 0.0) * test(index.x, 0.0);
    rc += _01 * test(index.y, 0.0) * test(index.x, 1.0);
    rc += _02 * test(index.y, 0.0) * test(index.x, 2.0);
    rc += _03 * test(index.y, 0.0) * test(index.x, 3.0);
    rc += _10 * test(index.y, 1.0) * test(index.x, 0.0);
    rc += _11 * test(index.y, 1.0) * test(index.x, 1.0);
    rc += _12 * test(index.y, 1.0) * test(index.x, 2.0);
    rc += _13 * test(index.y, 1.0) * test(index.x, 3.0);
    rc += _00 * test(index.y, 2.0) * test(index.x, 0.0);
    rc += _01 * test(index.y, 2.0) * test(index.x, 1.0);
    rc += _02 * test(index.y, 2.0) * test(index.x, 2.0);
    rc += _03 * test(index.y, 2.0) * test(index.x, 3.0);
    rc += _10 * test(index.y, 3.0) * test(index.x, 0.0);
    rc += _11 * test(index.y, 3.0) * test(index.x, 1.0);
    rc += _12 * test(index.y, 3.0) * test(index.x, 2.0);
    rc += _13 * test(index.y, 3.0) * test(index.x, 3.0);

    return rc;
}

vec4 eye(vec2 coord)
{
    // useful vars
    vec2 ii = floor(mod(coord, 4.0));
    vec2 uv = mod(coord, 1.0);
    vec2 aspect = iResolution.xy / iResolution.yy;

    vec2 ndc = uv * 2.0 - 1.0;
    vec2 ppc = ndc * aspect;
    vec2 ppn = normalize(ppc);

    vec2 index = (ii);

    vec3 col = vec3(0.0, 0.0, 0.0);

    // eye
    vec2 ebc = ndc;
    ebc.y = pow(abs(ebc.y), 1.0/1.5);
    float r = step(length(ebc), 0.7);
    vec3 eyelid = vec3(r, r, r);

    // eye ball
    float cd = 0.0;

    float t = iTime * 0.00001;
    float pt = t - 0.001;
    float nt = t + 0.001;

    float prx = floor(rand(index, pt) * 4.0);
    float pry = floor(rand(index, pt) * 4.0);

    float rx = floor(rand(index, t) * 4.0);
    float ry = floor(rand(index, t) * 4.0);

    if(length(index - vec2(rx, ry)) < 0.2 )
        cd = 1.0;

    float open = 1.0/2.2;
    float closed = 1.0/20.0;
    float closedness = mix(open, closed, cd);
    ebc.y = pow(abs(ebc.y), closedness);
    r = step(length(ebc), 0.7);
    vec3 eyeball = vec3(r, r, r);

    // pupil
    r = step(length(ppc), 0.2);
    vec3 pupil = vec3(r, r, r);
    vec3 pupil_col = vec3(length(ppc));

    // iris
    r = step(length(ppc), 0.4);
    vec3 iris = vec3(r, r, r);

    r = length(ppc);
    float at = atan(ppn.x, ppn.y) + sin(iTime);
    float mat = mod(at, pi / 10.0);

    //vec3 iris_col = mix(vec3(0.2, 0.5, 0.9), vec3(0.2, 0.9, 0.9), mat);

    vec3 iris_col = rand_col(index.yx);

    col = (eyeball * vec3(1.0, 1.0, 1.0)) * (1.0 - iris);
   	col += iris_col * iris * eyeball * (1.0 - pupil);
    col += pupil_col* pupil * eyeball;
    col += rand_col(index) * (1.0 - eyeball) * eyelid;

    return vec4(col, eyelid);
}

vec3 bg_func(vec2 uv)
{
    float x = uv.x + iTime;
    float y = uv.y;
    float yv = uv.y * 2.0 - 1.0;
    float m = 0.0;

    if(yv < 0.0)
    	m = step(mod(x + y, 0.4), 0.2);
    else
    	m = 1.0 - step(mod(x - y, 0.4), 0.2);

    vec3 bg3 = mix(_02, _03, sin(iTime*10.0));
    vec3 bg4 = mix(_12, _13, cos(iTime*10.0));

    vec3 bg2 = mix(mix(_00, _01, sin(iTime * 5.0) * 0.5 + 0.5), bg3, sin(iTime * 5.0));
    vec3 bg1 = mix(mix(_10, _11, cos(iTime * 5.0) * 0.5 + 0.5), bg4, sin(iTime * 5.0));

    vec3 bg = mix(bg1, bg2, m);

    return bg;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 _uv = fragCoord/iResolution.xy;
    vec2 uv = fragCoord/iResolution.xy;
    vec2 texel = 1.0 / iResolution.xy;
    vec2 aspect = iResolution.xy / iResolution.yy;

    uv.x += sin(iTime * 10.0 + uv.y * 10.0) * 0.01;

    // eyes   
    vec2 num = vec2(1.0, 1.0) + vec2(floor(mod(iTime / 4.0, 8.0)));
    vec4 eyes = eye(uv * num + vec2(iTime, 0.0));

    // bg
	vec3 bg = bg_func(uv);

    // led / pixels
    float led_size = 0.00005;
    float edge = 0.04;
    vec2 led_size2 = vec2(led_size, led_size) / aspect;
    vec2 mm = mod(_uv * aspect, led_size);
    vec2 mv = smoothstep(0.0, led_size, mm);
    vec2 led = step(mv, vec2(edge) * aspect);
    float third = 1.2/3.0;
    float led_r = step(mv.x, third);
    float led_g = step(mv.x, third * 2.0) * (1.0 - led_r);
    float led_b = step(mv.x, third * 3.0) * (1.0 - led_r) * (1.0 - led_g);
    vec3 led_rgb = vec3(led_r, led_g, led_b) * clamp(1.0 - (led.x + led.y), 0.0, 1.0);

   	bg *= led_rgb;

    vec3 col = bg * (1.0 - eyes.a);
    col += eyes.a * eyes.rgb;

    //col = bg;

    // output
    fragColor = vec4(col.rgb, 1.0);
}

vec3 DrawBox(vec2 uv, float L, float R, float B, float T, vec3 col){
	vec3 colorBox = vec3(0.0);

    if (uv.x>L && uv.x<R && uv.y>B && uv.y<T) {
       colorBox = vec3(col.r/255.0,col.g/255.0,col.z/255.0);
    }
    return colorBox; 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 colorA = vec3(237.0, 139.0, 53.0);
	vec3 colorB = vec3(239.0, 234.0, 106.0);
    vec3 colorC = vec3(56.0, 61.0, 127.0);
	vec3 colorD = vec3(85.0, 138.0, 176.0);
    vec3 brown = vec3(162.0/255.0,94.0/255.0,36.0/255.0);

    // Time varying pixel color
    vec3 color = vec3(0.5);

    float pct = abs(sin(iTime*.25));

    // fill left1
    color = DrawBox(uv, 0.0, 0.25, 0.0, 1.0, mix(colorA, colorC, pct));

    // fill left2
    color += DrawBox(uv, 0.25, 0.5, 0.0, 1.0, mix(colorB, colorD, pct));

    // fill right1
    color += DrawBox(uv, 0.5, 0.75, 0.0, 1.0, mix(colorC, colorA, pct));

    // fill right2
    color += DrawBox(uv, 0.75, 1.0, 0.0, 1.0, mix(colorD, colorB, pct));

    // fill boxL

    if (uv.x>0.25*0.5 && uv.x<0.25 && uv.y>0.5*0.75 && uv.y<0.5/0.75) {
       color = brown;
    }

    // fill boxR
    if (uv.x>0.75 && uv.x<0.75/.85 && uv.y>0.5*0.75 && uv.y<0.5/0.75) {
       color = brown;
    }
    // Output to screen
    fragColor = vec4(color,1.0);
}

precision mediump float;
uniform float u_time;
uniform vec2 u_mouse;
uniform vec2 u_resolution;
#define PI 3.14159265359
float kyoto(vec3 p){
    float scale = 1.0 / sqrt(2.0);
    return scale * (abs(p.x) + abs(p.y) + abs(p.z));
}
float shogi(vec3 p) {
    float scale = sqrt(2.0);
    return scale * max(max(abs(p.x), abs(p.y)), abs(p.z));
}
float euc(vec3 p){
    float scale = 1.0;
    return scale * length(p);
}
float dist(vec3 p){
    float t = abs(mod(0.5 * iTime, 4.0) - 2.0);
    return mix(
        mix(euc(p), shogi(p), clamp(t, 0.0, 1.0)),
        kyoto(p), clamp(t - 1.0, 0.0, 1.0)
    );
}
float ballSDF(vec3 p) {
    return dist(p) - 1.;
}
vec3 gradSDF(vec3 p) {
    float d = 0.0001;
    return normalize(vec3(
        ballSDF(p + vec3(d, 0.0, 0.0)) - ballSDF(p + vec3(-d, 0.0, 0.0)),
        ballSDF(p + vec3(0.0, d, 0.0)) - ballSDF(p + vec3(0.0, - d, 0.0)),
        ballSDF(p + vec3(0.0, 0.0, d)) - ballSDF(p + vec3(0.0, 0.0, - d))
    ));
}
mat3 euler(float h, float p, float r){
    return mat3(
        cos(r)*cos(h)-sin(r)*sin(p)*sin(h), -sin(r)*cos(p), cos(r)*sin(h)+sin(r)*sin(p)*cos(h),
        sin(r)*cos(h)+cos(r)*sin(p)*sin(h), cos(r)*cos(p), sin(r)*sin(h)-cos(r)*sin(p)*cos(h),
        -cos(p)*sin(h), sin(p), cos(p)*cos(h)
    ); 
}
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // fragment position
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);

    //camera transform
    float t = 0.5 * iTime;
    mat3 g = euler(t, t, t);

    // camera
    vec3 cPos = g * vec3(0.0, 0.0, 2.0);
    vec3 cDir = g * vec3(0.0, 0.0, - 1.0);
    vec3 cUp = g * vec3(0.0, 1.0, 0.0);
    vec3 cSide = cross(cDir, cUp);
    float targetDepth = 1.0;

    // light
    vec3 lightPos = g *vec3(0.0, 1.0, 1.0);

    // ray
    vec3 ray = cSide * p.x + cUp * p.y + cDir * targetDepth;
    ray = ray / dist(ray);

    // marching loop
    vec3 rPos = cPos;
    for(int i = 0; i < 60; i++ ) {
        rPos += ray * ballSDF(rPos);
    }

    // collision detection
    if (abs(ballSDF(rPos)) < 0.001) { //object
        vec3 normal = gradSDF(rPos);
        float diff = clamp(dot(normalize(lightPos - rPos), gradSDF(rPos)), 0.0, 1.0);
        vec3 amb = vec3(0.0, 1.0, 0.0);
        fragColor = vec4(diff * amb, 1.0);
    } else {    //background
        fragColor = vec4(1.0);
    }
}

//Carter S. Jan 2020
//Basic Raytrace of a Moving Sphere with Reflections and Refractions.
//normally these checks would be done recursively but for educational purpses it is often
//easier to see it done iteratively
//The Reflections and Refractions are only calculated for one bounce
//This is closer to a halfSphere "Lens" than a true glass sphere (need lots of bounces)
#define PI 3.14159265359

struct Ray {
	vec3 origin; // Origin of ray (not to be confused with world origin)
	vec3 direction; // Direction Normalized vector
};

struct Sphere {
    int objID;
	vec3 center;  // Center of the Sphere
	float radius; // Radius of the Sphere
};

struct Plane {
    int objID;
	vec3 normal; //Normal vector
    vec3 point; //Some point such that for all points p in the plane dot((p-point),normal) == 0
};

//Simplest light source
struct PointLight {
    vec3 position; //3d point in space
    vec4 color; //RGBA color
};


//Move the Sphere over time
void AnimateSphere(inout Sphere sphere) {
    //Size of the animation loop
	float loopRadius = 4.0;
    //initial sphere position
    float loopOffset = sphere.center.z;
    //time it takes to complete a loop
    float loopTime = 8.0;

    //Based on parametric equations of a circle
    sphere.center.x = sin((mod(iTime, loopTime))/loopTime*2.0*PI)*loopRadius;
    sphere.center.z = loopOffset + cos((mod(iTime,loopTime))/loopTime*2.0*PI)*loopRadius;

}

//Get the Specular color (shiny light)
vec4 calculateSpecular(in vec3 rayd, in vec3 normal, in vec3 toLight, in PointLight light) {
    //specular exponent
    float shinnyness = 5.0;
    //get the reflected light vector
    vec3 reflected = 2.0*dot(toLight, normal)*normal - toLight;
    //calculate specular color
    return light.color * max(0.0,pow(dot(rayd,reflected),shinnyness));
}

//Get the Diffuse color (Basic light effect)
vec4 calculateDiffuse(in vec3 normal, in vec3 toLight, in PointLight light) {
    return light.color * max(0.0,dot(normal,toLight));
}

//Get the direction to the light source (no shadows simplifies check)
vec3 checkLight(in vec3 intersect, in PointLight light) {
    return normalize(light.position - intersect);
}

//Get the sphere surface normal from the intersection point
vec3 getSphereIntersectionNormal(in vec3 intersect, in Sphere sphere) {
	return normalize(intersect - sphere.center);
} 

//Get the 3d point that is depth along the ray
vec3 calculateIntersectionPoint(in Ray ray, in float depth) {
	return ray.origin + ray.direction*depth;
}

// From the Wikipedia page on Line-Plane intersection
float intersectPlane(in Ray ray, in Plane plane) {
	float denominator = dot(plane.normal,ray.direction);
    if(abs(denominator) >= 0.001) {//make sure Ray is not parallel to plane (or nearly parallel)
        return dot((plane.point - ray.origin),plane.normal)/denominator;
    }
	return -1.0; // Any negative number to indicate no intersect (or intersection from behind)
}

// From the Wikipedia page on Line-sphere intersection
float intersectSphere(in Ray ray, in Sphere sphere) {
	// Sphere center to ray origin
	vec3 co = ray.origin - sphere.center;

	// The discriminant is negative for a miss, or a postive value
	// used to calcluate the distance from the ray origin to point of intersection
    //bear in mind that there may be more than one solution
	float discriminant = dot(co, ray.direction) * dot(co, ray.direction)
			- (dot(co, co) - sphere.radius * sphere.radius);

	// If answer is not negative, get ray intersection depth
	if (discriminant >= 0.0)
		return -dot(ray.direction, co) - sqrt(discriminant);
	else
		return -1.; // Any negative number to indicate no intersect
}

float performPlaneCheck(in Ray ray, in Plane plane, in Sphere sphere, out vec4 color){
//code for plane Check
// Check if the ray from the camera through the pixel intersects the Plane
	float intersectionDepth = intersectPlane(ray,plane);
    if (intersectionDepth >= 0.001 && intersectionDepth <= 1000.0) {//Front and Back Culling
    	//Point light is purple and a little to the left and aboveof the camera start
    	PointLight light = PointLight(vec3(-5.0,5.0,0.0), vec4(1.0,1.0,1.0,1.0));
    	//Get the ray intersection Point
    	vec3 intersect = calculateIntersectionPoint(ray,intersectionDepth);
    	//Initialize Plane color
    	vec4 ambientPlaneColor = vec4(0.0,0.0,0.0,1.0);

        //set Texture to appropriate material
        if (plane.objID == 1) {
        	ambientPlaneColor = texture(iChannel0,intersect.xz);
        }else {
        	ambientPlaneColor = texture(iChannel1,intersect.xy);
        }


    	//Get the direction to the lightsource from the intersection point
    	vec3 toLight = checkLight(intersect, light);
    	//contruct Ray for shadow check
    	Ray shadowRay = Ray(intersect, toLight);
    	//Do a shadow check
    	float shadowIntersectionDepth = intersectSphere(shadowRay,sphere);
    	if (shadowIntersectionDepth >= 0.05) { //Sphere is blocking the Light
        	color = ambientPlaneColor;
    	}else{ //Light is visable from this point
        	color = ambientPlaneColor + 0.25*calculateDiffuse(plane.normal, toLight, light);
    	}
        return intersectionDepth;
    }else{
        // else draw background color (black)
		color = vec4(0.0,0.0,0.0,1.0);
        return -1.0;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    // output a 4d fragment color vector [R,G,B,A]
    // input is a 2d fragment Co-ordinate vector [X,Y]
    // can interpret input vector as a pixel (function is called 640x360 times by default per frame)

	// Converting all coordinates (x,y) to be in the range [0,1]
	float u = fragCoord.x / iResolution.x; // iResolution.x is width in pixels
	float v = fragCoord.y / iResolution.y; // iResolution.y is height in pixels

	// Now convert from coordinate range [0,1] to [-1,1]
	// This moves pixel (0,0) to the middle of the image
	u = u * 2.0 - 1.0;
	v = v * 2.0 - 1.0;

    //the following line is necessary since the image may not be a square
    //removing this line results in the x dimension being stretched
    u *= (iResolution.x/iResolution.y);

	// The position of this pixel in 3D space 
    //(imagine a plane centered at the origin perpendicular to the camera, what would be the 3d coordinates of this pixel?)
	vec3 pixelPos = vec3(u, v, 0);

    // The camera position in this example is on a fixed loop.
    // Initialize at 0.0
    vec3 cameraPos = vec3(0.0, 0.0, -4.0); 

	// The ray direction is ray from the camera through the pixel
	vec3 rayDir = normalize(pixelPos - cameraPos);

    // The Sphere is at the origin
    Sphere sphere = Sphere(0,vec3(0.0, 0.0, 7.0), 1.0);

    //Animate the Sphere
    AnimateSphere(sphere);

    // The wooden plane is the xz plane at y = -2.0
    Plane woodPlane = Plane(1,vec3(0.0,1.0,0.0),vec3(0.0,-2.0,0.0));

    // The tile plane is the xy plane at z = 12.0
    Plane tilePlane = Plane(2,vec3(0.0,0.0,-1.0), vec3(0.0,0.0,15.0));

    //Create Ray struct from origin through current pixel
    Ray ray = Ray(cameraPos, rayDir);

	// Check if the ray from the camera through the pixel intersects the sphere
	float intersectionDepth = intersectSphere(ray, sphere);

	// If the depth at which the ray intersects the sphere is positive (in front of camera)
	if (intersectionDepth >= 0.05)//Sphere Check
	{//draw the sphere color (basic ambient + diffuse)
        //ambient color of the sphere is greyish
        vec4 ambientSphereColor = vec4(0.2,0.2,0.2,1.0);

        //Point light is white and a little to the left of the camera start
        PointLight light = PointLight(vec3(-3.0,0.0,0.0), vec4(0.5,0.5,0.5,1.0));

        //Get the ray intersection Point
        vec3 intersect = calculateIntersectionPoint(ray, intersectionDepth);

        //Get the surface normal at that point
		vec3 normal = getSphereIntersectionNormal(intersect,sphere);

        //Get the direction to the lightsource from the intersection point
        vec3 toLight = checkLight(intersect, light);

        //we are going to skip the checks for shadows for the sphere
        //but in other scenes we will not have this luxury

        //Refract Ray see https://en.wikipedia.org/wiki/Refraction for math details
        vec3 refracted = refract(ray.direction,normal, 0.7);//1.0/1.2 is index of refraction for glass
        Ray refractedRay = Ray(intersect,refracted);

        vec4 woodColor = vec4(0.0,0.0,0.0,0.0);//Initialize colors
        vec4 tileColor = vec4(0.0,0.0,0.0,0.0);

        //This is a lazy way of handling 2 planes in a scene
        float intersectedWood = performPlaneCheck(refractedRay,woodPlane,sphere, woodColor);
        float intersectedTile = performPlaneCheck(refractedRay,tilePlane,sphere, tileColor);
        vec4 refColor = vec4(0.0,0.0,0.0,0.0);
        if (intersectedWood < 0.0)
        {
        	refColor = tileColor;
        }else if (intersectedTile <= intersectedWood){
        	refColor = tileColor;
        }else{
            refColor = woodColor;
        }

        //calculate reflected Ray (normalize here is because im paranoid)
        vec3 reflected = normalize(ray.direction - 2.0*dot(ray.direction, normal)*normal);
        Ray rayreflect = Ray(intersect,reflected);

        //initialize reflected color
        vec4 reflectColor = vec4(1.0,1.0,1.0,1.0);
        //Plane Check
        float intersected = performPlaneCheck(rayreflect,woodPlane,sphere,reflectColor);
        //For this Scene we can skip the tilePlane check on reflections since
        //we cannot reflect from the sphere to the back wall.
        if (intersected > 0.0){
        	//Combine it all to get the correct color (with reflected and refracted Color (mostly refracted))
        	fragColor = 0.25*ambientSphereColor + 0.25*calculateDiffuse(normal,toLight,light) + 0.15*reflectColor + 0.35*refColor;
        }else{
        	//Combine it all to get the correct color (with refracted)
        	fragColor = 0.33*ambientSphereColor + 0.17*calculateDiffuse(normal,toLight,light) + 0.5*refColor;
        }

    } else {//Plane Checks (No Sphere Hit)
        vec4 woodColor = vec4(0.0,0.0,0.0,0.0);//Initialize colors
        vec4 tileColor = vec4(0.0,0.0,0.0,0.0);

        //This is a lazy way of handling 2 planes in a scene
        float intersectedWood = performPlaneCheck(ray,woodPlane,sphere, woodColor);
        float intersectedTile = performPlaneCheck(ray,tilePlane,sphere, tileColor);

        if (intersectedWood < 0.0)
        {
        	fragColor = tileColor;
        }else if (intersectedTile <= intersectedWood){
        	fragColor = tileColor;
        }else{
            fragColor = woodColor;
        }
        //Ideally a well coded ray tracer would handle these checks generically for all obj's in a scene
        //This is a toy example
    }
}

float Hash21(vec2 p) {
    p = fract(p*vec2(123.34, 456.21));
    p += dot(p, p+45.32);
    return fract(p.x*p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;
    vec2 uv2 = uv;

    uv2 *= 16.;

    vec2 gv = fract(uv2);
    vec2 id = floor(uv2);

    float r = Hash21(id);

    // Calculate the to center distance
    float d = length(uv - 0.5) * 2.0;

    // Calculate the ripple time
    float t = d * d * 25.0 - iTime * 4.0;

    // Calculate the ripple thickness
    d = (cos(t) * 0.5 + 0.5) * (1.0 - d);

    // Time varying pixel color
    vec3 col = 0.5 + 0.5 * cos(t / 20.0 + uv.xyx + vec3(0.0,2.0,4.0));

    // Set the output color to rgb channels and the thickness to alpha channel
    // AO is automatically calculated
    fragColor = vec4(col, r+(sin(iTime*r)*.5));
}

