    fragColor = vec4(1.05*truchet_noise(10.*uv),1.0);
}

#define S(a, b, t) smoothstep(a, b, t)
#define NUM_LAYERS 5.

// Color pallete
//      A Component  R     G     B   (Y shift)
#define pal_a vec3( 0.20, 0.50, 0.20 )  
//      B Component  R     G     B   (amplitude)
#define pal_b vec3( 0.10, 0.30, 0.20 )  
//      C Component  R     G     B   (period)
#define pal_c vec3( 1.00, 0.70, 0.40 )  
//      D Component  R     G     B   (phase)
#define pal_d vec3( 0.00, 0.15, 0.20 )    

vec3 pal( in float t )
{
    return 1. - pal_a + pal_b*-cos( 6.28318*(pal_c*t+pal_d) );
}

// Distance from p to line a-b
float distLine(vec2 p, vec2 a, vec2 b) {
	vec2 pa = p - a;
    vec2 ba = b - a;
    float t = clamp(dot(pa, ba) / dot(ba, ba), 0., 1.);
    return length(pa - ba * t);

}

// Noise 2 in, 1 out
float N21 (vec2 p) {
	p = fract(p*vec2(231.34, 851.73));
    p += dot(p, p+23.45);
    return fract(p.x * p.y);
}

// Noise 2 in , 2 out
vec2 N22 (vec2 p ){
	float n = N21(p);
    return vec2(n, N21(p+n));
}

// Get random position within a cell
vec2 getPos(vec2 id, vec2 offset) {
    vec2 r = N22(id+offset)*(iTime+22856.)*.2;
    return offset+sin(r)*.4;
}

// Is p within a line from a-b
float drawLine (vec2 p, vec2 a, vec2 b) {
	float d = distLine(p, a, b);
    float m = S(.08, .0, d);
    float d2 = length(a-b);
    m *= S(1.5, 0., d2);
    return m;
}

// Draw a whole layer 
float drawLayer(vec2 uv) {
    float m = 0.;

    // Grid coords
  	vec2 gv = fract(uv)-.5;
 	// ID for this cell
    vec2 id = floor(uv);

    // Get the dot position for this cell and 8 surrounding ones
    vec2 p[9];
    int i = 0;
    for(float y=-1.; y<= 1.; y++) {
    	for(float x=-1.; x<= 1.; x++) {
            p[i++] = getPos(id, vec2(x, y));
        }
    }

    // draw a line from the center to surrounding cells
    float t = iTime;
    for(int i=0; i<9; i++) {
    	m += drawLine(gv, p[4], p[i]);
    }
    // Fill in the diagonals
    m += drawLine(gv, p[1], p[3]);
    m += drawLine(gv, p[1], p[5]);
    m += drawLine(gv, p[7], p[3]);
    m += drawLine(gv, p[7], p[5]);

    return m;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 mouse = (iMouse.xy/iResolution.xy)-.5;

    float m = 0.;
    float t = sin(iTime*.03);

    // Rotate and zoom over time
    float s = sin(t);
    float c = cos(t);
    mat2 rot = mat2(c, -s, s, c);

    // grab the distance from the bottom
    float gradient = uv.y;

    // Grab distance from edges for vignette
    float vignette = length(uv);

    // Rotate coords
    uv *= rot;
    mouse *= rot;

    // Draw multiple layers at different scales and positions
    for (float i = 0.; i<1.; i+=1./NUM_LAYERS) {
        float z = fract(i+t);
        float size = mix(10., .5, z);
        float fade = S(0., .6, z)*S(1., .9, z);
        m += drawLayer(uv*size+i*20.-mouse)*fade;
    }

    // Color things
    vec3 base = pal(iTime*.03);
    vec3 col = m*base;
    col -= gradient*base*.8;
    col += smoothstep(0.8, .99, vignette)*2.;

    // Tone mapping? KeepsSee: https://www.shadertoy.com/view/3s3GDn
    col = 1.0 - exp( -col );

    // Output to screen
    fragColor = vec4(vec3(1)-col, 1.0);
}

//This parameter changes the density of the "oases."
const float mag = 8.0;

const float GREEN = 0.0;
const float BLUE = 1.0;
const float RED = 2.0;
const float YELLOW = 3.0;
//this function is from https://www.shadertoy.com/view/4djSRW
float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33+iTime/20000.0);
    return floor(fract((p3.x + p3.y) * p3.z)*2.0);
}


float magnify(vec2 fragCoord,float mag){
	return hash12(floor(fragCoord/pow(2.0,mag)));
}

bool is_above(vec2 fragCoord,float mag,float color){
	return magnify(fragCoord,mag) == color;
}
bool is_surrounded_by(vec2 fragCoord,float mag,float color){
	return magnify(fragCoord+vec2(0,1),mag) == color
        && magnify(fragCoord+vec2(1,0),mag) == color
        && magnify(fragCoord-vec2(0,1),mag) == color
        && magnify(fragCoord-vec2(1,0),mag) == color;
}

bool is_next_to(vec2 fragCoord,float mag,float color){
	return magnify(fragCoord+vec2(0,1),mag) == color
        || magnify(fragCoord+vec2(1,0),mag) == color
        || magnify(fragCoord-vec2(0,1),mag) == color
        || magnify(fragCoord-vec2(1,0),mag) == color;
}


vec3 get_color(float color){
    if(color == BLUE){
    	return vec3(0.149,0.141,0.912);
   	}
    else if(color == GREEN){
    	return vec3(0.000,0.833,0.224);
   	}
    else if(color == RED){
    	return vec3(1.0,0.0,0.0);
   	}
    else if(color == YELLOW){
    	return vec3(1.0,1.0,0.0);
   	}
}


float iterations(float color1,in vec2 fragCoord,float mag){
    while(mag > 1.0){
    if(color1 == GREEN && is_next_to(fragCoord,mag,BLUE)){
    	color1 = YELLOW;
    }
    else if(color1 == BLUE && is_next_to(fragCoord,mag,GREEN)){
        color1 = GREEN;
    }
    mag -= 1.0;
    }
    return color1;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragCoord = fragCoord*2.0 + iTime*vec2(50.0,0.0);
    float color1 = magnify(fragCoord,mag);
    //color1 = replace_pattern(mat2(1.0,1.0,1.0,1.0),mat2(0.0,2.0,2.0,0.0),fragCoord,mag);

    color1 = iterations(color1,fragCoord,mag-1.0);

    fragColor = vec4(get_color(color1),1.0);
}

#define MAX_STEPS 50
#define MIN_DIST 0.001
#define MAX_DIST 1000.0
#define EPSILON 0.001

struct Mat{
    vec3 ambient;
    vec3 diffuse;
    vec3 specular;
    vec3 shininess;
};

struct Hit{
    float d;
	Mat mat;
};

struct Light{
	vec3 pos;
    vec3 ambient;
    vec3 diffuse;
    vec3 specular;
};

mat2 rot(float a){
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}

bool isHit = false;

float sphere( vec3 p, float s )
{
  return length(p)-s;
}

float box(vec3 p, vec3 b){
    vec3 d = abs(p) - b;
      return length(max(d,0.0)) + min(max(d.x,max(d.y,d.z)),0.0);
}

vec3 rep(vec3 p, vec3 c)
{
    return mod(p, c) - 0.5 * c;
}

// Noise
float hash(vec2 p){
    vec3 p3 = fract(vec3(p.xyx) * 0.13); p3 += dot(p3, p3.yzx + 3.333);
    return fract((p3.x + p3.y) * p3.z); 
}

float noise(vec2 x){
    // Make the waves move
    x += iTime;
    x *= .25;
    vec2 i = floor(x);
    vec2 f = fract(x);

    float a = hash(i);
    float b = hash(i + vec2(1.0, 0.0));
    float c = hash(i + vec2(0.0, 1.0));
    float d = hash(i + vec2(1.0, 1.0));

	vec2 u = f * f * (3.0 - 2.0 * f);
    float m = mix(a, b, u.x) + (c - a) * u.y * (1.0 - u.x) + (d - b) * u.x * u.y;
	return m;
}

float fbm(vec2 x){
    x *= .3;
	mat2 m = mat2( 1.6,  1.2, -1.2,  1.6 );// * iTime*.002;
    float f = 0.0;
	f  = 0.5000*noise(x); 
    x = m*x;
	f += 0.2500*noise(x); 
    x = m*x;
	f += 0.1250*noise(x); 
    x = m*x;
	f += 0.0625*noise(x); 
    return f;
}

float waterHeight(vec2 p){
    float time = iTime * 0.3;
    float h = fbm(p*time*.05)*1.2;
    // Maximize induced nausea by adding more waves
    h += sin(iTime *.7 + dot(vec2(.3, .1), p));
    h += sin(iTime *.2 + dot(vec2(.1, .7), p));
    return h;
}

// Materials
Mat waterMat = Mat(vec3(.2, .4, .6), vec3(.4, .7, .6), vec3(.5), vec3(1.)); // Water material
Mat redMat = Mat(vec3(.8, .2, .3), vec3(.8, .2, .2), vec3(.3), vec3(0.0)); // Red material
Hit hit; // Global hit that saves material

// Scene map
Hit map(vec3 p){
	float ground = box(rep(p + vec3(0., 3., 1.1), vec3(0., 0., 1.)), vec3(15., 2.*waterHeight(p.xz)*.07+2., 2.));
    float ceiling = box(rep(p + vec3(0., -3., 1.1), vec3(0., 0., 1.)), vec3(15., 2.*waterHeight(p.xz)*.05+1.8, 2.));
    float ball = sphere(p + vec3(0.3, 0.9, .7), .5);

    float result = min(ground, ceiling);   
    result = min(result, ball);
    hit.d = result;
    if(result == ball){
        hit.mat = redMat;
    }
    else{
        hit.mat = waterMat;
    }
    return hit;
}   

vec3 march(vec3 ro, vec3 rd){
    float dt = 0.0;
    for(int i = 0; i < MAX_STEPS; i++){
        vec3 p = ro + dt * rd;
        p.y += sin(p.z - iTime * 2.) * .03; // Makes everything all wavey
        Hit mhit = map(p);
        float dist = mhit.d;
        if(dist < MIN_DIST){
            isHit = true;
            hit.mat = mhit.mat;
            break;
        }
        dt += dist*0.925;
    }
    return ro + dt * rd;
}

vec3 normals(vec3 p){
    const vec2 eps = vec2(EPSILON, 0);
    return normalize(vec3(map(p + eps.xyy).d - map(p - eps.xyy).d,
                          map(p + eps.yxy).d - map(p - eps.yxy).d,
                          map(p + eps.yyx).d - map(p - eps.yyx).d));
}

vec4 light(vec3 p, vec3 rd, Light mLight, Mat mat, vec3 n){

    vec3 l = normalize(mLight.pos - p);

    // Ambient
    float ambientStr = 0.8;
    vec3 ambient = ambientStr * mLight.ambient;

    // Diffuse
    float dif = max(dot(n, l), 0.0);
    vec3 diffuse = dif * mLight.diffuse;

    // Specular
    vec3 viewDir = normalize(rd - p);
    vec3 reflectDir = reflect(-l, n);
    float spec = pow(max(dot(viewDir, reflectDir), 0.0), 32.);
    float specularStr = 12.;
    vec3 specular = mLight.specular * (spec * mat.specular * specularStr);

    return vec4((diffuse * mat.diffuse + ambient * mat.ambient + specular * mat.shininess) , 1.0);
}

