    p.x = tempX;
    p.y = tempY;


    p.x += .5*snoise(vec2(time*.6+256.0, p.y));
    p.y += .5*snoise(vec2(time*.6+500.0, p.x));	

    float x_max = 0.56999993;
    float y_max = 0.74999976;

    float R = x_max;
    float r = .1;

