return vec4(red, green, blue, 0.0) * clamp(sin(time*5.0+0.0), 0.0, 1.0)
+ vec4(green, red, green, 0.0) * clamp(sin(time*5.0+4.0*PI/3.0), 0.0, 1.0)
+ vec4(green, green, red, 0.0) * clamp(sin(time*5.0+2.0*PI/3.0), 0.0, 1.0);
*/
