//Verticies of a basic tesselated square
vec3 verts[4] = vec3[](
	vec3(-1.0,-1.0,1.0),
	vec3(1.0,-1.0,1.0),
	vec3(-1.0,1.0,1.0),
	vec3(1.0,1.0,1.0)
);

//Faces of the square
vec3 faces[2] = vec3[](
	vec3(0.0, 1.0, 2.0),
	vec3(1.0, 3.0, 2.0)
);

//Not used
mat3 rotateY = mat3(
	cos(45.0), 0.0, (-1.0)*sin(45.0),
	0.0, 1.0, 0.0,
	sin(45.0), 0.0, cos(45.0)
);

//Convert to radians
float rads(float deg){
	return deg * (3.14159 / 180.0);
}

//Z rotation matrix as a function of time
mat3 getZRotate(float time){
	float rad = rads(10.0 *time);

	mat3 rot = mat3(
		cos(rad), sin(rad), 0.0,
		(-1.0)*sin(rad), cos(rad), 0.0,
		0.0, 0.0, 1.0
	);

	return rot;
}


/*
Transform the face, then convert coordinate to barycentric 
coordinates of that face to see if it's inside.
*/
bool checkTriangleIntersect(vec3 face, vec2 coord){
	mat3 rotateZ = getZRotate(iTime);

    vec3 v1 = rotateZ * verts[int(face[0])];
	vec3 v2 = rotateZ * verts[int(face[1])];
	vec3 v3 = rotateZ * verts[int(face[2])];

	float b1 = (((v2.y-v3.y)*(coord.x-v3.x)) + ((v3.x-v2.x)*(coord.y-v3.y))) / 
				(((v2.y-v1.x)*(v1.x-v3.x)) + ((v3.x-v2.x)*(v1.y-v3.y)));

	float b2 = (((v3.y-v1.y)*(coord.x-v3.x)) + ((v1.x-v3.x)*(coord.y-v3.y))) / 
				(((v2.y-v3.y)*(v1.x-v3.x)) + ((v3.x-v2.x)*(v1.y-v3.y)));

	float b3 = 1.0 - b1 - b2;

	return b1>=0.0 && b1<=1.0 && b2>=0.0 && b2<=1.0 && b3>=0.0 && b3<=1.0;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //[-4, 4]
    vec2 uv = 4.0 * ((2.0 * (fragCoord/iResolution.xy)) - 1.0);

	//Check if coord is within any faces
    vec3 col;
    for(int i=0;i<faces.length();i++){
		if(checkTriangleIntersect(faces[i], uv)){
			if(i%2==0)
				col = vec3(1.0, 0.0, 0.0);
			else
				col = vec3(0.0, 1.0, 0.0);
		}
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

/**
 This shader is basically an improvement upon a previous fireworks shader I did a while
 ago (www.shadertoy.com/view/Ws3SRS). Here, each firework spawns at a random position
 instead of the just following the same pattern in a loop, so the fireworks show can be
 watched and enjoyed infinitely. The fireworks spawn from behind the buildings, but in
 front of the mountains. I hope everyone finds watching this relaxing and enjoyable!

 I wish a very happy and a prosperous new year to everyone!
*/

#define PI  3.141592653589793
#define TAU 6.283185307179586

// Helper macros 
#define C(x) clamp(x, 0., 1.)
#define S(a, b, x) smoothstep(a, b, x)
#define F(x, f) (floor(x * f) / f)

