// copy+paste from iq! thx
float iSphere(in vec3 ro, in vec3 rd, in vec4 sph)
{
    vec3 oc = ro - sph.xyz;
    float b = dot(oc, rd);
    float c = dot(oc, oc) - sph.w * sph.w;
    float h = b * b - c;
    if (h < 0.0)
        return -1.0;
    h = sqrt(h);
    float t1 = -b - h;
    float t2 = -b + h;
    if (t1 < .001 && t2 < .001)
        return -1.0;
    if (t1 < .001)
        return t2;
    return t1;
}
const float hpi = 3.1415926 / 2.;

// https://www.shadertoy.com/view/llySRh
#define keyDown(ascii)(texelFetch(iChannel3,ivec2(ascii,1),0).x > 0.)

float tex(in vec3 U)
{
    vec2 p = U.xy;
    float f = 0.0;
    float t = 0.;

    // anim loop
    int sec = 5; float speed = 240.;
    int frm = 1; 

    if(!keyDown(39))
        frm++;


    t = float(frm+iFrame%(60*sec)) / speed;


    for(float i = 0.0; i < 4.; ++i)
    {
        float s = sin(t + i * hpi) * p.y * 5.;
        float c = cos(t + i * hpi) * p.x * 2.;
        f += .0175 / abs(length(p - vec2(0, 2)) + clamp(c + s, -6., 1.));
    }

    // return  .01/length(U.xy); 

    return f;
}

#define R(_p,_a)_p=(cos(_a)*_p+sin(_a)*vec2(_p.y,-_p.x));
void mainImage(out vec4 O, in vec2 U)
{
