    vec2 R = iResolution.xy;

    vec3 o = vec3(0, 0,initialZOOM);
    vec3 d = normalize(vec3((U - .5 * R) / min(R.x, R.y), -.5));
    vec2 m = (3. * atan(hpi-3. * vec2(iMouse.x, iMouse.y) / R));
    if (iMouse.z > .0001)
    {
        o.z = 10.; //"zoom"
        R(o.yz, m.y) R(d.yz, m.y)R(o.xz, m.x) R(d.xz, m.x)
    }
	vec3 dn = vec3(0, 1, 0), dc = vec3(0, -2, 0.5);//plane
    vec3 dk = dot(dn, o - dc) / dot(dn, d) * d 
        										+ dc;//flip texture
    float pt = dot(dn, dc) / dot(dn, d);


    vec4 sc0 = vec4(0, 0, 6, 200.);//big one
    vec4 sc1 = vec4(0, 0, 0.5, 2.);//green
    float t0 = iSphere(o, d, sc0);
    float t1 = iSphere(o, d, sc1);

    float tmin = 1e10;
	if(!(texelFetch(iChannel3,ivec2(32,2),0).x > 0.) ) // spacebar
    if (pt > 0. && pt < tmin)//plane
    {
        tmin = pt;
        vec3 hit = o + pt * d;
        vec3 n = normalize(hit - dk);
        float ntex = tex(n.xyz) + tex(n.yzx) + tex(n.zxy);
        col = ntex + vec3(0, 0, .5);
    }
    if (t0 > 0.0 && t0 < tmin)//big one
    {
        tmin = t0;
        vec3 hit = o + t0 * d;
        vec3 n = normalize(hit - sc0.xyz);
        float ntex = tex(n.xyz) + tex(n.yzx) + tex(n.zxy);
        col = ntex + vec3(.5, 0, 0);
    }
    if(t1 > 0.0 && t1 < tmin) //green sph
    {
        tmin = t1;
        vec3 hit = o + t1 * d;
        vec3 n = normalize(hit - sc1.xyz);
         n=n-n-n; //rotation
        //R(n.xy,radians(90.))
       // R(n.yz,radians(90.))    
        float ntex = tex(n.xyz) + tex(n.yzx) + tex(n.zxy);
        // if(mod(iTime, 2.)>1.) ntex = tex(6.*-n.xyz);
        col = mix(col, (12. * ntex) * vec3(0, .5, 0), .2);
    }
    O.xyz = col;
    O.a = 1.;
}

