float Xor(float a, float b) {
    return a*(1.-b) + b*(1.-a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	

    float t = iTime*.02+iDate[2];
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    float centDist = length(uv)*(8.*pow(0.5*sin(t*2.43)+1., 2.+sin(t*1.06)));


    float a = 0.786;
    float totalSpinTime = 2.;
    float spinOccurance = 20.;
    //Get it to spin every so often
    if (mod(t, spinOccurance) <= totalSpinTime) {
        a += (3.1416*2.)*smoothstep(0., 1., mod(t, spinOccurance)/totalSpinTime);
    } 
    float s = sin(a);
    float c = cos(a);   
    uv *= mat2(c, -s, s, c);

    float uvZoom = (4.+8.*pow(sin(t*0.187+length(uv))*0.5+0.5, 2.+sin(0.278*t))+4.*sin(t*2.5)) * (0.5*sin(t*5.7)+1.);
    uv *= uvZoom;

    float blur = uvZoom*0.003;

    float lineWidth = uvZoom*0.005 + sin(2.7*t)*0.003;
    float lineBlur = uvZoom*0.002;
    vec3 lineCol = vec3(0.);

    float extraLineWidth = uvZoom*0.01 + sin(2.7*t)*0.003;
    float extraLineBlur = extraLineWidth/3.;
    vec3 extraLineCol = vec3(0.);
    float extraLineCutoff = 11.;
    float extraLineCutoff2 = 6.;


    vec2 gv = fract(uv+0.5)-0.5;
    vec2 id = floor(uv+0.5);

    float m = 0.;

    for(float y=-2.; y<=2.; y++) {
        for(float x=-2.; x<=2.; x++) {
            vec2 offs = vec2(x, y);

            float dCir = length(gv-offs);
            float dSqu = abs(abs((gv-offs)[0])-abs((gv-offs)[1]));
            float d = mix(dCir, dSqu, 1.*(0.5*sin(t*.63) +0.5));

   			float dist = length(id+offs)*.3*(0.5*sin(0.93*t)+0.5);


            float rMix = 0.6*pow(sin(-t+dist*0.6)*.5 +.5, 4.) + 0.4*(sin(3.*t+dist*0.6)*.5 +.5);
            float r = mix(1.1, 2.5, rMix);

            if (d>r-lineWidth/2. && d<r) {
                float blurRat = (d-(r-lineWidth/2.)) / ((r-lineWidth/2.+lineBlur)-(r-lineWidth/2.));
                lineCol+=mix(0., 1., smoothstep(0., 1., blurRat));  
                //lineCol+=1.;
            }
            else if((d<r+lineWidth/2. && d>r)) {
                float blurRat = (d-(r+lineWidth/2.)) / ((r+lineWidth/2.-lineBlur)-(r+lineWidth/2.));
                lineCol+=mix(0., 1., smoothstep(0., 1., blurRat));
                //lineCol+=1.;
            }


            if (uvZoom < extraLineCutoff) {
                float extraLineFade = (uvZoom-extraLineCutoff)/(0.-extraLineCutoff);
                float r2 = r/2.;
            	if (d>r2-extraLineWidth/2. && d<r2) {
                    float blurRat = (d-(r2-extraLineWidth/2.)) / ((r2-extraLineWidth/2.+extraLineBlur)-(r2-extraLineWidth/2.));
                    extraLineCol+=mix(0., 1., smoothstep(0., 1., blurRat*extraLineFade));  
            	}
            	else if((d<r2+extraLineWidth/2. && d>r2)) {
                    float blurRat = (d-(r2+extraLineWidth/2.)) / ((r2+extraLineWidth/2.-extraLineBlur)-(r2+extraLineWidth/2.));
                    extraLineCol+=mix(0., 1., smoothstep(0., 1., blurRat*extraLineFade));
            	}
            }

            if (uvZoom < extraLineCutoff2) {
                float extraLineFade = (uvZoom-extraLineCutoff2)/(0.-extraLineCutoff2);
                float r3 = r*(0.5*sin(t*5.)+0.5);
            	if (d>r3-extraLineWidth/2. && d<r3) {
                    float blurRat = (d-(r3-extraLineWidth/2.)) / ((r3-extraLineWidth/2.+extraLineBlur)-(r3-extraLineWidth/2.));
                    extraLineCol+=mix(0., 1., smoothstep(0., 1., blurRat*extraLineFade));  
            	}
            	else if((d<r3+extraLineWidth/2. && d>r3)) {
                    float blurRat = (d-(r3+extraLineWidth/2.)) / ((r3+extraLineWidth/2.-extraLineBlur)-(r3+extraLineWidth/2.));
                    extraLineCol+=mix(0., 1., smoothstep(0., 1., blurRat*extraLineFade));
            	}
            }

            m = Xor(m, smoothstep(r, r-blur, d));
        }
    }

    //col.rg = gv;
    vec3 col = vec3(0);
    col+=vec3(m*(0.5*sin(t*0.2)+0.5), m*(0.5*sin(t*0.25)+0.5), m*(0.5*sin(t*0.15)+0.5));
    vec3 colMult = vec3(0.5*sin(6.1*t + centDist)+0.7, 0.5*sin(7.2*t+centDist)+0.7, 0.5*sin(8.3*t+centDist)+0.7);
    lineCol *= colMult;
    extraLineCol *= col*0.7;

    col*= colMult;
    col+=lineCol;
    col+=extraLineCol;

    fragColor = vec4(col,1.0);
}

#define time iTime
#define SPEED 20.

float PI = acos(-1.);

vec3 palette(float x) {
  vec3 p = vec3(1, .5, 1);
  vec3 q = vec3(.7, .6, 1);
  vec3 r = vec3(.3, .6, .3);
  vec3 s = vec3(.9, .5, .3);

  return p + q * sin(2. * PI * (x * r + s));
}

mat2 rot2d(float a) {
  float c = cos(a), s = sin(a);

  return mat2(c, s, -s, c);
}

float opSmoothUnion( float d1, float d2, float k ) {
  float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
  return mix( d2, d1, h ) - k*h*(1.0-h); 
}

vec3 opSmoothUnion( vec3 d1, vec3 d2, float k ) {
  vec3 h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
  return mix( d2, d1, h ) - k*h*(1.0-h); 
}

vec3 kifs(vec3 p, float d, float s, float tf) {
  float t = tf * time;

  for (float i = 0.; i < d; i += 1.) {
    p.xy *= rot2d(t + i);
    p.xz *= rot2d(t * 0.6 - i);
 	p = opSmoothUnion(p, -p, -1.);
    p -= s;
    s *= 0.7;
  }

  return p;
}

float map(vec3 p) {
  float wave = .5 * sin(time) + .5;
  float d = 100.;
  vec3 psph = kifs(p, 3., 1., .5);
  vec3 pextr = kifs(p, 3., 1. + .1 * wave, .6);

  float dsph = length(psph) - 1.;
  float dextr = length(pextr + sin(time) * vec3(.5, 0., 0.)) - .9;
  float dobj = opSmoothUnion(dsph, -dextr, -.1);

  d = min(d, dobj);

  return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1);

  vec3 ro = vec3(0, 0, -20.);
  vec3 rd = normalize(vec3(uv, 1));

  float d = 0.;
  int i;
  for (i = 0; i < 100; i++) {
    vec3 p = ro + d * rd;
    float ds = map(p);

    if (ds < 0.01 || ds > 100.) {
      break;
    }

    d += ds * .5;
  }
  vec3 p = ro + d * rd;
  vec2 e = vec2(0.01, 0);
  vec3 n = normalize(map(p) - vec3(map(p - e.xyy), map(p - e.yxy), map(p - e.yyx)));
  vec3 l = vec3(20., 20., -5.);
//  l.xz *= rot2d(time / .1);
  float dif = dot(n, normalize(l - p));

  p.z = clamp(p.z, 0., 50.);
  vec3 col = vec3(dif * palette(p.z / 1.));

  fragColor = vec4(col, 1.);
}

#define PI  3.14159265359
#define PI2 6.28318530718

#define ARMS 6.
#define ANGLE_SPEED 3.
#define DENSITY 3.
#define SPEED 8.

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2
        R = iResolution.xy,
        // Cartesian [-1, 1] along shorter axis.
        uv = (2. * fragCoord - R) / min(R.x, R.y);
    float
        T = iTime,
        // Angle [-PI, PI] and radius.
        a = atan(uv.x, uv.y),
        r = length(uv);

    // Patterns based on the angle.
    float ap1 = sin(a * ARMS + T * ANGLE_SPEED);
    // Patterns based on radius.
    float rp1 = sin(1. / r * PI2 * DENSITY + T * SPEED);
    // Expose interference-like patters in the middle.
    float rp2 = smoothstep(9., 1., 1. / r);

    vec3 color = vec3(ap1 * rp2, rp1, rp2);
    fragColor = vec4(color * 1.2, 1.);
    //gamma22(fragColor, color);
}

float rand2d(vec2 uv) {
    return fract(sin(dot(uv, vec2(13.23, 78.1231))) * 1000000.0);
}

vec2 ddrand2d(vec2 uv) {
    vec2 w = fract(sin(vec2(
        dot(uv, vec2(13.23, 78.1231)),
        dot(uv, vec2(799.134, 42.52))
        )) * 1000000.0);
    return (w - 0.5) * 2.0;
}

float perlin(vec2 uv) {
    vec2 u = floor(uv);
    vec2 f = fract(uv);
    vec2 s = smoothstep(0.0, 1.0, f);

    vec2 a = ddrand2d(u);
    vec2 b = ddrand2d(vec2(u.x + 1.0, u.y));
    vec2 c = ddrand2d(vec2(u.x, u.y + 1.0));
    vec2 d = ddrand2d(vec2(u.x + 1.0, u.y + 1.0));
    return mix(mix(dot(a, f), dot(b, f - vec2(1.0, 0.0)), s.x),
               mix(dot(c, f - vec2(0.0, 1.0)), dot(d, f - vec2(1.0, 1.0)), s.x), s.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    float aspect = iResolution.x / iResolution.y;
	vec2 off = vec2(iTime / 100.0, 1.0);
    uv += off;
    float r = perlin(vec2(uv.x * aspect, uv.y) * 2.0) * 0.5 + 0.5;
    r *= sin(iTime / 2.5) * 0.25 + 0.75;
    vec3 col = vec3(0.0, 0.0, 0.0);
    if (r < 0.4) {
        // Water
        col = mix(r * vec3(0.2, 0.8, 0.78), vec3(0.1, 0.7, 0.8), 1.0 - pow(r / 0.4, 5.0));
    } else if (r > 0.65) {
        // Snow
        col = r * vec3(1.0, 1.0, 1.0);
    } else if (r > 0.5) {
        // Grass
        col = r * vec3(0.3, 0.87, 0.4);
    } else {
        // Land
        col = r * vec3(0.989, 0.52, 0.22);
    }

    fragColor = vec4(col, 1.0);
}

