void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.y*=iResolution.y/iResolution.x;

    vec3 ro = vec3(-.5,.5+sin((iTime-4.5)*.0982)*.4, -3.0+iTime);
    vec3 lookat = vec3(1.0+uv.x*(cos(uv.y+3.14)+1.0)*10.0, .2+uv.y*(cos(uv.x+3.14)+1.0)*-5.0 , iTime); //fish eye effect.
    vec3 f = normalize(lookat-ro);
    vec3 r = normalize(cross(vec3(0., 1., 0.),f));
    vec3 u = cross(f, r);
    vec3 rd = f + uv.x*r + uv.y*u;
    vec4 d = RayMarch(ro, rd, true, 1.0);
	vec3 p = ro + rd * d.x;
	vec3 col = GetLight(p, true);

// Colourize scene, 0 to1 - Brick 2 - Pendulum 3 - Water 10 - Sky

	if (d.y < 1.5) col *= mix(vec3 (0.7725491, 0.4196079, 0.2196079), vec3 (0.9666667, 0.8254902, 0.709804), d.y*300.0);  //brick
	if (d.y > 1.5 && d.y < 2.5) col *= vec3 (0.5529412, 0.2980392, 0.1411765)+GetSpecular(p,rd); //pendulum
	if (d.y > 2.5 && d.y < 3.5) col *= .5*mix(vec3 (0.0411765, 0.2215687, 0.6215686),vec3 (0.1411765, 0.6215687, 0.9215686),pow(clamp(1.0-d.z*.5,0.0,1.0),2.0))+GetReflectionWater(p,rd)*.5; //water, deep, shallow
	col = mix(col, mix(vec3 (0.7490196, 0.6352941, 0.3333333),vec3 (0.2537255, 0.2288235, 0.1415686),pow(clamp(p.y*.15,0.0,1.0),.4)),clamp(d.x*.025,0.0,1.0)); //sky&fog
    col += vec3(0.2431373,0.2588235,0.2039216)*.2; //ambient
	col *= pow(1.0-(pow(uv.x*1.4,2.0)+pow(uv.y*2.49,2.0)),.1); //Vignette

        fragColor =  vec4(col,1.0);
}

mat3 transformationMat(vec2 scale, float angle, vec2 pos){
    float c = cos(angle);
    float s = sin(angle);
    return mat3(1., 0., pos.x,
                0., 1., pos.y, 
                0., 0., 1.) *
           mat3(c , -s, 0.,
           		s ,  c, 0.,
          		0., 0., 1.)*
           mat3(1./scale.x, 0., 0.,
                0., 1./scale.y, 0.,
                0., 0., 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec3 col;

    float r = 1.;

    mat3 transform = transformationMat(vec2(0.125, (1.3+sin(iTime))/8.), iTime, vec2(cos(iTime)/3., sin(iTime)/3.));

    mat3 inv = inverse(transform);

    float xPos = inv[0][2];
    float yPos = inv[1][2];
    float x = (abs(inv[0][0])+abs(inv[0][1]));
    float y = (abs(inv[1][0])+abs(inv[1][1]));

    float minX = xPos-x;
    float minY = yPos-y;
    float maxX = xPos+x;
    float maxY = yPos+y;

    if(uv.x < maxX && uv.x > minX &&
       uv.y < maxY && uv.y > minY)
    {
        col = vec3(1., 0., 0.);
    }

    uv = (vec3(uv.xy, 1.)*transform).xy;


    if(uv.x < 1. && uv.x > -1. &&
       uv.y < 1. && uv.y > -1.)
    {
        col = vec3(1., 1., 1.);
    }

    if(uv.x*uv.x+uv.y*uv.y < r*r)
    {
        col = vec3(0., 1., 0.);
    }

    fragColor = vec4(col,1.0);
}

const float pi=acos(-1.0);
const float pi2=pi*2.0;

mat2 rot(float a){
    float c=cos(a), s=sin(a);
    return mat2(c, s, -s, c);
}

vec2 pmod(vec2 p, float r){
    float a=atan(p.x, p.y)+pi/r;
    float n=pi2*(-sin(0.42*iTime)*sin(0.42*iTime)+0.35)/r;
    a=floor(a/n)*n-pi;
    return p*rot(-a);
}

float crossf(vec3 p, float r){
    p=abs(p);
    float dxy=max(p.x, p.y);
    float dyz=max(p.y, p.z);
    float dxz=max(p.z, p.x);
    return min(dxy, min(dyz, dxz))-r;
}

float boxf(vec3 p, vec3 b){
    p=abs(p)-b;
    return length(max(p, 0.0))+min(max(p.x, max(p.y, p.z)), 0.0);
}


float mengersponge(vec3 p, float scale, float width){
    float d=boxf(p, vec3(1.0));
    float s=1.0;
    for(int i=0; i<5; i++){
        vec3 a=mod(p*s, 2.0)-1.0;
        s*=scale;
        vec3 r=1.0-scale*abs(a);
        float c=crossf(r, width)/s;
        d=max(d,c);
    }
    return d;
}

float map(vec3 p){
    p=mod(p, 4.0)-2.0;
    return mengersponge(p, 3.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv=(fragCoord.xy*2.0-iResolution.xy)/min(iResolution.x, iResolution.y);
    vec3 ro=vec3(0.0, 0.0, 8.0*iTime);
    vec3 rd=normalize(vec3(uv, 0.8*sin(0.58*iTime)));
    vec3 col=vec3(0);
    float dp=0.0, dpp=0.0;
    for(int i=0; i<99; i++){
        vec3 pos=ro+rd*dp;
        pos.xy*=rot(0.1*iTime);
        pos.xy=pmod(pos.xy, 8.0);
        float d=map(pos);
        if(d<0.00001){
            col=vec3(1.0-float(i)*0.02);
            break;
        }
        dp+=d*0.8;
        dpp=d*75.516;
    }
    fragColor=vec4(col, dpp);
}

//
// - Thin isosurfaces of 3d noise as emission source
// - Beer-Lambert model of absorption
//

#define M_PI 3.14159

float SCALE_TIME = 0.15;

// Emission source
float NOISE_SCALE = 2.0;
float NOISE_NUM_OCTAVES = 6.0;
float ISOSURFACE_VALUE_HALF_WIDTH = 0.03;
float NUM_ISOSURFACE_SIDES = 2.0;


// Volume
float VOLUME_DEPTH = 0.2;
float VOLUME_NUM_SAMPLES = 6.0;
float EMISSION_SCALE = 0.2;
vec3 EMISSION_COLOR = vec3(0.0, 1.0, 1.0);


//
// Noise
//

float hash31(vec3 v) {
  vec3 u = vec3(1234.5, 5432.1, 5678.9);
  return fract(sin(dot(v, u) * 2357.0) * 56789.0);
}

float hash41(vec4 v) {
  vec4 u = vec4(1234.5, 5432.1, 5678.9, 3456.7);
  return fract(sin(dot(v, u) * 2357.0) * 56789.0);
}

vec2 hash32(vec3 v) {
  return vec2(hash31(v), hash41(vec4(v, 1.0)));
}

vec3 hashGradient3(vec3 v) {
  vec2 p = hash32(v);

  // Usual spherical sampling
  // Prob([0, theta] \sub [0, pi]) = (1 - cos(theta)) / 2
  float theta = acos(1.0 - 2.0 * p[0]);
  float phi = 2.0 * M_PI * p[1];

  return vec3(
    sin(theta) * cos(phi),
    sin(theta) * sin(phi),
    cos(theta)
  );
}

float mix2(float f00, float f10, float f01, float f11, vec2 uv) {
  return mix(mix(f00, f10, uv[0]), mix(f01, f11, uv[0]), uv[1]);
}

float mix3(
    float f000, float f100, float f010, float f110,
    float f001, float f101, float f011, float f111,
    vec3 v) {
  float fxy0 = mix2(f000, f100, f010, f110, v.xy);
  float fxy1 = mix2(f001, f101, f011, f111, v.xy);
  return mix(fxy0, fxy1, v.z);
}

float gradientNoise3(vec3 v) {
  vec3 vi = floor(v);
  vec3 vf = v - vi;
  float f000 = dot(hashGradient3(vi + vec3(0.0, 0.0, 0.0)), vf - vec3(0.0, 0.0, 0.0));
  float f100 = dot(hashGradient3(vi + vec3(1.0, 0.0, 0.0)), vf - vec3(1.0, 0.0, 0.0));
  float f010 = dot(hashGradient3(vi + vec3(0.0, 1.0, 0.0)), vf - vec3(0.0, 1.0, 0.0));
  float f110 = dot(hashGradient3(vi + vec3(1.0, 1.0, 0.0)), vf - vec3(1.0, 1.0, 0.0));
  float f001 = dot(hashGradient3(vi + vec3(0.0, 0.0, 1.0)), vf - vec3(0.0, 0.0, 1.0));
  float f101 = dot(hashGradient3(vi + vec3(1.0, 0.0, 1.0)), vf - vec3(1.0, 0.0, 1.0));
  float f011 = dot(hashGradient3(vi + vec3(0.0, 1.0, 1.0)), vf - vec3(0.0, 1.0, 1.0));
  float f111 = dot(hashGradient3(vi + vec3(1.0, 1.0, 1.0)), vf - vec3(1.0, 1.0, 1.0));
  vec3 vf_smooth = smoothstep(vec3(0.0), vec3(1.0), vf);
  float t = mix3(f000, f100, f010, f110, f001, f101, f011, f111, vf_smooth);
  // Normalize via upper/lower bound = +- 2 / sqrt(3) ~= 1.15
  return (t / 1.15 + 1.0) * 0.5;
}

float noise(vec3 v, float n) {
  float result = 0.0;
  for (float i = 0.0; i < n; i++) {
    float p = pow(2.0, i);
    result += (gradientNoise3(v * p) / p);
  }
  result /= (pow(2.0, n) - 1.0) / (pow(2.0, n - 1.0));
  return result;
}

//
// Misc
//

float smoothBump(float fac, float bump, float half_width) {
  return 1.0 - smoothstep(0.0, half_width, abs(fac - bump));
}

//
// Main
//

void mainImage(out vec4 frag_color, vec2 frag_coord) {
  vec2 uv =  frag_coord / iResolution.y;
  float t = SCALE_TIME * iTime;

  // Compute "intensity" by summing each layers
  float fac_total = 0.0;
  for (float i = 0.0; i < VOLUME_NUM_SAMPLES; i++) {
    float z = i / VOLUME_NUM_SAMPLES * VOLUME_DEPTH;

    // Noise
    float fac_noise = noise(vec3(NOISE_SCALE * uv, z + t), NOISE_NUM_OCTAVES);
    fac_noise = smoothstep(0.0, 1.0, fac_noise); // tonemap

    // Pickup isosurfaces
    float fac = 0.0;
    fac += smoothBump(fac_noise, 0.5, ISOSURFACE_VALUE_HALF_WIDTH);
    for (float j = 1.0; j <= NUM_ISOSURFACE_SIDES; j++) {
      fac += smoothBump(fac_noise, 0.5 + 4.0 * j * ISOSURFACE_VALUE_HALF_WIDTH, ISOSURFACE_VALUE_HALF_WIDTH);
      fac += smoothBump(fac_noise, 0.5 - 4.0 * j * ISOSURFACE_VALUE_HALF_WIDTH, ISOSURFACE_VALUE_HALF_WIDTH);
    }

    // Attenuate by depth
    fac_total += exp(- z) * fac;
  }


  frag_color = vec4(fac_total * EMISSION_SCALE * EMISSION_COLOR, 1.0);
}

precision highp float;


mat2 rot(float a) {
    float c = cos(a), s = sin(a);
    return mat2(c,s,-s,c);
}

const float pi = acos(-1.0);
const float pi2 = pi*2.0;

vec2 pmod(vec2 p, float r) {
    float a = atan(p.x, p.y) + pi/r;
    float n = pi2 / r;
    a = floor(a/n)*n;
    return p*rot(-a);
}

float box( vec3 p, vec3 b ) {
    vec3 d = abs(p) - b;
    return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

float ifsBox(vec3 p) {
    for (int i=0; i<5; i++) {
        p = abs(p) - 1.0;
        p.xy *= rot(iTime*0.3);
        p.xz *= rot(iTime*0.1);
    }
    p.xz *= rot(iTime);
    return box(p, vec3(0.4,0.8,0.3));
}

float map(vec3 p, vec3 cPos) {
    vec3 p1 = p;
    p1.x = mod(p1.x-5., 10.) - 5.;
    p1.y = mod(p1.y-5., 10.) - 5.;
    p1.z = mod(p1.z, 16.)-8.;
    p1.xy = pmod(p1.xy, 5.0);
    return ifsBox(p1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);

    vec3 cPos = vec3(0.0,0.0, -3.0 * iTime);
    // vec3 cPos = vec3(0.3*sin(iTime*0.8), 0.4*cos(iTime*0.3), -6.0 * iTime);
    vec3 cDir = normalize(vec3(0.0, 0.0, -1.0));
    vec3 cUp  = vec3(sin(iTime), 1.0, 0.0);
    vec3 cSide = cross(cDir, cUp);

    vec3 ray = normalize(cSide * p.x + cUp * p.y + cDir);

    // Phantom Mode https://www.shadertoy.com/view/MtScWW by aiekick
    float acc = 0.0;
    float acc2 = 0.0;
    float t = 0.0;
    for (int i = 0; i < 99; i++) {
        vec3 pos = cPos + ray * t;
        float dist = map(pos, cPos);
        dist = max(abs(dist), 0.02);
        float a = exp(-dist*3.0);
        if (mod(length(pos)+24.0*iTime, 30.0) < 3.0) {
            a *= 2.0;
            acc2 += a;
        }
        acc += a;
        t += dist * 0.5;
    }

    vec3 col = vec3(acc * 0.01, acc * 0.011 + acc2*0.002, acc * 0.012+ acc2*0.005);
    fragColor = vec4(col, 1.0 - t * 0.03);
}

#define MAX_HEIGHT 60.0
#define MAX_DISTANCE 400.0
#define NOISE_SCALE 0.03
#define WATER_HEIGHT 23.0

#define PI 3.14159265359

float hash( float n )
{
  return fract(cos(n)*41375.92653);
}

float noise2(in vec2 x)
{
  vec2 p  = floor(x);
  vec2 f  = smoothstep(0.0, 1.0, fract(x));
  float n = p.x + p.y*57.0;

  return mix(
      mix(hash(n+0.0),  hash(n+1.0),  f.x),
      mix(hash(n+57.0), hash(n+58.0), f.x),
      f.y);
}

float fbm(in vec2 x)
{
    float val = 0.5 * noise2(x);
    val += 0.25 * noise2(2.01 * x);
    val += 0.12 * noise2(4.03 * x);
    val += 0.06 * noise2(7.99 * x);
    val += 0.03 * noise2(16.02 * x);
    //val += 0.01 * noise2(32.04 * x);
    return val/0.96;
}

float fbmDetailed(in vec2 x)
{
    float val = 0.5 * noise2(x);
    val += 0.25 * noise2(2.01 * x);
    val += 0.12 * noise2(4.03 * x);
    val += 0.06 * noise2(7.99 * x);
    val += 0.03 * noise2(16.02 * x);
    //val += 0.01 * noise2(32.04 * x);
    val += 0.002 * noise2(512.01 * x);
    return val/0.962;
}

float heightmap(in vec2 p)
{
    float height = MAX_HEIGHT * fbm(NOISE_SCALE * p);
    return height;
}

float heightmapDetailed(in vec2 p)
{
    float height = MAX_HEIGHT * fbmDetailed(NOISE_SCALE * p);
    return height;
}

vec3 terrainNormal(in vec3 p)
{
    float eps = 0.3;
    vec3 v1 = p - vec3(p.x-eps, heightmap(p.xz - vec2(eps,0.0)), p.z);
    vec3 v2 = p - vec3(p.x,     heightmap(p.xz - vec2(0.0,eps)), p.z-eps);
    vec3 n = normalize(cross(v2, v1));
    return n;
}

vec3 terrainNormalDetailed(in vec3 p)
{
    float eps = 0.3;
    vec3 v1 = p - vec3(p.x-eps, heightmapDetailed(p.xz - vec2(eps,0.0)), p.z);
    vec3 v2 = p - vec3(p.x,     heightmapDetailed(p.xz - vec2(0.0,eps)), p.z-eps);
    vec3 n = normalize(cross(v2, v1));
    return n;
}

float fresnel(in vec3 eyeDir, float R0)
{
    return R0 + (1.0 - R0)*pow(1.0 - dot(eyeDir, vec3(0.0,1.0,0.0)), 5.0);
}

vec3 terrainColor(in vec3 p)
{
    float h = p.y;
    float h2 = h + 5.0 * (2.0 * fbm(p.xz) - 1.0);
    vec3 col = vec3(0.1,0.3,0.0);
