struct CAMERA{
	vec3  camPos;
    vec3  rd;

};

float shortestDistToRay(CAMERA c, vec3 p){


      return    length(cross(p-c.camPos,c.rd))/length(c.rd);
}

float DrawPoint(CAMERA c, vec3 p){

    float  d       =  shortestDistToRay(c,p);
	       d       =  smoothstep(.06,.05,d);

    return d;
}
vec3 getCameraRayDirection(vec2 uv, vec3 lookat, vec3 ro, float zoom){

    vec3   f       =  normalize(lookat-ro);
    vec3   r       =  cross(vec3(0.,1.,0.),f);
    vec3   u       =  cross(f,r);
    vec3   sc      =  ro + f*zoom;
    vec3   i       =  sc + uv.x*r + uv.y*u;
    vec3   rd      =  i-ro;
    return rd;
}

CAMERA createCamera(vec2 uv,vec3 lookat,vec3 ro,float zoom){

    vec3   rd      =  getCameraRayDirection(uv,lookat,ro,zoom);

    CAMERA c       =  CAMERA(ro,rd);
    return c;
}

