mat2 rot2d(float a) {
  float c = cos(a), s = sin(a);

  return mat2(c, s, -s, c);
}

vec3 kifs(vec3 p, float d, float s, float tf) {
  float t = tf * time;

  for (float i = 0.; i < d; i += 1.) {
    p.xy *= rot2d(t + i);
    p.xz *= rot2d(t * 0.6 - i);
    p.xy *= rot2d(PI / 2.);
	p = abs(p);
    p -= s;
    s *= 0.8;
  }

  return p;
}

float map(vec3 p) {
  p.xy *= rot2d(time * .1);
  p.yz *= rot2d(time * .25);
  float wave = .5 * sin(time) + .5;
  float d = 10000.;
  vec3 p1 = kifs(p, 2., 1., .3);
  vec3 p2 = kifs(p, 5., .5 + 3. * (.5 * sin(time / 4.) + .5), .8);
//  vec3 p2 = kifs(p, 5., 2.5, .8);
  float mix_param = .2 + .8 * wave;
  p = mix(p1, p2, mix_param);
//  p = mix(p1, p2, .5 + .5 * sin(2. * time));
  d = min(d, length(p) - 1.);
  return d;
}

