vec3 GetNormal(vec3 p) {
	vec2 e=vec2(.001,0);
	return normalize(vec3(
		h(p+e.xyy)-h(p-e.xyy),
		h(p+e.yxy)-h(p-e.yxy),
		h(p+e.yyx)-h(p-e.yyx)
	));
}

float AO(vec3 p,vec3 q){			// AO at point p with normal q
	float o=0.,s=1.,r,d;
    	for(float i=0.;i<5.;i++){
		r=.01+.12*i/4.;
		vec3 a=q*r+p;
		d=h(a);
		o+=-(d-r)*s;
		s*=.95;
    	}
    	return clamp(1.-3.*o,0.,1.);   
}

float SH(vec3 p, vec3 q){			// Calculate shadow amount: p=intersection point; q=light direction
	vec3 r = p + q*.01;
	float d;
	for(int i=0; i<16; i++) {
		d = h(r);
		if (d < .001)
			break;
		r += q * d;				// March along!
	}
	if (d < 0.001)
		return 0.1;
	else
		return 1.;
}

