		vec3 c = vec3(.8,.9,1) * diffuse * AO(p,n) * SH(p,ld);



        vec3 lp1 = vec3(10.0,1.,iTime-5.0);		// Light position

		vec3 ld1 = normalize(lp1-p);		// Light direction
		vec3 n1 = GetNormal(p); 			// Normal
		float diffuse1 = dot(n1,ld1);		// Diffuse amount
		vec3 c1 = vec3(.3,.5,.8) * diffuse1 * AO(p,n1) * SH(p,ld1);

		fragColor =( vec4(c, 1)+vec4(c1, 1))/1.5;



	} else						// We fucking hit NOTHING
		fragColor = vec4(uv.x / 2., uv.y * 1.4, 1, 1);
}

// Modified version of 10092018 by Edapx
// https://www.shadertoy.com/view/llVyWw

// 2D Random
float random (in vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))
                 * 43758.5453123);
}

// 2D Noise based on Morgan McGuire @morgan3d
// https://www.shadertoy.com/view/4dS3Wd
float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    // Smooth Interpolation
    // Cubic Hermine Curve.  Same as SmoothStep()
    vec2 u = f*f*(3.0-2.0*f);
    // u = smoothstep(0.,1.,f);

    // Mix 4 coorners percentages
    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

vec2 vectorField(vec2 uv){
  vec2 res = uv;
  float n = noise(res*vec2(3.0));
  res.y -= iTime*0.01;
  res += sin(res.yx*40.) * 0.02;
  res += vec2(n);
  return res;
}

float plot(float val, float c, float t){
  float l = smoothstep(c,c-t,val);
  float r = smoothstep(c,c-t/5.,val);
  return r-l;
}

