  col     += bgCol*vec3(smoothstep(1.7, .01,x+y));

  fragColor = vec4(col,1.0);
}

/*
	Fitting an unit arc using a quadratic bezier curve
	Cubic version: https://www.shadertoy.com/view/wly3WG

	As one can see, cubic fitting is much better than quadratic fitting.

    Since rendering quadratic is much faster than cubic, fitting the arc/ellipse
    using multiple quadratic bezier curves is still better. It's not easy to notice 
    the C0 continuety.

*/

#define PI 3.1415926
#define _13 0.333333333
#define cbrt(x) ((x)>0.?pow(x,_13):-pow(-(x),_13))

struct Bezier2{
    vec2 A, B, C;
};


// ========================= Fitting Code =========================


#if 0

// along the tangent
void fitArc(in float theta, out Bezier2 R) {
    float c = cos(theta), s = sin(theta);
    float k = theta==0.0 ? 0.0 : (1.0 - c) / s;
    R.A = vec2(1.0, 0.0), R.B = vec2(1.0, k), R.C = vec2(c, s);
}

#else

// least square, let the control point be k*(cos(θ/2),sin(θ/2))
void fitArc(in float theta, out Bezier2 R) {
    // derivative of error: error'=ax³+bx²+cx+d
    // thanks https://www.integral-calculator.com/ for helping me integrate that disgusting function!
    float alpha = 0.5 * theta;
	float s1 = sin(alpha), s2 = s1 * s1, s4 = s2 * s2, s6 = s4 * s2,
		c1 = cos(alpha), c2 = c1 * c1, c3 = c2 * c1, c4 = c3 * c1, c5 = c4 * c1, c6 = c4 * c2, c7 = c5 * c2;
    float a = 32.*s4+64.*c2*s2+32.*c4,
        b = (60.*c1*s4+(120.*c3+60.*c1)*s2+60.*c5+60.*c3) / a,
        c = (20.*s6+(100.*c2-16.)*s4+(140.*c4+32.*c2-148.)*s2+60.*c6+48.*c4-108.*c2) / a,
        d = (35.*c1*s6+(105.*c3-5.*c1)*s4+(105.*c5+10.*c3-131.*c1)*s2+35.*c7+15.*c5-111.*c3-91.*c1) / a;
    // calculate the minima (zero derivative), solve a the cubic function
	float p = (c - b * b * _13) * _13, q = -0.5 * ((b*b / 13.5 - c * _13) * b + d);
	a = q * q + p * p * p;
	float k = a > 0.0 ? cbrt(q + sqrt(a)) + cbrt(q - sqrt(a)) - b * _13
		: 2.0 * pow(q*q - a, 0.5 * _13) * cos(atan(sqrt(-a), q) * _13) - b * _13;
    // apply the solution of the equation to the control points
    R.A = vec2(1.0, 0.0), R.B = k * vec2(c1, s1), R.C = vec2(c1*c1-s1*s1, 2.0*c1*s1);
}

#endif



