// quadratic bezier distance approximation by tayholliday: https://www.shadertoy.com/view/XsX3zf
float det(vec2 a, vec2 b) { return a.x*b.y-b.x*a.y; }
vec2 get_distance_vector(vec2 b0, vec2 b1, vec2 b2) {
  float a=det(b0,b2), b=2.0*det(b1,b0), d=2.0*det(b2,b1);
  float f=b*d-a*a;
  vec2 d21=b2-b1, d10=b1-b0, d20=b2-b0;
  vec2 gf=2.0*(b*d21+d*d10+a*d20);
  gf=vec2(gf.y,-gf.x);
  vec2 pp=-f*gf/dot(gf,gf);
  vec2 d0p=b0-pp;
  float ap=det(d0p,d20), bp=2.0*det(d10,d0p);
  float t=clamp((ap+bp)/(2.0*a+b+d), 0.0 ,1.0);
  return mix(mix(b0,b1,t),mix(b1,b2,t),t);
}
float sdBezier2(in Bezier2 R, in vec2 p) {
  return length(get_distance_vector(R.A-p, R.B-p, R.C-p));
}

void mainImage(out vec4 col, in vec2 P) {
    vec2 res = iResolution.xy;
    vec2 p = 6.0*(P-0.5*res)/length(res);
    float h = 0.2*length(res); 	// hardness of anti-aliasing

    float ang = atan(p.y,p.x);
    if (ang<0.0) ang += 2.0*PI;
    float a = sin(0.6*iTime); a=1.8*PI*a*a;
    int n = int(abs(a)/1.5) + 1;
    float da = a / float(n);
    if (iMouse.z>0.0){
        vec2 d = iMouse.xy-0.5*iResolution.xy;
        a = atan(d.y,d.x);
    	if (a<0.0) a += 2.0*PI;
        n=1, da=a;
    }

    // axis
    vec2 axis = clamp(h*(abs(p)-0.02),0.0,1.0);
    float bk = min(axis.x,axis.y);
    vec3 bkg = mix(vec3(0,0,1),vec3(1),bk);

    // circle
    float circ = clamp(h*(abs(length(p)-1.0)-0.02),0.0,1.0);
    vec3 c = (ang<a)?vec3(1,0,0):vec3(0.7);
    c = mix(c,bkg,circ);

    // bezier curve
    Bezier2 R;
    fitArc(da, R);
    if (ang<a){
        float t = mod(ang,da);
        vec2 q = length(p)*vec2(cos(t),sin(t));
    	float d = sdBezier2(R,q);
    	c = mix(vec3(0),c,clamp(h*(d-0.01),0.0,1.0));
    }

    // control points
    float d = length(p-R.B)-0.04;
    c = mix(vec3(1),c,clamp(h*d,0.0,1.0));
    c = mix(vec3(0),c,clamp(h*(abs(d)-0.002),0.0,1.0));
    d = min(length(p-R.A),length(p-R.C))-0.05;
    c = mix(vec3(1),c,clamp(h*d,0.0,1.0));
    c = mix(vec3(0),c,clamp(h*(abs(d)-0.005),0.0,1.0));

    col = vec4(c, 1.0);
}

