struct harmonicSettings
{
    float rowCount; 						// number of vertical divisions
    float cycleTime; 						// duration for the sync of all rows
    float oscillationsPerCycleOfSlowest;  	// oscillations of the slowest band per cycle
    float thickness;						// thickness of horizontal bands
    bool showThickness;
    bool showShade;
    bool showMirror;
};

vec3 getColor(harmonicSettings settings, vec2 uv)
{
    float rowIndex = floor(uv.y * settings.rowCount);    
    float oscillations = settings.oscillationsPerCycleOfSlowest + rowIndex;

    float period = settings.cycleTime / oscillations; 

    float twoPiR = 6.28318531;

    if (settings.showThickness)
    {
    	settings.thickness = settings.thickness * (1. - 2. * abs(uv.x - .5));
    }

    // normalizes -1,1 sin values to 0,1
    float xPos = (sin(twoPiR * iTime / period) + 1.) / 2.;

    float baseColorVal = .25;
    vec3 color = vec3(baseColorVal);

    bool mask = false;

    if (abs(xPos - uv.x) < settings.thickness)
    {
        color.r += (1. - baseColorVal);
        mask = true;
    }

    if (settings.showMirror && abs(xPos - (1. - uv.x)) < settings.thickness)
    {
        color.b += (1. - baseColorVal);
        mask = true;
    }

    if (mask)
    {
     	color.g = uv.y;   
    }

    if (settings.showThickness && settings.showShade && mask)
    {    
        float minShade = .25;
        float halfShade = 1. - ((1. - minShade) / 2.);

        float minShadeLocal, maxShadeLocal, centerValue;

        // background       
        if (cos(twoPiR * iTime / period) < 0.)
        {
            minShadeLocal = minShade;
            maxShadeLocal = halfShade;
            centerValue = 1. - cos(twoPiR / 2. * (abs(uv.x - .5)));
        }   
        else // foreground
        {
            minShadeLocal = halfShade;
            maxShadeLocal = 1.;
            centerValue = cos(twoPiR / 2. * (abs(uv.x - .5)));
        }

        float shadeValue = mix(minShadeLocal, maxShadeLocal, centerValue);

        color *= shadeValue;
    }

    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    // settings: rowCount, cycleTime, oscillations, thickness, showThickness, shade, mirror
    harmonicSettings preset00 = harmonicSettings(8., 20., 4., .25, true, true, true);  
    harmonicSettings preset01 = harmonicSettings(40., 120., 30., .25, true, true, true);
    harmonicSettings preset02 = harmonicSettings(120., 120., 10., .25, true, true, true);
    harmonicSettings preset03 = harmonicSettings(200., 120., 10., .25, false, true, false);
    harmonicSettings preset04 = harmonicSettings(120., 240., 10., .02, true, true, true);
    harmonicSettings preset05 = harmonicSettings(12., 1200., 10., .4, true, true, true);

    vec3 color = getColor(preset02, uv);

    fragColor = vec4(color, 1.0);
}

