#define REFLECTION_AMOUNT 4
#define Inf 10000.

struct Sphere
{
	vec3 position;
	float radius;
	vec3 albedo;
	vec3 specular;
};

Sphere CreateSphere(vec4 it, vec3 alb, vec3 spec)
{
    Sphere sphere;
    sphere.position = it.xyz;
 	sphere.radius = it.w;
	sphere.albedo = alb;
	sphere.specular = spec;
	return sphere;
}
struct Ray
{
	vec3 origin;
    vec3 direction;
    vec3 energy;
};

Ray CreateRay(vec3 origin, vec3 direction)
{
    Ray ray;
    ray.origin = origin;
    ray.direction = direction;
    ray.energy = vec3(1);
    return ray;
}

struct RayHit
{
	vec3 position;
	float distance;
	vec3 normal;
	vec3 albedo;
	vec3 specular;
};

RayHit CreateRayHit()
{
	RayHit hit;
	hit.position = vec3(0);
	hit.distance = Inf;
	hit.normal = vec3(0);
	return hit;
}

void IntersectGroundPlane(Ray ray, inout RayHit bestHit)
{
    float t = -ray.origin.y / ray.direction.y;
    if(t > 0. && t < bestHit.distance)
    {
        bestHit.distance = t;
        bestHit.position = ray.origin + t * ray.direction;
        bestHit.normal = vec3(0,1,0);
        bestHit.albedo = vec3(.2);
        bestHit.specular = vec3(.1);
    }
}

void IntersectSphere(Ray ray, inout RayHit bestHit, Sphere sphere)
{
	// Calculate distance along the ray where the sphere is intersected
	vec3 d = ray.origin - sphere.position;
	float p1 = -dot(ray.direction, d);
	float p2sqr = p1 * p1 - dot(d, d) + sphere.radius * sphere.radius;
	if (p2sqr < 0.)
	{
		return;
	}

	float p2 = sqrt(p2sqr);
	float t = p1 - p2 > 0. ? p1 - p2 : p1 + p2;
	if (t > 0. && t < bestHit.distance)
	{
		bestHit.distance = t;
		bestHit.position = ray.origin + t * ray.direction;
		bestHit.normal = normalize(bestHit.position - sphere.position);
		bestHit.albedo = sphere.albedo;
		bestHit.specular = sphere.specular;
	}

}

Ray CreateCameraRay(vec2 uv, vec2 M)
{
    vec3 origin = vec3(20.*sin(M.x*5.),2.,-30.*cos(M.x*5.));

    vec3 lookat = vec3(0,2.*M.y*5.+10.,10.);
    vec3 f = normalize(lookat-origin);
    vec3 rg = normalize(cross(vec3(0,1,0),f));
    vec3 u = normalize(cross(f,rg));
    vec3 c = origin + f*1.;
    vec3 i  = c + uv.x*rg + uv.y*u;

    vec3 direction = normalize(i-origin);
    return CreateRay(origin, direction);
}

RayHit Trace(Ray ray)
{
    RayHit bestHit = CreateRayHit();
    IntersectGroundPlane(ray, bestHit);

    Sphere sphere = CreateSphere(vec4(0,2.+sin(iTime)/2.,5,1),vec3(1,0,0),vec3(0));
    IntersectSphere(ray, bestHit, sphere);
    Sphere sphere2 = CreateSphere(vec4(2,2,5.+cos(iTime),.5),vec3(0,1,0),vec3(1));
    IntersectSphere(ray, bestHit, sphere2);
    Sphere sphere3 = CreateSphere(vec4(-2,2,5.+cos(iTime),.5),vec3(0,1,1),vec3(1));
    IntersectSphere(ray, bestHit, sphere3);
    Sphere sphere4 = CreateSphere(vec4(0.+sin(iTime),4.,5.,.5),vec3(1,1,0),vec3(.5));
    IntersectSphere(ray, bestHit, sphere4);
    Sphere sphere5 = CreateSphere(vec4(0.,15,20,10),vec3(1),vec3(1));
    IntersectSphere(ray, bestHit, sphere5);
    return bestHit;
}
const int KEY_SPACE = 32;
vec3 Shade(inout Ray ray, RayHit hit)
{
    float newTime = iTime + 10.;
	if(hit.distance < Inf)
    {
    	ray.origin = hit.position + hit.normal * 0.001;
        ray.direction = reflect(ray.direction, hit.normal);
        ray.energy *= hit.specular;

        float rot = texelFetch( iChannel0, ivec2(KEY_SPACE,2),0 ).x;
        vec3 lightPos;
        if(rot == 0.){
        	lightPos = normalize(vec3(5.*sin(newTime),5.,-5.*cos(newTime)*2.));
        }else{
        	lightPos = normalize(vec3(1.,2.,-5.));
        }

        Ray shadowRay = CreateRay(hit.position + hit.normal * 0.001, lightPos);
		RayHit shadowHit = Trace(shadowRay);
        if (shadowHit.distance != Inf)
		{
			return vec3(0);
		}
        return clamp(dot(hit.normal, lightPos) * 1.,0.,1.)* hit.albedo;
    }
    else
    {
        ray.energy = vec3(0);
        return vec3(.2);
    }

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 M = (iMouse.xy/iResolution.xy)*2.-1.;
    Ray ray = CreateCameraRay(uv, M);

    vec3 result = vec3(0);
    for (int i = 0; i < REFLECTION_AMOUNT; i++)
    {
    	RayHit hit = Trace(ray);
    	result += ray.energy * Shade(ray, hit);
        if(ray.energy.x == 0. || ray.energy.y == 0. || ray.energy.z == 0.) break;
    }

    fragColor = vec4(result,1.0);
}

float DrawCircle (vec2 coord, vec2 c, float r, float th)
{
    float d = length(coord - c * iResolution.xy);
    float col = 0.;

    if(d < r) col = 1.;
    else if(d < r + th) col = 1. - (d - r) / th;

    return col;
}

float DrawSquare (vec2 coord, vec2 c, vec2 r)
{
    float xd = length(coord.x - c.x * iResolution.x);
    float yd = length(coord.y - c.y * iResolution.y);

    float col = 0.;
    if(xd <= r.x && yd <= r.y) col = 1.;

    return col;
}

vec3 DrawFace(vec2 fragCoord, vec2 fc, float fr, vec3 col)
{
    float fTh = fr * 0.02; 
    float oTh = fr * 0.01;
    if(fTh < 2.) fTh = 2.;
    if(oTh < 2.) oTh = 2.;

    float face = DrawCircle(fragCoord, fc, fr, fTh);
	float lEye = DrawCircle(fragCoord, vec2(fc.x - fr * 0.0005,fc.y + fr * 0.001), fr * 0.15,oTh);
	float rEye = DrawCircle(fragCoord, vec2(fc.x + fr * 0.0005	,fc.y + fr * 0.001), fr * 0.15,oTh);    

    float lowMouth = DrawCircle(fragCoord, vec2(fc.x,fc.y - fr * 0.00005), fr * 0.675,oTh);
    float upMouth = DrawCircle(fragCoord, vec2(fc.x,fc.y + fr * 0.00075), fr * 0.9,oTh);
    float smile = lowMouth - upMouth;
    if(smile < 0.) smile = 0.;

    float mask = face - smile - lEye - rEye;

    return col * mask;
}

