float tau=6.28318;
float star(vec3 p,float t,float r,out float i){
 p.xz*=mat2(cos(t*r),-sin(t*r),sin(t*r),cos(t*r));
 float re=tau/5.,a=mod(atan(p.y,p.x)+re*t*.25,re)-re*.5,
 l=length(p.xy);
 vec3 q=vec3(cos(a)*l,sin(a)*l,p.z*p.z*10.),d=vec3(1.,0.,0.);
 i=length(q.xy-d.xy*clamp(dot(q.xy,d.xy),.0,1.))-(.5-q.x*.25);
 return length(q-d*clamp(dot(q,d),.0,1.))-(.5-q.x*.25);
}
float m(vec3 p,out float i,out float j){
 float t=iTime*2.,a=atan(p.y,p.x);
 return min(star(p*vec3(2.,2.,1.),t*.25,1.,i),
  star(vec3(mod(p.z+1.5,3.)-1.5,
   mod(a*12.+tau*.5+(t+tau*.9)*
    (mod(floor((p.z+1.5)/3.),2.)*3.-1.5),tau)-tau*.5,
     length(p.xy)-5.),(t+tau)*.25,1.,j));
}
void mainImage(out vec4 col,in vec2 coord){
  vec2 uv=(coord-iResolution.xy*.5)/iResolution.y,e=vec2(.01,.0);
  vec3 ro=vec3(0.,0.,-3.),rd=normalize(vec3(-uv,1.)),cp=ro+rd;
  float st=0.,dist=0.,cd=0.,i,j,g;
  for(;st<1.;st+=1./300.){
   cd=m(cp,i,j);
   if(abs(cd)<.01||cd>200.)break;
   dist+=cd*.05;
   cp=ro+rd*dist;
  }
  st=pow(st+(fract(sin(uv.x+ uv.y*7512.)*4573.1)-.5)*.1,5.);
  col=mix(vec4(1.,.8,.0,0.),vec4(0.),length(uv)-st)+st;
  if(cd<.01){
  vec3 n=normalize(vec3(cd-m(cp+e.xyy,g,g),cd-m(cp+e.yyx,g,g),
  cd-m(cp+e.yyx,g,g)));
  float li=clamp(dot(n,vec3(.577)),0.,1.);
  col.rgb=vec3(.95,.4,.12)*.25;
  col.rgb+=vec3(1.,1.,1.)*li*.75;i=-(i>.1?j:i);
  col.rgb=mix(col.rgb,vec3(1.,1.,0.)*1.2,(i+pow(i,4.)+step(.125,i))/3.);
 }
}

// Judgement of the Sun

// Many thanks to IQ
// https://www.iquilezles.org/www/articles/smin/smin.htm
// Polynomial Cubic Smooth Min (k = 0.1);
float smin(float a, float b, float k)
{
    float h = max(k-abs(a-b),0.0);
    return min(a,b)-h*h*h/(6.0*k*k);
}

// Noise
float randFloat(float n)
{
 	return fract( sin( n*64.19 )*420.82 );
}
vec2 randVec2(vec2 n)
{
 	return vec2(randFloat( n.x*12.95+n.y*43.72 ),randFloat( n.x*16.21+n.y*90.23 )); 
}
float worley(vec2 n, float s)
{
    float dist = 2.0;
    for( int x=-1;x<=1;x++ )
    {
        for( int y=-1;y<=1;y++ )
        {
            vec2 p = floor( n/s )+vec2(x,y);
            float d = length( randVec2( p )+vec2(x,y)-fract( n/s ) );
            if ( d < dist )
            {
             	dist = d;   
            }
        }
    }
    return dist;
}

float wave(float dist, float offset)
{
    return (0.01*sin(29.0*dist-0.9*(iTime+offset)))
        +(0.005*sin(72.0*dist-3.1*(iTime+offset)))
        +(0.003*sin(96.0*dist-4.2*(iTime+offset)));
}

// Clouds
void clouds(inout vec3 col, in vec2 uv)
{
    float noise = 0.8*worley(uv*64.0+vec2(-20.42*iTime,0.0), 128.0)+0.15*worley(uv*64.0+vec2(923.324-1.2*iTime,10.234), 5.0);
    float grain = randFloat(uv.x*12.95+uv.y*43.72+0.001*noise);
    noise -= grain*0.05;
    vec3 lightDir = normalize(vec3(-0.25, 0.75, 0.1));
    vec3 norm = normalize(vec3(worley(uv*256.0+vec2(90.1921,403.32), 32.0-8.0*noise)-0.5, 0.4*(worley(uv*(64.0+128.0*noise)+vec2(90.1921,3.14159), 24.0)-0.5), 0.5));
    float lightDot = dot(lightDir, norm);
    float disp = 10.0*wave(lightDot+uv.y,0.0);
    float light = clamp(0.64+lightDot, 0.0, 1.0);

    col = mix(vec3(0.1,0.42,0.64), col, light);
}

