float smoothMin(float distA, float distB, float k){
	float h = max(k-abs(distA - distB),0.0)/k;
    return min(distA, distB) - h*h*h*k*1.0/6.0;
}

mat2 m2 = mat2(0.8,  0.6, -0.6,  0.8);
mat2 im2 = mat2(0.8,  -0.6, 0.6,  0.8);


float noise(in vec2 p){

    float res=0.;
    float f=1.;
	for( int i=0; i< 3; i++ ) 
	{		
        p=m2*p*f+.6;     
        f*=1.2;
        res+=sin(p.x+sin(2.*p.y));
	}        	
	return res/3.;
}

float fbmabs( vec2 p ) {

	float f=1.;   
	float r = 0.0;	
    for(int i = 0;i<8;i++){	
		r += abs(noise( p*f )+.5)/f;       
	    f *=2.;
        p=im2*p;    
	}
	return 1.-r*.5;
}
//courtesy of https://www.shadertoy.com/view/4l23Rh
vec3 sky(in vec2 p)
{	
return sin(vec3(1.7,1.5,1)+ .7+ .9*fbmabs(p*4.-.02*iTime))+.25;
}

vec2 r(vec2 ax, float a) {return mat2(cos(a),-sin(a),sin(a),cos(a))*ax;}

float sdRoundBox( vec3 p, vec3 b, float r )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}

float checkers( in vec2 p )
{
    vec2 q = floor(p);
    return mod(q.x+q.y,2.);
}

vec2 map(vec3 pos)
{
    vec3 boxpos = pos;
    boxpos/=1.25;
    boxpos.xy = r(boxpos.xy,1.); 
    boxpos.xz -=.025*sin(pos.x*20.*(mod(sin(iTime/8.)*1.2,2.4)-1.2)*1.25);
    boxpos*=2.;
    float sphere2 = float(length(vec3(sin(iTime)*1.25,-.90-sin(iTime),cos(iTime)*1.25)-pos)-.35);
    float sphere3 = float(length(vec3(cos(iTime+1.43)*1.25,-.90-cos(iTime),sin(iTime)*1.25)-pos)-.35);
    float box = length(boxpos)-1.25;
    float plane = float(pos.y+1.5);

    vec2 scene = vec2(10.);

    //SDF+materials
    scene = vec2(smoothMin(scene.x,sphere2,0.4), scene.x<sphere2 ? scene.y:2.);
    scene = vec2(smoothMin(scene.x,box,0.4), scene.x<box ? scene.y:3.);
    scene = vec2(smoothMin(scene.x,sphere3,0.4), scene.x<sphere3 ? scene.y:4.);
    scene = vec2(smoothMin(scene.x,plane,.4),scene.x<plane ? scene.y:1.);
    return scene;
}

vec2 rm(vec3 ro, vec3 rd)
{
    vec2 dO = vec2(0.);

    for (int i = 0; i < 128; i++)
    {
    	vec2 t = map(ro + dO.x * rd);
        dO.x += t.x;
        dO.y = t.y;
        if(t.x < .01 || dO.x > 50.) break;
    }
    return dO;
}

vec3 n(vec3 pos)
{
    float d = map(pos).x;
    vec2 e = vec2(.001,.0);
    vec3 pn = d - vec3(
        map(pos-e.xyy).x,
        map(pos-e.yxy).x,
        map(pos-e.yyx).x
        );
	return normalize(pn);
}

vec3 l(vec3 pos)
{
    vec3 total = vec3(0.);
    vec3 nor = n(pos);
    vec3 sundir = normalize(vec3(6.0,5.,0.)); 
    vec3 sundif = clamp(dot(nor,sundir),.0,1.)*vec3(.67);  
    vec3 skydif = clamp(dot(nor,vec3(.0,1.,.0)),.0,1.)*vec3(.5,.0,.5);
    vec3 bounce = clamp(dot(nor,-sundir),.0,1.)*vec3(.15);  
    total+=sundif+bounce+skydif;  
    return total;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;   

    //bg
    vec3 col = vec3(.5,-.5+uv.y,uv.y+.75)*sky(vec2(uv.x/3.+iTime/14.,uv.y/3.));

    vec3 ro = vec3(3.*sin(10.*iMouse.x/iResolution.x+0.),-1.,3.*cos(10.*iMouse.x/iResolution.x));
    vec3 ta = vec3(.0,.0,.0);

    vec3 ww = normalize(ta-ro);
    vec3 uu = normalize(cross(ww,vec3(.0,1.,.0)));
    vec3 vv = normalize(cross(uu,ww));

    vec3 rd = normalize(uv.x*uu+uv.y*vv+ww*1.5);
    vec2 r = rm(ro,rd);
    vec3 pos = ro+rd*r.x;

    if(r.x < 50.)
    {
        //floor
        if(r.y == 1.) 
        {            
            vec3 ro = pos+n(pos)*.02;
            vec3 rd = normalize(reflect(rd,n(pos)));
            vec2 r = rm(ro,rd);
            vec3 prf = ro+rd*r.x;
			float f = checkers(pos.xz);
            if(r.y == 2.) {
            col = l(pos)*vec3(.2)+fbmabs(pos.xy*10.)*vec3(.1,.1,2.9);   
                } 
            else if(r.y == 4.) {
            col = l(pos)*vec3(.2)+fbmabs(pos.xy*10.)*vec3(2.1,.1,.6);   
            } else {
				col=l(prf*vec3(f))*sky(vec2(iTime/4.+uv.x,uv.y))*vec3(.5,.0+uv.y,.5);
               }
        }

        //ballz
        if(r.y == 2.) {  
            col = l(pos)*vec3(.2)+fbmabs(pos.xy*10.)*vec3(.1,.1,2.9);   
        }
        if(r.y == 4.) {  
            col = l(pos)*vec3(.2)+fbmabs(pos.xy*10.)*vec3(2.1,.1,.6);   
        }

        //glass sphere
        if(r.y == 3.) 
        {
            col = vec3(.05,.06,.02);
            vec3 ro = pos+n(pos)*.02;
            vec3 rd = normalize(reflect(rd,n(pos)));
            vec2 r = rm(ro,rd);
            vec3 prf = ro+rd*r.x;  
               if(r.y == 2.) {
            col = l(pos)*vec3(2)+fbmabs(pos.xy*10.)*vec3(.1,.1,2.9);   
               } else if(r.y == 4.) {
	            col = l(pos)*vec3(.2)+fbmabs(pos.xy*10.)*vec3(2.1,.1,.6);   
               } else if(r.y ==1.) {
                   float f = checkers(prf.xz);
                   col+=l(prf*vec3(f))*sky(-iTime/4.+prf.xz)*(vec3(.5+uv.y))*vec3(.75,.50+uv.y,.75);
               }  
               else
               {
                col+=l(prf)*vec3(.5,.5,uv.y+.75)/1.+sky(vec2(uv.x+-iTime/8.,uv.y))/2.*vec3(.5,.0+uv.y,.5);
               }
        }      
    }
    fragColor = vec4(col,1.0);
}

bool near(float a, float b){
	return abs(a-b)<0.1;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float t = iTime;
    while(t>6.){
    	t = t - 6.;
    }
    float scale = t*10.-float(int(t*10./50.))/50.+10.;

    vec2 uv = fragCoord/iResolution.xy;
    uv = (fragCoord-0.5*iResolution.xy)/scale;

    float x= uv.x;
    float y= uv.y;

    float gray;

    if(near(sin(x*x+y*y),cos(x*y))){
        gray = 1.;
    }else{
    	gray = 0.;
    }


    float r = gray;
    float g = gray;
    float b = gray;

    vec3 col = vec3(r,g,b);
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 u = 3.*(2.*fragCoord-iResolution.xy)/iResolution.y;
    vec2 m = 8.*(2.*iMouse.xy-iResolution.xy)/iResolution.y;
    float pi = 3.14159265359;

    vec2 p = cos(4.1+vec2(0.,pi*.5))*(2.7+sin(iTime)*.2);
    if(iMouse.z > .5) p = m;

    u = u*length(p);

    for(float i = .5; i < 20.; ++i)
    {
        float e = mod(i,2.)-.5;
        e = mix(   6.,				//close hand fan
                1./6.,				//open  hand fan
                e);										
        u = sin(atan(u.x,u.y)*e+vec2(0.,pi*.5))*pow(length(u),e);
        u-= p;														
    }
    fragColor = .5+.5*sin(6.28*(.55+length(u)/length(p)+vec4(.2,.05,.1,.0)));
}

#define TESTGRID true
#define BACKIMAGE true


struct stopDef {
    vec2 center;
    float z;
    float radius;
};

bool clipStop(vec3 start, vec3 ray, stopDef stop) {
    float t = (stop.z - start.z) / ray.z;
    if (t >= 0.) {
    	vec2 hitPos = (start + ray * t).xy;
        vec2 hitxy = hitPos - stop.center;
        return dot(hitxy, hitxy) <= stop.radius * stop.radius;                
    } else {    
    	return false;
    }
}


struct surfaceDef {
    vec2 center;
    float z;
    float curvRadius;
    float nRatio;
};


bool refractSurface(in vec3 start, in vec3 ray, surfaceDef surface, out vec3 end, out vec3 endRay) {
    //x = x0 + v*t
    //sphere:
    //v^2*t^2 + 2*(x0 - c)*v*t + (c0 - x)^2 - r^2 = 0
    //dc = c0 - x
    //tca = -(x0 - c) * v/v^2 = dc * v/v^2
    //==> 
    //t^2 - 2*tca*t + dc^2/v^2 - r^2/v^2 = 0
    //==> t = tca +/- sqrt(tca^2 + r^2/v^2 - dc^2/v^2)

    vec3 curvCenter = vec3(surface.center, surface.z + surface.curvRadius);
    vec3 dc = curvCenter - start;
    float tca = dot(dc, ray);
    float tdelta = sqrt(tca * tca + surface.curvRadius * surface.curvRadius - dot(dc, dc));

    float t;
    if (tca - tdelta >= 0. && surface.curvRadius >= 0.) {
        t = tca - tdelta;
    } else if (tca + tdelta >= 0. && surface.curvRadius <= 0.) {
        t = tca + tdelta;
    } else {
        return false;
    }

    vec3 hitPos = start + ray * t;

    vec3 normal = normalize(hitPos - curvCenter);
    vec3 perp = dot(ray, normal) * normal;
   	vec3 parallel = ray - perp;    
    vec3 parallelOut = parallel / surface.nRatio;
    float po2 = dot(parallelOut, parallelOut);
    if (po2 > 1.) {
        // total internal reflection
        return false;
    }

    endRay = parallelOut + normalize(perp) * sqrt(1. - po2);

    end = hitPos;
    return true;
}

struct lensDef {
    surfaceDef front;
    surfaceDef back;
    stopDef stop;
};

lensDef makeLens(float z, float power, float powerDelta, float radius, float thickness, float nIndex) {    
    lensDef lens;
   	lens.front.nRatio = nIndex;
    lens.back.nRatio = 1.0 / nIndex;
    lens.front.center = vec2(0.);
    lens.back.center = vec2(0.);

    float r1 = (nIndex - 1.) * 2. / (power + powerDelta + .00001);
    float r2 = -(nIndex - 1.) * 2. / (power - powerDelta + .00001);

    lens.front.curvRadius = r1;
    lens.back.curvRadius = r2;

    lens.front.z = z;
    lens.back.z = z + thickness;

    lens.stop.center = vec2(0.);
    lens.stop.z = z - 0.00001;
    lens.stop.radius = radius;

    return lens;
}

bool traceLens(in vec3 start, in vec3 ray, lensDef lens, out vec3 end, out vec3 endRay) {
    if (clipStop(start, ray, lens.stop)) {                
        //ray = normalize(ray);
        if (refractSurface(start, ray, lens.front, start, ray)) {
            //ray = normalize(ray);
            if (refractSurface(start, ray, lens.back, start, ray)) {
                end = start;
                endRay = ray;
                return true;
            }
        }
    }

    return false;		    
}

vec4 sampleBackground(vec2 upvp) {
    vec4 hitColor = vec4(0.);
    if (BACKIMAGE) {
            hitColor = texture(iChannel0, clamp(upvp + 0.5, 0., 1.));
    }
    // test grid
    if (TESTGRID) {
        hitColor = hitColor / 3. + vec4(0.);
        vec2 upvpc = mod(upvp + 0.01, 0.02) - 0.01;
        if (sqrt(dot(upvpc, upvpc)) < 0.002) hitColor = vec4(1.);
        if (min(abs(upvpc.x), abs(upvpc.y)) < 0.0002) hitColor = vec4(1.);
    }
	return hitColor;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Screen coordinates. interpretation: sin(alpha), sin(beta)
	vec2 uv = (fragCoord.xy - iResolution.xy*.5) / iResolution.y;

    // Infinity coordinates. interpretation: sin(alpha'), sin(beta')    
    vec2 upvp;

    fragColor = vec4(0.);

    stopDef aperture;
    aperture.center = vec2(0.);
    aperture.z = 1.5; //????
    aperture.radius = .1;


    lensDef l1 = makeLens(
        2.,		// z
        1.5,	// power
        0.,		// powerDelta
        aperture.radius * 4., // radius
        .1,		// thickness
        2.5		// nIndex
        );

	lensDef l2 = makeLens(
        9.5 - 1. * sin(iTime/2.) + .001, // z (focus!)
        0.15,	// power
        -0.15,		// powerDelta
        3.,		// radius
        1.,		// thickness
        2.5		// nIndex
        );


    float rSampleAperture = aperture.radius;

    int nXSamples = 18;
    int nSamples2D = nXSamples * nXSamples;
    for (float r = 0.; r < float(nXSamples); r++) {
        vec2 lastupvp;
	    for (float th = 0.; th <= 2. * r; th++) {
            float xnoise = mod(r * 1871. + th * 1607., 43.) / 43. - .5;
            float ynoise = mod(r * 1061. + th * 1637., 57.) / 57. - .5;
            //vec2 ij = vec2(i, j) + vec2(xnoise, ynoise);
            //vec2 ppxy = (ij - float(nXSamples / 2)) / float(nXSamples) * rSampleAperture * 2.;
            float th0noise = 5.*(mod(float((int(r) ^ 98765) * 1061), 57.) / 57. - .5);
            float theta = 6.28318 * (th + th0noise) / (2. * r + 1.);
            vec2 noisexy = vec2(xnoise, ynoise) / float(nXSamples) * rSampleAperture;
            vec2 ppxy = r / float(nXSamples) * rSampleAperture * vec2(cos(theta), sin(theta)) + noisexy;

            vec3 start = vec3(ppxy, aperture.z);
            vec3 ray = vec3(uv, sqrt(1. - dot(uv, uv)));

            if (clipStop(start, ray, aperture)) {                
                if (traceLens(start, ray, l1, start, ray)) {
	                if (traceLens(start, ray, l2, start, ray)) {

                        vec2 deltaupvp = vec2(0.);
                        if (iMouse.z > 0.) {
                            deltaupvp = (iMouse.xy - iResolution.xy * .5) / iResolution.y;
                        }
                        upvp = ray.xy + deltaupvp;

                        vec4 sampledValue = sampleBackground(upvp);
                        if (th > 0.) {
                            sampledValue = 
                                (sampledValue +
                                 sampleBackground((upvp + lastupvp * 4.) / 5.) +
                                 sampleBackground((upvp * 2. + lastupvp * 3.) / 5.) +
                                 sampleBackground((upvp * 3. + lastupvp * 2.) / 5.) +
                                 sampleBackground((upvp * 4. + lastupvp) / 5.)
                                ) / 5.;
                        }

                        fragColor += sampledValue;

                        lastupvp = upvp;
                    }
                }
            }

        }
    }


    // Output to screen
    fragColor /= float(nSamples2D);
}

