// This shader shows the correct way to compute a number of
// copies of an object (left), and the usual but incorrect
// way to do it (right), which does infinite repetition
// followed by a box that clips the unwanted copies away.

// Related techniques:
//
// Elongation  : https://www.shadertoy.com/view/Ml3fWj
// Rounding    : https://www.shadertoy.com/view/Mt3BDj
// Onion       : https://www.shadertoy.com/view/MlcBDj
// Metric      : https://www.shadertoy.com/view/ltcfDj
// Combination : https://www.shadertoy.com/view/lt3BW2
// Repetition  : https://www.shadertoy.com/view/3syGzz
// Extrusion2D : https://www.shadertoy.com/view/4lyfzw
// Revolution2D: https://www.shadertoy.com/view/4lyfzw
//
// More information here: http://iquilezles.org/www/articles/distfunctions/distfunctions.htm


// Create multiple copies of an object - http://iquilezles.org/www/articles/distfunctions/distfunctions.htm
vec2 opRepLim( in vec2 p, in vec2 s, in vec2 lima, in vec2 limb )
{
    return p-s*clamp(round(p/s),lima,limb);
}

mat2 r2(in float a){ float c = cos(a), s = sin(a); return mat2(c, s, -s, c); }

vec2 opPolarRepetition(in vec2 p, in float numrep, in float NumS, in float NumEnd)
{
    // Polar angle.
    float a = atan(p.y, p.x);

    float aNum = numrep;

    // Partitioning the angle into "aNum" cells.
    float ia = floor(a/6.2831853*aNum);
    ia = (ia + .5)/aNum*6.2831853;
   // if(abs(ia)<3.14156/(6.0/2.0))//need to fix this for constraining start and end angles
    { // Converting the radial centers to their positions.

       if(cos(iTime*0.25)<0.0)
       {
           //use this to keep the shape from deforming
           p *= r2(ia);
    	  // Above is equivalent to:
    	  //p = vec2(p.x*cos(ia) + p.y*sin(ia), p.y*cos(ia) - p.x*sin(ia));
       }
       else
       {
           //use this to deform the shapes
    	   p = vec2(length(p), mod(a, 6.2831/aNum) - 6.2831/aNum/2.);
       }

    // Setting the radial distance.
    // Moving the points out a bit along the radial line. If you didn't perform this,
    // all objects would be superimposed on one another in the center.
    //p.x -= 3.5;

    	p.x = p.x-1.0*clamp(round(p.x/1.0),NumS,NumEnd); // correct radial repetition instead of mod
    }
    return p;
}

// Create infinite copies of an object -  http://iquilezles.org/www/articles/distfunctions/distfunctions.htm
vec2 opRep( in vec2 p, in float s )
{
    return mod(p+s*0.5,s)-s*0.5;
}

// http://iquilezles.org/www/articles/distfunctions/distfunctions.htm
float opIntersection( float d1, float d2 )
{
    return max(d1,d2);
}

// http://iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdBox( in vec2 p, in vec2 b ) 
{
    vec2 q = abs(p) - b;
    return min(max(q.x,q.y),0.0) + length(max(q,0.0));
}

