void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    // sdf
    float d1;
    float d2;
    if( p.x<0.0 ) 
    {
    	vec2 q = p*6.0 + vec2(5.25,0.0);
        vec2 r1 = opPolarRepetition(q,1.0+8.0*abs(cos(iTime*0.25)),2.0,3.0);
        //8 repetitions in 360 degrees, along radius start at 2 and end at 3

        //d1 = length(r1); // for circular primitive
        //trying to find the right width to only show deformed white rectangle (not more not less)
        d1 = sdBox( r1, vec2(0.1,0.3))-0.1;//2.5/(1.0+8.0*abs(cos(iTime*0.25)))) ) -  0.1;// for box primitive;

        d1 = max( -d1,length(q)-3.70);//showing inside of a circle instead of outside
    }
    else      
    {
        vec2 q = p*6.0 - vec2(5.25,0.0);
        vec2 r1 = opPolarRepetition(q,1.0+6.0*abs(cos(iTime*0.25)),0.0,2.0);;//time varying repetitions
        //6 repetitions in 360 degrees, along radius start at 0 and end at 2

        d2 = length(r1)-0.5;//for circular primitive
     }

	float d = d1+d2;

    // colorize
    vec3 col = vec3(1.0) - sign(d)*vec3(0.1,0.4,0.7);
	col *= 1.0 - exp(-2.0*abs(d));
	col *= 0.8 + 0.2*cos(40.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.05,abs(d)) );

    col *= smoothstep(0.005,0.010,abs(p.x));

	fragColor = vec4(col,1.0);
}

  vec2 getScreenSpace() {
    vec2 uv = (gl_FragCoord.xy - 0.5 * iResolution.xy) / min(iResolution.y, iResolution.x);

    return uv;
  }

vec3 permute(vec3 x) { return mod(((x*34.0)+1.0)*x, 289.0); }

float snoise(vec2 v){
  const vec4 C = vec4(0.211324865405187, 0.366025403784439,
           -0.577350269189626, 0.024390243902439);
  vec2 i  = floor(v + dot(v, C.yy) );
  vec2 x0 = v -   i + dot(i, C.xx);
  vec2 i1;
  i1 = (x0.x > x0.y) ? vec2(1.0, 0.0) : vec2(0.0, 1.0);
  vec4 x12 = x0.xyxy + C.xxzz;
  x12.xy -= i1;
  i = mod(i, 289.0);
  vec3 p = permute( permute( i.y + vec3(0.0, i1.y, 1.0 ))
  + i.x + vec3(0.0, i1.x, 1.0 ));
  vec3 m = max(0.5 - vec3(dot(x0,x0), dot(x12.xy,x12.xy),
    dot(x12.zw,x12.zw)), 0.0);
  m = m*m ;
  m = m*m ;
  vec3 x = 2.0 * fract(p * C.www) - 1.0;
  vec3 h = abs(x) - 0.5;
  vec3 ox = floor(x + 0.5);
  vec3 a0 = x - ox;
  m *= 1.79284291400159 - 0.85373472095314 * ( a0*a0 + h*h );
  vec3 g;
  g.x  = a0.x  * x0.x  + h.x  * x0.y;
  g.yz = a0.yz * x12.xz + h.yz * x12.yw;
  return 130.0 * dot(m, g);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = getScreenSpace();

    float w = 30./iResolution.y;

    uv *= 30.;
    vec2 grid = floor(uv);
    vec2 subuv = fract(uv);

    vec3 colour = vec3(0);
    float d = 0.;

    for(int x = -1; x < 2; x++) {

      for(int y = -1; y < 2; y++) {
        vec2 os = vec2(x,y);
        vec2 _grid = grid + os;
        vec2 _subuv = subuv - os;

        float r = snoise(_grid*.1 + iTime*.5)*.2+.3;
          d = r;
        colour += vec3(smoothstep( r+w, r-w, length(_subuv-.5) ));
      }

    }


    fragColor = vec4(colour, mix(0., d, colour.x));
}

