void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    // Calculate the to center distance
    float d = length(uv - 0.5) * 2.0;

    // Calculate the ripple time
    float t = d * d * d * 35.0 - iTime * 13.0;


    // Calculate the ripple thickness
    d = (sin(t * 0.1) * cos(t * 2.1) * 0.5 + 0.5) * (1.0 - d);

    // Time varying pixel color
    vec3 col = 0.5 + 0.5 * cos(t / 20.0 + uv.xyx + vec3(0.0,2.0,4.0));
	col.rgb = vec3(pow(col.r, 20.0) + 0.2);
    // Set the output color to rgb channels and the thickness to alpha channel
    // AO is automatically calculated
    fragColor = vec4(col, d);
}

// Cubic interpolation
float cubemix(float a, float b, float t) {
    float c = t*t*(3.-2.*t);
    return mix(a, b, c);
}

float rand( vec2 p ) {
	vec2 f = fract(p);
	p = floor(p);
	float v = p.x+p.y*1000.0;
	vec4 r = vec4(v, v+1.0, v+1000.0, v+1001.0);
	r = fract(10000.0*sin(r*.001));
	f = f*f*(3.0-2.0*f);
	return 2.0*(mix(mix(r.x, r.y, f.x), mix(r.z, r.w, f.x), f.y))-1.0;
}

float valuenoise(vec2 uv) {
    vec2 iuv = floor(uv);
    vec2 offset = vec2(0.,1.);
    float v00 = rand(iuv);
    float v01 = rand(iuv+offset.xy);
    float v10 = rand(iuv+offset.yx);
    float v11 = rand(iuv+offset.yy);
    vec2 disp = fract(uv);
    float v0 = cubemix(v00, v01, disp.y);
    float v1 = cubemix(v10, v11, disp.y);
    return cubemix(v0, v1, disp.x)*.5+.5;
}

float fractalnoise(vec2 uv, float mag) {
    float d = valuenoise(uv);
    int i;
    float fac = 1.;
    vec2 disp = vec2(0., 1.);
    for (i=0; i<3; i++) {
        uv -= mag * iTime * disp * fac;
        fac *= 0.5;
        d += valuenoise(uv/fac)*fac;
    }
    return d;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.x*20.;
	uv.y -= iTime*10.;
    float val = fractalnoise(uv, 5.);
    vec3 col = mix(vec3(1., 0.2, 0.), vec3(1., 1., 0.5), smoothstep(0.5, 1., val));
    float phase1 = iTime * 20.5;
    float offset = smoothstep(-0.5, 0.5, sin(iTime*20.))*40.;
    float offset2 = sin(iTime*15.25) * 40.;
	float left = iResolution.x*(0.5 + 0.1*(sin(phase1) - 1.)) + offset + offset2;
    float right = iResolution.x*(0.5 - 0.1*(cos(phase1) - 1.)) + offset + offset2;
    float bottom = iResolution.y*0.2 + offset * 0.5;
    float top = iResolution.y*0.55 + offset*0.5;
    if (fragCoord.x < left || fragCoord.x > right || (fragCoord.y < top && fragCoord.y > bottom)) {
    	col = vec3(0.);   
    }
    fragColor = vec4(col,1.0);
}

#define PI 3.14159265359
mat3 rotX(float t) {
    return mat3(
        1.0, 0.0, 0.0,
        0.0, cos(t), sin(t),
        0.0, - sin(t), cos(t)
    );
}
mat3 rotY(float t) {
    return mat3(
        cos(t), 0.0, sin(t),
        0.0, 1.0, 0.0,
        - sin(t), 0.0, cos(t)
    );
}
mat3 rotZ(float t) {
    return mat3(
        cos(t), sin(t), 0.0,
        - sin(t), cos(t), 0.0,
        0.0, 0.0, 1.0
    );
}
float sphereSDF(vec3 p, vec3 cent, float rad) {
    return distance(p, cent) - rad;
}
float sceneSDF(vec3 p){
    float t = 0.5* iTime;
    p *= rotY(t) * rotX(t) * rotZ(t);
    float dist = 1.0;
    for (float i = 0.0; i < 6.0; i++){
        vec3 cent = vec3(cos(2.0 * PI * i / 6.0), sin(2.0 * PI * i / 6.0), 0.0);
        dist = min(dist, sphereSDF(p, cent, 0.2));
    }
    return mix(dist, sphereSDF(p, vec3(0.0), 1.), abs(mod(t, 2.0) - 1.0));
}
vec3 gradSDF(vec3 p) {
    float d = 0.0001;
    return normalize(vec3(
        sceneSDF(p + vec3(d, 0.0, 0.0)) - sceneSDF(p + vec3(-d, 0.0, 0.0)),
        sceneSDF(p + vec3(0.0, d, 0.0)) - sceneSDF(p + vec3(0.0, - d, 0.0)),
        sceneSDF(p + vec3(0.0, 0.0, d)) - sceneSDF(p + vec3(0.0, 0.0, - d))
    ));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord) {
    // fragment position
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);

    // camera
    vec3 cPos = vec3(0.0, 0.0, 2.0);
    vec3 cDir = vec3(0.0, 0.0, - 1.0);
    vec3 cUp = vec3(0.0, 1.0, 0.0);
    vec3 cSide = cross(cDir, cUp);
    float targetDepth = 1.0;

    // light
    vec3 lightDir = vec3(0.0, 0.0, 1.0);
    // lightDir *= rotY(u_time);

    // ray
    vec3 ray = normalize(cSide * p.x + cUp * p.y + cDir * targetDepth);

    // marching loop
    vec3 rPos = cPos;
    for(int i = 0; i < 40; i++ ) {
        rPos += ray * sceneSDF(rPos);
    }

    // collision detection
    if (abs(sceneSDF(rPos)) < 0.001) {
        vec3 normal = gradSDF(rPos);
        float diff = clamp(dot(lightDir, gradSDF(rPos)), 0.1, 1.0);
        fragColor = vec4(vec3(diff), 1.0);
    } else {
        fragColor = vec4(vec3(0.0), 1.0);
    }
}

#define S(a,b,t) smoothstep(a,b,t)


float N21 (vec2 p){
	float d = fract(sin(p.x*110.+(8.21-p.y)*331.)*1218.);
    return d;
}

float Noise2D(vec2 uv){
    vec2 st = fract(uv);
    vec2 id = floor(uv);
    st = st*st*(3.0-2.0*st);
    float c=mix(mix(N21(id),N21(id+vec2(1.0,0.0)),st.x),mix(N21(id+vec2(0.0,1.0)),N21(id+vec2(1.0,1.0)),st.x),st.y);
	return c;
}

float fbm (vec2 uv){

    float c=0.;
	c+=Noise2D(uv)/2.;
    c+=Noise2D(2.*uv)/4.;
    c+=Noise2D(4.*uv)/8.;
    c+=Noise2D(8.*uv)/16.;
    return c/(1.-1./16.);
}

vec3 fbm3(vec2 uv){
    vec3 color;
	float f1 = fbm(uv);
    color= mix(vec3(0.1,0.0,0.0),vec3(0.9,0.1,0.1),2.5*f1);

    float f2 = fbm(2.4*f1+uv+0.15*sin(iTime)*vec2(7.0,-8.0));
    color= mix(color,vec3(0.6,0.5,0.1),1.5*f2);
    float f3 = fbm(3.5*f2+uv-0.15*cos(1.5*iTime)*vec2(4.0,3.0));
    color= mix(color,vec3(0.1,0.35,0.45),f3);

    color= mix(color,vec3(0.45,0.35,0.25),S(0.7,0.75,f2));
    color= mix(color,vec3(0.2,0.4,0.2),S(0.75,0.8,f2));
    color= mix(color,vec3(0.55,0.55,0.35),S(0.88,0.99,f2));
    color= mix(color,vec3(0.55,0.55,0.35),S(0.88,0.99,f3));

    return color;

}

