    float fieldDir = atan(mField.y, mField.x);
    //fragColor = vec4(log(fieldMag * 5.2) * 0.2, fieldDir / (2.0 * PI) + 0.5, 0.0, 1.0);
    vec2 mField2 = magneticField(points, uv + vec2(0.005));
    vec2 mDeriv = mField - mField2;

    float logmag2 = log(distance(vec2(0), mField2)) * 5.0;

    //fragColor = vec4(hsv2rgb(vec3(fieldDir / (PI * 2.0), 1.0, log(fieldMag * 20.0) * 0.1)), 1.0);
    float logmag = log(fieldMag) * 5.0;
    //float threshold = pow(distance(vec2(0.0), mDeriv), 0.33);
    //float col = (mod(5.0 * logmag, 2.0) > threshold) ? 0.0 : 1.0;
    float col = (abs(mod(logmag, 2.0) - mod(logmag2, 2.0)) > 1.0) ? 1.0 : 0.0;
    fragColor = vec4(hsv2rgb(vec3(fieldDir / (PI * 2.0), 1.0, col)), 1.0);
}

vec2 f(vec2 x, vec2 c) {
    return mat2(x,-x.y,x.x)*x + c;
}

vec3 palette(float t, vec3 a, vec3 b, vec3 c, vec3 d) {
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;uv *= 1.3;uv += 0.5;
    vec4 col = vec4(1.0);
    float time = iTime;

    int u_maxIterations = 75;

    float r = 0.7885*(sin((time/3.) - 1.57)*0.2+0.85);
	vec2 c = vec2(r*cos((time/3.)), r*sin((time/3.)));

    vec2 z = vec2(0.);
    z.x = 3.0 * (uv.x - 0.5);
    z.y = 2.0 * (uv.y - 0.5);
    bool escaped = false;
    int iterations;
    for (int i = 0; i < 10000; i++) {
        if (i > u_maxIterations) break;
        iterations = i;
        z = f(z, c);
        if (dot(z,z) > 4.0) {
            escaped = true;
            break;
        }
    }

    vec3 iterationCol = vec3(palette(float(iterations)/ float(u_maxIterations),
                                     vec3(0.5),
                                     vec3(0.5),
                                     vec3(1.0, 1.0, 0.0),
                                     vec3(0.3 + 0.3 * sin(time),
                                          0.2 + 0.2 * sin(1. + time),
                                          0.2  + 0.2 * sin(1.5 + time))));

	vec3 coreCol = vec3(0.);

    float f_ite = float(iterations);
    float f_maxIte = float(u_maxIterations);
    fragColor = vec4(escaped ? iterationCol : coreCol, f_ite/f_maxIte );
}

