    uv = RotZ(vec3(uv,0.), 0.4*sin(time*0.368)).xy;

    vec3 col = vec3(0);

    vec3 lookat = cameralookat();

    float zoom = 0.3;//0.5 + 0.3*sin(time*0.326);
    vec3 ro = camerapos();

    vec3 F = normalize(lookat-ro); // Forward
    vec3 R = normalize(cross(vec3(0., 1., 0.), F)); //Right
    vec3 U = cross(F, R); //Up

    vec3 C = ro + F*zoom;
    vec3 I = C + uv.x*R + uv.y*U;
    vec3 rd = normalize(I-ro);

    // a.x will determine if the pixel will get the torus with the holes
    // a.y will determine the color 
    vec2 a = vec2(0.,0.);
    vec2 b = (iMouse.xy-.5*iResolution.xy)/iResolution.y;
    if(uv_temp.x > b.x) {a.x = 1.;}
    if(uv_temp.y > b.y) {a.y = 1.;}

    vec3 d = RayMarch(ro,rd, MaxSteps, a.x);
    vec3 p = ro + rd*d.x;

    ret = GetCol(ro, rd, p, d, a);

    //ret = dif;
    //ret = Hive(uv*5.+10.);

    float mouseClicked = iMouse.w > 0. ? 0.12 : 0.;
    vec3 vline = exp(-80.*length(uv_temp.x-b.x))*vec3(1.,1.,1.); // vertical line
    vec3 hline = exp(-80.*length(uv_temp.y-b.y))*vec3(1.,1.,1.); // horizontal line
    vec3 lineCol = texture(iChannel1,uv_temp*0.05
                           + mouseClicked*vec2(time*0.5,time)
                           + vec2(time*0.01, time*0.02)).xyz;
    lineCol = (lineCol)*3.*vec3(0.8,1.,0.4);
    ret *= 1.-vline;
    ret += vline*lineCol;
    ret *= 1.-hline;
    ret += hline*lineCol;


    //ret = line;
    //ret = lineCol;

    fragColor = vec4(ret,1.0);
}

