vec2 RayMarch(vec3 ro, vec3 rd) {
vec2 h, t=vec2( 0.);  
for (int i=0; i<64; i++) 
 {   
  h = GetDist(ro + t.x * rd);   
  if(h.x<0.001||abs(t.x)>70.) break;
  t.x+=h.x *.6;
  t.y=h.y;
 }
 if(t.x>70.) 
 t.x=.0;
return t;
}


vec3 GetNormal(vec3 p){
 vec2 d = GetDist(p);
 vec2 e = vec2(0.001,0);
 vec3 n = d.x - vec3(
 GetDist(p-e.xyy).x,
 GetDist(p-e.yxy).x,
 GetDist(p-e.yyx).x);
 return normalize(n);
}

float GetLight(vec3 p) {
 vec3 lightPos = vec3(sin(iTime),cos(iTime),0.);
 vec3 l = normalize(lightPos-p);
 vec3 n = GetNormal(p);
 float dif = clamp(dot(n, l)*.5+.5, 0., 1.);
 vec2 d = RayMarch(p+n*.001*1., l);
 if (d.x<length(lightPos-p)) dif *= .01;
 return dif;       
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
 vec3 f = normalize(l-p),
 r = normalize(cross(vec3(0,1,0), f)),
 u = cross(f,r),
 c = p+f*z,
 i = c + uv.x*r + uv.y*u,
 d = normalize(i-p);
 return d;
}

