//
// Knotted Hyper/Epi-Trochoid
//

#define M_PI 3.14159

float AA = 2.0;
float SCALE_TIME = 1.5;
vec2  UV_CENTER = vec2(0.0, 0.0);
float UV_HEIGHT = 2.2;

float CHECKER_SCALE = 1.0;
vec3  CHECKER_COLOR0 = vec3(0.1);
vec3  CHECKER_COLOR1 = vec3(0.2);

const float kEpi = 0.0;
const float kHyper = 1.0;

const float EPI_OR_HYPER = kEpi;
float NUM_SYMMETRY = 4.0;
const float NUM_POINTS = 128.0 * (EPI_OR_HYPER == kEpi ? 2.0 : 1.0);
float POINT_RADIUS = M_PI / NUM_POINTS * (EPI_OR_HYPER == kEpi ? 2.0 : 0.9);
vec4  TROCHOID_COLOR = vec4(0.8, 1.0, 1.0, 0.9);

//
// Re-parameterize hyper/epi-trochoid for easier visual control
//
//   "num_symmetry"
//     n = r1 / r2 = 1 / r2 \in N
//
//   "knotness" (knot (a > 1), intersecton (1 > a > 0), no-intersection (0 > a))
//     r3 = (1 - a) r2 + a * (r1 - r2)  (for hyper-trochoid)
//     r3 = (1 - a) r2 + a * (r1 + r2)  (for epi-trochoid)
//
// TODO:
// - reduce loop by taking symmetry into account
// - for higher n, there is different kind of qualitative change (e.g. intersection with neighbor).
//
float signedDistanceToKnottedTrochoidPoints(
    vec2 p, float trochoid_type, float knotness, float num_symmetry,
    float num_points, float point_radius, float knot_offset) {

  float r1 = 1.0;
  float r2 = 1.0 / num_symmetry;
  float r3, size; {
    if (trochoid_type == kHyper) {
      r3 = (1.0 - knotness) * r2 + knotness * (r1 - r2);
      size = r1 - r2 + r3;
    }
    if (trochoid_type == kEpi) {
      r3 = (1.0 - knotness) * r2 + knotness * (r1 + r2);
      size = r1 + r2 + r3;
    }
  }
  r1 /= size; r2 /= size; r3 /= size;

  float min_distance = 1000.0;
  for (float i = 0.0; i < num_points; i++) {
    float t = 2.0 * M_PI * i / num_points;
    vec3 q; {
      if (trochoid_type == kHyper) {
        q = vec3(
          // usual hyper-trochoid formula
          (r1 - r2) * cos(t) + r3 * cos((1.0 - num_symmetry) * t),
          (r1 - r2) * sin(t) + r3 * sin((1.0 - num_symmetry) * t),
          // knot by waving z coord along symmetry
          sin(num_symmetry * (t + knot_offset)));
      }
      if (trochoid_type == kEpi) {
        q = vec3(
          // usual epi-trochoid formula
          (r1 + r2) * cos(t) + r3 * cos((1.0 + num_symmetry) * t - M_PI),
          (r1 + r2) * sin(t) + r3 * sin((1.0 + num_symmetry) * t - M_PI),
          // knot by waving z coord along symmetry
          sin(num_symmetry * (t + knot_offset)));
      }
    }
    float radius = point_radius * mix(0.6, 1.0, q.z);
    float sd = distance(p, q.xy) - radius;
    if (sd < min_distance) {
      min_distance = sd;
    }
  }
  return min_distance;
}


//
// Misc
//

float signedDistanceToChecker(vec2 uv, float scale) {
  uv *= scale;
  vec2 uvi = floor(uv);
  vec2 uvf = uv - uvi;
  float dist = min(min(min(uvf.x, uvf.y), 1.0 - uvf.x), 1.0 - uvf.y);
  dist /= scale;
  bool is_even_spot = mod(uvi.x + uvi.y, 2.0) == 0.0;
  return is_even_spot ? -dist : dist;
}

mat3 invViewTransform(vec2 center, float height) {
  vec2 Res = iResolution.xy;
  vec2 size = vec2(height * Res.x / Res.y, height);
  vec2 a = center - size / 2.0;
  float Sy = height / Res.y;
  mat3 xform = mat3(
       Sy,   0,   0,
        0,  Sy,   0,
      a.x, a.y, 1.0);
  return xform;
}

float smoothBoundaryCoverage(float signed_distance, float boundary_width) {
  return 1.0 - smoothstep(0.0, 1.0, signed_distance / boundary_width + 0.5);
}


//
// Main
//

void mainImage(out vec4 frag_color, vec2 frag_coord) {
  mat3 inv_view_xform = invViewTransform(UV_CENTER, UV_HEIGHT);
  float inv_view_scale = inv_view_xform[0][0];
  vec2 uv =  vec2(inv_view_xform * vec3(frag_coord, 1.0));
  float t = SCALE_TIME * iTime;

  vec3 color;
  {
    // Checker
    float sd = signedDistanceToChecker(uv, CHECKER_SCALE);
    float coverage = smoothBoundaryCoverage(sd / inv_view_scale, AA);
    color = mix(CHECKER_COLOR0, CHECKER_COLOR1, coverage);
  }
  {
    // Hyper/Epi-Trochoid
    float knotness = 3.0 * (0.5 + 0.5 * sin(t));
    float knot_offset = 0.0; // not used now
    float sd = signedDistanceToKnottedTrochoidPoints(
        uv, EPI_OR_HYPER, knotness, NUM_SYMMETRY, NUM_POINTS, POINT_RADIUS, knot_offset);
    float coverage = smoothBoundaryCoverage(sd / inv_view_scale, AA);
    color = mix(color, TROCHOID_COLOR.xyz, TROCHOID_COLOR.w * coverage);
  }
  frag_color = vec4(color, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 UV = fragCoord.xy / iResolution.xy;

   	float XRed = pow(abs((-UV.x + 0.5) * abs(sin(iTime))), 0.25);
    float YRed = pow(abs((-UV.y + 0.5) * abs(sin(iTime))), 0.25);

    float XRedB = pow(abs((-UV.x + 0.25) * abs(sin(iTime))), 0.25);
    float YRedB = pow(abs((-UV.y + 0.25) * abs(sin(iTime))), 0.25);

    float XRedC = pow(abs((-UV.x + 0.75) * abs(sin(iTime))), 0.25);
    float YRedC = pow(abs((-UV.y + 0.75) * abs(sin(iTime))), 0.25);

    float XBlue = pow(abs((-UV.x + 0.5) * abs(sin(iTime * 2.0))), 0.25);
    float YBlue = pow(abs((-UV.y + 0.5) * abs(sin(iTime * 2.0))), 0.25);

    float XBlueB = pow(abs((-UV.x + 0.25) * abs(sin(iTime * 2.0))), 0.25);
    float YBlueB = pow(abs((-UV.y + 0.25) * abs(sin(iTime * 2.0))), 0.25);

    float XBlueC = pow(abs((-UV.x + 0.75) * abs(sin(iTime * 2.0))), 0.25);
    float YBlueC = pow(abs((-UV.y + 0.75) * abs(sin(iTime * 2.0))), 0.25);

    float XGreen = pow(abs((-UV.x + 0.5) * abs(sin(iTime * 4.0))), 0.25);
    float YGreen = pow(abs((-UV.y + 0.5) * abs(sin(iTime * 4.0))), 0.25);

    float XGreenB = pow(abs((-UV.x + 0.25) * abs(sin(iTime * 4.0))), 0.25);
    float YGreenB = pow(abs((-UV.y + 0.25) * abs(sin(iTime * 4.0))), 0.25);

    float XGreenC = pow(abs((-UV.x + 0.75) * abs(sin(iTime * 4.0))), 0.25);
    float YGreenC = pow(abs((-UV.y + 0.75) * abs(sin(iTime * 4.0))), 0.25);

    float FinalRedX = XRed * XRedB * XRedC;
    float FinalRedY = YRed * YRedB * YRedC;

    float FinalGreenX = XGreen * XGreenB * XGreenC;
    float FinalGreenY = YGreen * YGreenB * YGreenC;

    float FinalBlueX = XBlue * XBlueB * XBlueC;
    float FinalBlueY = YBlue * YBlueB * YBlueC;

    float FinalRed = FinalRedX * FinalRedY;
    float FinalGreen = FinalGreenX * FinalGreenY;
    float FinalBlue = FinalBlueX * FinalBlueY;

    float HorizontalOverlay = abs(sin(((UV.x * 3.141592) * 400.0)));
    float VerticalOverlay = abs(sin(((UV.y * 3.141592) * 225.0)));

    vec4 FirstPass = vec4(1.0 - FinalRed, 1.0 - FinalGreen, 1.0 - FinalBlue, 1.0);
    vec4 SecondPass = vec4(HorizontalOverlay, HorizontalOverlay, HorizontalOverlay, 1.0);
    vec4 ThirdPass = vec4(VerticalOverlay, VerticalOverlay, VerticalOverlay, 1.0);

    fragColor = FirstPass * SecondPass * ThirdPass;

}

#define colorWidth        0.1  // Ranges from 0.0 to 1.0
#define colorCenter       0.7  // Ranges from 0.0 to 1.0
#define repetition        5.0  // Ranges from 1.0 to infinity (starts to look ridiculous past 20)
#define colorShiftSpeed   10.0 // Frequency at which colors "move"
#define direction         1.0  // Positive makes colors shift away from you, negative towards you

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = -1.0+2.0*fragCoord/iResolution.y;

    float an = iTime*0.1;

    p = mat2(cos(an),-sin(an),sin(an),cos(an)) * p;

    vec2 uv = vec2(p.x,1.0)/abs(p.y) + iTime;

	fragColor = vec4( texture(iChannel0,uv).xyz*abs(p.y), 1.0);

    float average = (fragColor.r + fragColor.g + fragColor.b) / 3.0;
    average *= repetition * direction;
	float red = sin(iTime * colorShiftSpeed + average + 2.0) * colorWidth + colorCenter;
	float green = sin(iTime * colorShiftSpeed + average + 0.0) * colorWidth + colorCenter;
	float blue = sin(iTime * colorShiftSpeed + average + 4.0) * colorWidth + colorCenter;
	fragColor = vec4(red, green, blue, 1.0);
}

#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST 0.01

mat2 GetRot(float alpha)
{
    float c = cos(alpha);
    float s = sin(alpha);

    return mat2(c, -s, s, c);
}

float smoothMin(float p1, float p2, float k)
{
    float h = clamp(0.5 + 0.5 * (p2 - p1) / k,0., 1.);
    return mix(p2, p1, h) - k * h * (1.0-h);
}

float BoxDist(vec3 p, vec3 s)
{
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

float GetDist(vec3 p)
{
    float t = iTime * .5;

    vec4 s1 = vec4(cos(t)*0.1 + 1.1, 1, 6, 1);
    vec4 s2 = vec4(-cos(t)*0.1 - 1.1, 1, 6, 1);
    vec4 s3 = vec4(0, 1. + cos(t) + 0.1, 5.2 , 0.4);

    vec3 boxPos = p - vec3(0,1,6);
    boxPos.xz *= GetRot(t);
    boxPos.xy *= GetRot(-t);

    float sd1 = length(p - s1.xyz) - s1.w;
    float sd2 = length(p - s2.xyz) - s2.w; 
    float sd3 = BoxDist(boxPos, vec3(.5)); 
    float pd = p.y;

    float s21Min = smoothMin(sd1, sd2, .15);
    float s21_3Min = smoothMin(sd3, s21Min, .3);

    return min(pd, s21_3Min);
}

vec3 GetNormal(vec3 p)
{
    float d = GetDist(p);
    vec2 e = vec2(0.01, .0);

    vec3 n = d - vec3(
    	GetDist(p - e.xyy),
    	GetDist(p - e.yxy),
    	GetDist(p - e.yyx));

    return normalize(n);
}

float RayMarch(vec3 ro, vec3 rd)
{
    float d0 = 0.;
        for (int i = 0; i < MAX_STEPS; i++)
        {
            vec3 p = ro + rd * d0;
            float ds = abs(GetDist(p));
            d0 += ds;
            if (d0 > MAX_DIST || ds < SURF_DIST)
            {
                break;
            }
        }

    return d0;
}

float GetLight(vec3 p)
{
    vec3 lightPos = vec3(2, 5, 1);

    vec3 l = normalize(lightPos - p);
    vec3 n = GetNormal(p);

    float diff = clamp(dot(n, l)*.5+.5, 0., 1.);
    float distToLight = RayMarch(p + n * SURF_DIST * 2., l);
    if (p.y<.01 && distToLight < length(p - lightPos))
    {
        diff *= 0.4;
    }

    return diff;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy) / iResolution.y;

    vec3 ro = vec3(0, 2, 0);
    vec3 rd = normalize (vec3(uv.x, uv.y - 0.2, 1));

    float d = RayMarch(ro, rd);
    vec3 p = ro + rd * d;
    float dif = GetLight(p);
    vec3 col = vec3(0.8, 0.7, 0.9);

    fragColor = vec4(vec3(dif),1.0);
}

