float saw(float x)
{
    float f = mod(floor(abs(x)), 2.0);
    float m = mod(abs(x), 1.0);
    return f*(1.0-m)+(1.0-f)*m;
}

vec2 saw(vec2 x) { return vec2(saw(x.x), saw(x.y)); }
vec3 saw(vec3 x) { return vec3(saw(x.x), saw(x.y), saw(x.z)); }
vec4 saw(vec4 x) { return vec4(saw(x.x), saw(x.y), saw(x.z), saw(x.w)); }


mat2 rotate(float x) { return mat2(cos(x), sin(x), sin(x), -cos(x)); }

float cross2d( in vec2 a, in vec2 b ) { return a.x*b.y - a.y*b.x; }

vec2 invBilinear( in vec2 p, in vec2 a, in vec2 b, in vec2 c, in vec2 d )
{
    vec2 res = vec2(-1.0);

    vec2 e = b-a;
    vec2 f = d-a;
    vec2 g = a-b+c-d;
    vec2 h = p-a;

    float k2 = cross2d( g, f );
    float k1 = cross2d( e, f ) + cross2d( h, g );
    float k0 = cross2d( h, e );

    // if edges are parallel, this is a linear equation. Do not this test here though, do
    // it in the user code
    if( abs(k2)<0.001 )
    {
        float v = -k0/k1;
        float u  = (h.x*k1+f.x*k0) / (e.x*k1-g.x*k0);
        //if( v>0.0 && v<1.0 && u>0.0 && u<1.0 ) 
            res = vec2( u, v );
    }
	else
    {
        // otherwise, it's a quadratic
        float w = k1*k1 - 4.0*k0*k2;
        //if( w<0.0 ) return vec2(-1.0);
        w = sqrt( w );

        float ik2 = 0.5/k2;
        float v = (-k1 - w)*ik2;// if( v<0.0 || v>1.0 ) v = (-k1 + w)*ik2;
        float u = (h.x - f.x*v)/(e.x + g.x*v);
        //if( u<0.0 || u>1.0 || v<0.0 || v>1.0 ) return vec2(-1.0);
        res = vec2( u, v );
    }
    return (res);
}

float draw(vec2 uv)
{
    return (1.-smoothstep(0., .1, abs(uv.x-.5)))*(1.-smoothstep(0.9, 1., abs(uv.y)));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (fragCoord.xy)/iResolution.xy;//*2.-1.;
    p.x = p.x*2.-1.;
    p.x *= iResolution.x/iResolution.y;
	p.x = p.x *.5+.5;

    vec3 col = vec3(0.);

    const float max_iterations = 16.;

    float map = 0.;
    map += draw(p);
    for(float f = 0.; f < max_iterations; f+=1.){
        float iteration = (f/max_iterations+1.);
        float angle = (time*iteration)/PI-1.*PI/4.;//floor(((angle/PI)*.5+.5)*rots);

        vec2 a = vec2(1., 1.);
        vec2 b = vec2(0., 1.);
        vec2 c = vec2(0., 0.);
        vec2 d = vec2(1., 0.);

        p = p*2.-1.;

        vec2 s = vec2(.75);
        vec2 o = vec2(.5, 0.);
        mat2 m = rotate(angle);

		a = a*2.-1.; b = b*2.-1.; c = c*2.-1.; d = d*2.-1.;

        a = a*m; b = b*m; c = c*m; d = d*m;
        a *= s; b *= s; c *= s; d *= s;
        a += o; b += o; c += o; d += o;
        //= a*.5+.5; b = b*.5+.5; c = c*.5+.5; d = d*.5+.5;


        /*
		a = a*2.-1.; b = b*2.-1.; c = c*2.-1.; d = d*2.-1.;
		*/

        vec2 a2 = a*vec2(-1., 1.);
        vec2 b2 = b*vec2(-1., 1.);
        vec2 c2 = c*vec2(-1., 1.);
        vec2 d2 = d*vec2(-1., 1.);
        if(p.x > 0.)
        	p = (invBilinear( p, a, b, c, d ));
        else
        	p = (invBilinear( p, a2, b2, c2, d2 ));



        //map += 1.-smoothstep((f)/max_iterations, (f+1.)/max_iterations, (abs(p.x+p.y)));
        map += draw(p)*iteration;//1.-smoothstep((f)/max_iterations, (f+1.)/max_iterations, (abs(p.y-.5)));
    }

    fragColor = vec4(flux(map*PI)*clamp(map, 0., 1.), 1.0 );
}

const float CIRCLE_RADIUS = 0.1;
const float CIRCLE_SPEED = 0.5;
const float CIRCLE_Y = 0.5;

/*
Circle moves at a constant speed from the left side of
the screen to the right side, changing direction
when it hits one side.

Position normalized to [0,1]
*/
vec2 circlePosition(float time){

    float dist = time * CIRCLE_SPEED;

    float distInt = floor(dist);

    bool isRight = int(distInt) % 2 == 0;

    float circleX = isRight? (dist - distInt) : 1.0 - (dist - distInt);

    return vec2(circleX, CIRCLE_Y);
}

bool isInCircle(vec2 coord, vec2 circleCoord, float circleR){
    return distance(circleCoord, coord) < circleR;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec2 circleCoord = circlePosition(iTime);
    if(isInCircle(uv, circleCoord, CIRCLE_RADIUS)){
        col = vec3(0.0,0.0,0.0);
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

