float fbm(vec3 x) {
	float v = 0.0;
	float a = 0.5;
	vec3 shift = vec3(100);
	for (int i = 0; i < 5; ++i) {
		v += a * noise(x);
		x = x * 2.0 + shift;
		a *= 0.5;
	}
	return v;
}


float sdSphere(vec3 pos, float rad)
{
 	return length(pos) - rad;
}

Object map(vec3 pos)
{
    Object o;
    o.difVal = 1.0;

    o.dist = 1000.0;
    o.normEps = 0.001;
    o.color = vec3(0);

    //float ang = PI/2.0;
    float fracAng = iTime/12.0;
    vec3 objPos = pos - vec3(0.0, -0.0, 0.0); 

     objPos.xz = mat2(cos(fracAng), sin(fracAng), -sin(fracAng), cos(fracAng)) * objPos.xz;
    float dSphere = sdSphere(objPos, 5.0);

    if(dSphere < o.dist){
        o.dist = dSphere;
        o.color = mix(vec3(0, 0.2, 0.9)*fbm(objPos/2.0), vec3(.1, .3, .15)*fbm(objPos*1.5), step(0.5, fbm(objPos*1.5)));
        o.specVal = 8.0;
        o.specKs = 0.01;
    }

    float size = 1.0;
    //float angFrac = PI/6.0;
    vec3 posFracOff = vec3(9, 0.0, 10.);

    vec3 posFrac = objPos - posFracOff;
    float dDist;
    float minDist;


    dDist = sdSphere(posFrac, size);

    float scale = 2.7;
    vec3 p = objPos/scale;
    float modi;

    for(float x=0.0; x < ITERATIONS; x++)
    {        
        modi =(p.x * p.z) / abs(p.x * p.z);
        fracAng *= modi;
        p.x = abs(p.x);
        p.z = abs(p.z);
        p = p * scale;

        p -= posFracOff;
        p.xz = mat2(cos(fracAng), sin(fracAng), -sin(fracAng), cos(fracAng)) * p.xz;

        float newDDist = sdSphere(p, 1.5) / pow(scale, x);

        //newDDist /= size;
        dSphere = min(dSphere, newDDist);
        if(dSphere < o.dist){
            o.dist = dSphere;

            float d;
            float minDist = 10000.0;
            vec3 minPt;
            vec3 len = (p);
            vec3 len1 = p - posFracOff;
            vec2 id;

            float angZ = atan(len.z, len.x);
            float angY =atan(len.y,len.x);

            float scale = 20.0;
            float yScale = 24.0;

            angZ *= scale;
            id.x = floor(angZ/PI);
            id.y = floor(p.y*yScale / PI);

            id += vec2(x*modi);

            float dVor, minDVor;
            vec3 vor;
            vec2 minId;

            float ang1 = -id.x*PI / scale;
            vec3 orig = vec3(0, id.y * PI / yScale, 0);
            float angRise = asin(orig.y / 1.5);
            vec3 origBase = vec3(1.5, 0, 0);
            origBase.xy = mat2(cos(angRise), sin(angRise), -sin(angRise), cos(angRise)) * origBase.xy;
            origBase.xz = mat2(cos(ang1), -sin(ang1), sin(ang1), cos(ang1)) * origBase.xz;
            orig.xz = origBase.xz;


            minDVor = 1000.0;
            minId = id;
            for(float x=-1.0; x<=1.0; x++)
            {
                for(float y=-1.0; y<=1.0; y++)
                {
                    vec2 off = vec2(x,y);
                    vec2 idOff = id+off;
                    if(idOff.x == scale)
                        idOff.x = -scale;
                    if(idOff.x == -(scale+1.))
                        idOff.x = scale-1.;

                    float ang1Off = -idOff.x*PI / scale;
                    vec3 origOff = vec3(0, idOff.y * PI / yScale, 0);
                    float angRiseOff = asin(origOff.y / 1.5);
                    vec3 origBaseOff = vec3(1.5, 0, 0);
                    origBaseOff.xy = mat2(cos(angRiseOff), sin(angRiseOff), -sin(angRiseOff), cos(angRiseOff)) * origBaseOff.xy;
                    origBaseOff.xz = mat2(cos(ang1Off), -sin(ang1Off), sin(ang1Off), cos(ang1Off)) * origBaseOff.xz;
                    origOff.xz = origBaseOff.xz;

                    vec2 rotsOff = random(idOff, vec2(2.*PI/(scale + (scale)), 0), vec2(0, 0));
                    vor.xz = mat2(cos(-rotsOff.x), -sin(-rotsOff.x), sin(-rotsOff.x), cos(-rotsOff.x)) * origOff.xz;
                    vor.y = origOff.y;

                    dVor = length(p - vor);
                    if(dVor < minDVor)
                    {
                     	minDVor = dVor;
                        minId = idOff;
                    }
                }
        	}

            vec3 posId = vec3(minId, 1);
            o.color = mix(vec3(0.1, 0.1, 0.055)*fbm(posId/5.0), vec3(0.3, 0.15, 0.066)*fbm(posId/2.0), 
                          step(posId.y, (yScale/2.0)-2.0) * step(0.5, fbm(posId/5.0)));

            o.specVal = 8.0;
            o.specKs = 0.01;
    	}
    }


    return o;
}

vec3 calcNormal(vec3 pos, float ep)
{
    return normalize(vec3(map(pos + vec3(ep, 0, 0)).dist - map(pos - vec3(ep, 0, 0)).dist,
                		map(pos + vec3(0, ep, 0)).dist - map(pos - vec3(0, ep, 0)).dist,
                		map(pos + vec3(0, 0, ep)).dist - map(pos - vec3(0, 0, ep)).dist));                                
}

MarchRes marchRay(vec3 pos, vec3 dir, float speed)
{
 	MarchRes res;
    Object o;

    res.totalDist = 0.001;
    res.glowAmt = 1.0;
    res.minDist = 1000.0;

    for(int x=0; x<150; x++)
    {
 		res.curRay = pos + (dir*res.totalDist);

        o = map(res.curRay);

        if(abs(o.dist) < 0.00001)
        {
            res.obj = o;
            break;
        }
        else if(res.totalDist >= VIEW_DIST) break;


        if(o.dist < res.minDist) res.minDist = o.dist;
        res.totalDist += o.dist*speed;
    }

    if(res.totalDist < VIEW_DIST)
    {
        o.normal = calcNormal(res.curRay, o.normEps);

        res.obj = o;
    }


    return res;
}

vec3 calcDiffuseLight(Object o, Light l, vec3 pos)
{
    vec3 dir = normalize(l.pos - pos);
    float val = clamp(dot(o.normal, dir), 0.0, 1.0);
    float oVal = val;
    //val = round(val/0.33);
    //val = smoothRound(val, 0.33, 0.05);
    //val *= 0.33;

    vec3 col = (o.color) * l.intensity * l.color * val * o.difVal;   
    //col = colQuant(3.0, col);
    return col;
}

vec3 calcSpecLight(Object o, Light l, vec3 pos, vec3 camPos)
{
 	vec3 dir = normalize(l.pos - pos);  
    vec3 viewDir = normalize(camPos - pos);
    vec3 specR = 2.0*clamp( dot(o.normal, dir), 0.0, 1.0) * o.normal - dir;
    float spec = clamp( dot(viewDir, specR), 0.0, 1.0);

    vec3 col = o.specKs*l.intensity*(l.color*pow(spec, o.specVal));
    //col = colQuant(2.0, col);
    return col;
}

