    mat2 rotCamX = mat2( vec2(cos(camAngX), sin(camAngX)), vec2(-sin(camAngX), cos(camAngX)) );

    dir.yz = rotCamX * dir.yz;
    dir.xz = rotCam * dir.xz;

    #ifdef ZOOM
