// lots of inspiration from IQ and hg_sdf

const float PI = 3.14159265359;
const int MAX_MARCHING_STEPS = 50;
const float EPSILON = 0.0001;

vec2 rotate2d(vec2 v, float a) {
	return vec2(v.x * cos(a) - v.y * sin(a), v.y * cos(a) + v.x * sin(a)); 
}

// Rotate around a coordinate axis (i.e. in a plane perpendicular to that axis) by angle <a>.
// Read like this: R(p.xz, a) rotates "x towards z".
void pR(inout vec2 p, float a) {
	p = cos(a)*p + sin(a)*vec2(p.y, -p.x);
}

float sdTorus( vec3 p, vec2 t ) {
	vec2 q = vec2(length(p.xz)-t.x,p.y);
	return length(q)-t.y;
}

float opTwist( vec3 p, float fftValue, float time ) {
    float c = cos((fftValue*1.5) *p.y);
    float s = sin((fftValue+0.25)*p.y);
    mat2  m = mat2(c,-s,s,c);
    vec3  q = vec3(m * p.xz, p.y);

    return sdTorus(q, vec2(abs(sin(iTime*0.1))+0.5*(fftValue*0.03), fftValue*0.0001));

}

float opRep( vec3 p, vec3 c ) {

    float idx = mod((floor((p.x)/c.x)), 32.0);
    float idy = mod((floor((p.y)/c.y)), 32.0);
    float idz = mod((floor((p.z)/c.z)), 32.0);

	float id = length(vec3(idx, idy, idz));

    vec3 q = mod(p, c) - 0.5 * c;

    vec3 r = q;  

    float rotationAmount = (id * 5.0) + (iTime * 2.0);

    bool xmod2 = mod(idx, 2.0) == 0.0;

    // offset even rows
    if (xmod2) {
    	q.y += 1.5;
        r.y -= 1.5;
    }

	pR(q.xy, rotationAmount);
    pR(q.xz, rotationAmount * 0.1);

    float shape1 = opTwist(q, 1.0, iTime);

    if (xmod2) {

        pR(r.xy, rotationAmount);
        pR(r.xz, rotationAmount * 0.1);

        float shape2 = opTwist(r, 1.0, iTime);

    	return min(shape1, shape2);

    } else {

        return shape1;

    }
}


float sceneSDF(vec3 samplePoint) {
	return opRep(samplePoint, vec3(3.0, 3.0, 3.0));
}

vec3 castRay(vec3 pos, vec3 dir) {
	for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
		float dist = sceneSDF(pos);
        if (dist < EPSILON) {
			return pos;
        }
		pos += dist * dir;
	}    
	return pos;
}


float lightPointDiffuse(vec3 pos, vec3 lightPos) {
	float lightDist = length(lightPos - pos);
	float color = 3.0 / (lightDist * lightDist);
	return max(0.0, color*10.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	vec4 mousePos = (iMouse / iResolution.xyxy) * 2.0 - 1.0;

	mousePos *= vec2(PI / 2.0, PI / 2.0).xyxy;

    if (iMouse.zw == vec2(0.0)) {
        mousePos.xy = vec2(0.5, -0.2); 
    }

	vec2 screenPos = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;

	vec3 cameraPos = vec3(0.0, 0.0, -8.0);

	vec3 cameraDir = vec3(0.0, 0.0, 1.0);
	vec3 planeU = vec3(2.0, 0.0, 0.0);
	vec3 planeV = vec3(0.0, iResolution.y / iResolution.x * 2.0, 0.0);
	vec3 rayDir = normalize(cameraDir + screenPos.x * planeU + screenPos.y * planeV);

	cameraPos.yz = rotate2d(cameraPos.yz, mousePos.y);
	rayDir.yz = rotate2d(rayDir.yz, mousePos.y);

	cameraPos.xz = rotate2d(cameraPos.xz, mousePos.x);
	rayDir.xz = rotate2d(rayDir.xz, mousePos.x);

    cameraPos.zy += iTime;

	vec3 rayPos = castRay(cameraPos, rayDir);

    // base color
	vec3 color = vec3(0.0);


    color += lightPointDiffuse(rayPos, cameraPos) * 9.0;

    color = pow(color, vec3(0.5));

    float contrast = 0.5;

    color.rgb = (color.rgb - 0.5) / (1.0 - contrast) + 0.5;

    float grey = color.r * 0.21 + color.g * 0.71 + color.b * 0.07;

	fragColor = vec4(color, min(grey*0.1, 0.1));
}

float hash1( uint n ) 
{
    // integer hash copied from Hugo Elias
	n = (n << 13U) ^ n;
    n = n * (n * n * 15731U + 789221U) + 1376312589U;
    return float( n & uvec3(0x7fffffffU))/float(0x7fffffff);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    uvec2 p = uvec2(fragCoord);

    float c = vec3( hash1(p.x + 1920U*p.y - uint(iMouse.x) + (1920U*1080U))).z;

    c = pow(c, (sin(iTime*0.5)+1.2) * 1000.0);

	fragColor = vec4(vec3(c),1.0);
}

#define WAVE_WIDTH 0.05

vec3 background() {
    return vec3(1);
}

vec3 colorRGB(float x, float y, float z) {
    return vec3(x/255., y/255., z/255.);
}

int wave(vec2 uv, float offset, float waveLength, float a, float speed, int layer) {
    if (abs(a * sin((offset + uv.x + iTime * speed) * 1./waveLength) - uv.y) <= WAVE_WIDTH/3. + WAVE_WIDTH * pow(uv.x, 2.)) {
    	return layer;
    }
    return 0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = -1. + 2.*fragCoord/iResolution.xy;

    // Clear background as base
    vec3 col = background();
    vec3 rd = vec3(normalize(uv), 1.);
    vec3 red = colorRGB(255., 188., 188.);
    vec3 blue = colorRGB(147., 255., 255.);
    vec3 yellow = colorRGB(255., 237., 127.);
    int wave1 = wave(uv, 0., .5, .25, .25, 1);
    int wave2 = wave(uv, .4, .5, .20, .3, 2);
    int wave3 = wave(uv, .7, 1., .20, .2, 3);
    int wave = max(wave1, wave2);
    wave = max(wave, wave3);
    if (wave == 1) {
        col = red;
    } else if (wave == 2) {
        col = blue;
    } else if (wave == 3) {
        col = yellow;
    }

    // Output to screen
    fragColor = vec4(col, 1.);
}

#define PI 3.14159265359
#define PI2 (PI/2.)

// tuneables
#define SCALE 15.0
#define ROUGH 0.25
#define REFL 0.02
#define LIGHTWRAP sin(iTime/2.0)*5.0 // was 5.0, this is clearly more fun
#define NUM_LIGHTS 64
#define SPHERE_RAD 1.5
#define ORBIT_DIST 4.0
#define ALBEDO vec3(0.25)
#define HUE_SHIFT_RATE 0.25
#define HUE_BAND_SCALE 0.25
#define VERTICAL_ACCUM_SIN_SCALE 0.5
#define LIGHT_INTENSITY 0.5


#define saturate(x) clamp(x, 0.0, 1.0)

// GGX code borrowed from John Hable: https://gist.github.com/Kuranes/3065139b10f2d85074da
float GGX(vec3 N, vec3 V, vec3 L, float roughness, float F0)
{
    float alpha = roughness*roughness;

    vec3 H = normalize(V+L);

    float dotNL = saturate(dot(N,L));

    float dotLH = saturate(dot(L,H));
    float dotNH = saturate(dot(N,H));

    float F, D, vis;

    // D
    float alphaSqr = alpha*alpha;
    float denom = dotNH * dotNH *(alphaSqr-1.0) + 1.0;
    D = alphaSqr/(PI * denom * denom);

    // F
    float dotLH5 = pow(1.0-dotLH,5.);
    F = F0 + (1.-F0)*(dotLH5);

    // V
    float k = alpha/2.;
    float k2 = k*k;
    float invK2 = 1.-k2;
    vis = 1./(dotLH*dotLH*invK2 + k2);

    float specular = dotNL * D * F * vis;
    return specular;
}

// blatantly stolen from https://gist.github.com/eieio/4109795 (because rainbows)
vec3 hsv_to_rgb(float h, float s, float v)
{
	float c = v * s;
	h = mod((h * 6.0), 6.0);
	float x = c * (1.0 - abs(mod(h, 2.0) - 1.0));
	vec3 color;

	if (0.0 <= h && h < 1.0) {
		color = vec3(c, x, 0.0);
	} else if (1.0 <= h && h < 2.0) {
		color = vec3(x, c, 0.0);
	} else if (2.0 <= h && h < 3.0) {
		color = vec3(0.0, c, x);
	} else if (3.0 <= h && h < 4.0) {
		color = vec3(0.0, x, c);
	} else if (4.0 <= h && h < 5.0) {
		color = vec3(x, 0.0, c);
	} else if (5.0 <= h && h < 6.0) {
		color = vec3(c, 0.0, x);
	} else {
		color = vec3(0.0, 0.0, 0.0);
	}

	color.rgb += v - c;

	return color;
}

struct PointLight
{
    vec3 pos;
    vec3 color;
};

vec3 sphereNorm(vec2 ws, vec3 c, float r)
{
    vec3 pt = vec3((ws-c.xy)/r, 0);
    pt.z = -cos(length(pt.xy)*PI2);
    return normalize(pt);
}

bool sphereTest(vec2 ws, vec3 c, float r)
{
    vec3 pt = vec3(ws-c.xy, c.z);
    return (dot(pt.xy, pt.xy) < r*r);
}

vec3 spherePos(vec2 ws, vec3 c, float r)
{
    vec3 pt = vec3(ws, c.z);
    pt.z -= cos(length((ws-c.xy)/r)*PI2)*r;
    return pt;
}


vec4 sphere(vec3 pt, vec3 N, PointLight pl, float rough, float refl)
{   
    vec3 V = vec3(0, 0, -1);
    vec3 pToL = pl.pos - pt;
    vec3 L = normalize(pToL);

    float decay = length(pToL);
    decay = 1./decay*decay;

    float diffuse = dot(N,L) / PI;
    float spec = GGX(N, V, L, rough, refl);

    if (dot(N,L) >= 0.)
    {
    	return vec4(decay * pl.color * (spec + diffuse * ALBEDO), pt.z);
    }
    else
    {
        return vec4(0, 0, 0, pt.z);
    } 
}

struct LightAnim
{
    vec3 period;
    vec3 shift;
    vec3 orbit;
    vec3 offset;
};


PointLight getLight(vec3 color, LightAnim anim)
{
    vec3 pos = sin(iTime * anim.period + anim.shift) * anim.orbit + anim.offset;
    PointLight mypt = PointLight(pos, color);
    return mypt;
}


vec4 renderLight(vec2 cs, PointLight pt)
{
    return vec4(pt.color * saturate(.1 - abs(length(cs-pt.pos.xy)))*100., pt.pos.z);
}

void drawWriteZ(vec4 result, inout vec4 fragColor)
{
    fragColor.xyz += result.xyz;
    fragColor.w = result.w;
}

void drawTestZ(vec4 result, inout vec4 fragColor)
{
	if (result.w <= fragColor.w || fragColor.w < 0.)
    {
        fragColor.xyz += result.xyz;
    }
}

void planet(vec2 csUv, inout vec4 fragColor, LightAnim anim, bool isGeo, vec3 norm, vec3 pos, vec3 color)
{
    PointLight ptL = getLight(color, anim);

    if (isGeo)
    {
    	drawWriteZ(sphere(pos, norm, ptL, ROUGH, REFL), fragColor);
    }
    drawTestZ(renderLight(csUv, ptL), fragColor);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float aspect = iResolution.x / iResolution.y;
	vec2 uv = fragCoord.xy / iResolution.xy;       
    vec4 csUv = vec4(uv-vec2(0.5, 0.5), iMouse.xy/iResolution.xy);
    csUv.x *= aspect;
    csUv.xy *= SCALE;   

    float sphereRad = SPHERE_RAD;
    float orbitDelta = ORBIT_DIST;
    float orbit = sphereRad+orbitDelta;

    // period, shift, orbit, offset
    LightAnim anim = LightAnim(vec3(1, 0, 1), vec3(0, PI2, PI2), vec3(orbit, 0, -orbit), vec3(0, 0, 10));

    vec3 sphereCenter = vec3(0, 0, 10);

    vec3 sPos = spherePos(csUv.xy, sphereCenter, sphereRad);
    vec3 sNorm = sphereNorm(csUv.xy, sphereCenter, sphereRad);
    bool isSphere = sphereTest(csUv.xy, sphereCenter, sphereRad);

    fragColor.xyzw = vec4(0, 0, 0, -1); // lazy "depth" value

    const int totalPlanets=NUM_LIGHTS;

    for (int i = 0; i < totalPlanets; ++i)
    {
        float rat = 1.-float(i)/float(totalPlanets);

        float hue = mod(HUE_SHIFT_RATE*-iTime+rat*HUE_BAND_SCALE,1.);

        vec3 color = hsv_to_rgb(hue, 1., LIGHT_INTENSITY*rat);

    	planet(csUv.xy, fragColor, anim, isSphere, sNorm, sPos, color);

        anim.orbit.y += sin(iTime)*VERTICAL_ACCUM_SIN_SCALE; // making things more interesting...

        anim.shift += LIGHTWRAP*2.*PI/float(totalPlanets);

    }

    fragColor.xyz = pow(fragColor.xyz, 1./vec3(2.2));
}

const vec4  WATER_COLOR    = vec4( 51.0/255.0, 175.0/255.0, 212.0/255.0, 0.19);
const vec4  RIPPLE_COLOR   = vec4(255.0/255.0, 255.0/255.0, 255.0/255.0, 0.6);
const float GROUND_DENSITY = 1.6;
const float RIPPLE_DENSITY = 6.0;
const float RIPPLE_SPEED   = 1.5;
const vec2  LIGHT_VECTOR   = normalize(vec2(-1.0, 0.5));

vec2 hash(vec2 pos)
{
    pos = vec2(dot(pos, vec2(2.71, 81.21)), dot(pos, vec2(1195.113, 114.114)));
    return fract(sin(pos) * 3.14159265 + sin(pos) * 2.71828);
}

vec3 voronoi(vec2 pos)
{
    float d = 0.01 * sin(iTime * 1.0);
    pos *= mat2(cos(d), -sin(d), sin(d), cos(d));

    vec2 grid_pos = floor(pos);
    vec2 local_pos = fract(pos);

    float closest_sqr_dist = 4.0;
    vec2 vector_to_closest_voronoi_point = vec2(0.0);

    for(int i = -2; i <= 2; ++i)
        for(int j = -2; j <= 2; ++j)
        {
            vec2 near_grid_local_pos = vec2(float(i), float(j));
            vec2 near_grid_local_voronoi_point = hash(grid_pos + near_grid_local_pos);

            near_grid_local_voronoi_point = 
                (0.5 * sin(RIPPLE_SPEED * iTime + 71.0 * near_grid_local_voronoi_point));

            vec2 vector_to_voronoi_point =
                near_grid_local_pos + near_grid_local_voronoi_point - local_pos;

            float sqr_dist = dot(vector_to_voronoi_point, vector_to_voronoi_point);
            if (closest_sqr_dist > sqr_dist)
            {
                closest_sqr_dist = sqr_dist;
                vector_to_closest_voronoi_point = vector_to_voronoi_point;
            }
        }

    return vec3(sqrt(closest_sqr_dist), vector_to_closest_voronoi_point);
}

vec3 ground_color(const vec2 pos)
{
    vec3 v = voronoi(RIPPLE_DENSITY * pos);
    vec2 distortion = v.yz * 0.018;
    distortion *= pow(sin(clamp(v.x * 2.0, 0.0, 1.0) * 3.14159265), 2.2);

    vec3 color = textureLod(iChannel0, GROUND_DENSITY * (pos + distortion), 0.0).xyz;
    color = pow(color, vec3(1.1));

    float caustic = voronoi(RIPPLE_DENSITY* 1.7 * (pos + vec2(4.1, 3.7) + distortion)).x;
    caustic = pow(caustic, 1.0) * 0.9;
    color = mix(color, vec3(0.0), caustic);

    return color;
}

vec4 water_color(const vec2 pos)
{
    vec3 v = voronoi(RIPPLE_DENSITY * pos);
    vec4 water_color = mix(WATER_COLOR, RIPPLE_COLOR, pow(v.x, 3.1));
    return water_color;
}

void mainImage(out vec4 frag_color, in vec2 frag_coord)
{
    vec2 pixel_pos = (frag_coord.xy - iResolution.xy * 0.5);
    pixel_pos /= max(iResolution.x, iResolution.y);

    vec3 ground_color = ground_color(pixel_pos);
    vec4 water_color = water_color(pixel_pos);

    frag_color = vec4(mix(ground_color, water_color.xyz, water_color.w), 1.0);
}

