float N21(vec2 p)
{
    return fract(sin(p.x * 1920. + p.y * 8848.) * 10248.);    
}

float N22(vec2 p){
	vec3 a = fract(p.xyx * vec3(156.25,5432.1,2247.58));
    a += dot(a, a + 78.54);
    return fract(vec2 (a.x * a.y, a.y*a.z)).x;
}

float SmoothNoise(vec2 uv){
    vec2 lv = smoothstep(0.,1.,fract(uv));
    vec2 id = floor(uv);

    float bl = N21(id);
    float br = N21(id + vec2(1,0));
    float b = mix(bl,br,lv.x);

    float tl = N21(id + vec2(0,1));
    float tr = N21(id + vec2(1,1));
    float t = mix(tl,tr,lv.x);

    return mix(b,t,lv.y);
}

float SmoothNoise2(vec2 uv, float s){

    float c = SmoothNoise(uv * 2. * s);     
    c += SmoothNoise(uv * 4. * s) * 0.5;
	c += SmoothNoise(uv * 8. * s) * 0.25;
    c += SmoothNoise(uv * 16. * s) * 0.125;
    c += SmoothNoise(uv * 32. * s) * 0.0625;    
    c += SmoothNoise(uv * 64. * s) * 0.03125;    

    return c / 1.96875;
}

float GetDist(vec3 p){

