    //float terrainDist = sin(p.x) + sin(p.z) + p.y * 2. + 5.;
    //float terrainDist = p.y;
    float terrainDist = p.y + SmoothNoise2(tuv, 0.2) * SmoothNoise2(tuv, 0.5) * 150.;

    float d= min(sphereDist, terrainDist);
    return d;
}

float RayMarch(vec3 ro, vec3 rd, out bool isMax){
	float dO = 0.;
    isMax= false;

    for(int i = 0; i < MAX_STEPS; i++){
    	vec3 p = ro + rd * dO;
        float ds = GetDist(p);
        dO += ds;
        if(dO > MAX_DIST ){
        	isMax = true;
            break;
        }
        else if(ds < SURF_DIST){
            break;
        }
    }

    return dO;
}

vec3 GetNormal(vec3 p){
	float d = GetDist(p);
    vec2 e = vec2(.01,0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

