#define EPSILON 0.0001

float smin(float a, float b, float k)
{
    float h = max( k-abs(a-b), 0.0 )/k;
    return min( a, b ) - h*h*k*(1.0/4.0);
}

vec4 qsqr( in vec4 a ) // square a quaterion
{
    return vec4( a.x*a.x - a.y*a.y - a.z*a.z - a.w*a.w,
                 2.0*a.x*a.y,
                 2.0*a.x*a.z,
                 2.0*a.x*a.w );
}

float map(in vec3 p, in vec4 c)
{
    vec4 z = vec4(p,0.0);
    float md2 = 1.0;
    float mz2 = dot(z,z);


    float n = 1.0;
    for( int i=0; i<11; i++ )
    {
        // dz -> 2·z·dz, meaning |dz| -> 2·|z|·|dz|
        // Now we take thr 2.0 out of the loop and do it at the end with an exp2
        md2 *= mz2;
        // z  -> z^2 + c
        z = qsqr(z) + c;  

        mz2 = dot(z,z);
        if(mz2>4.0) break;
        n += 1.0;
    }

    return 0.25*sqrt(mz2/md2)*exp2(-n)*log(mz2);  // d = 0.5·|z|·log|z| / |dz|
}


float sceneSDF(vec3 p)
{
    vec3 center = vec3(-sin(iTime)*0.2, 1, 1);
    float radius = 0.1;
    float sphere = length(p - center) - radius;

    vec3 boxCenter = vec3(sin(iTime)*0.2, 1, 1);
    vec3 q = abs(p - boxCenter) - vec3(0.1);
  	float box = length(max(q, 0.0)) + min(max(q.x, max(q.y, q.z)), 0.0);

    return smin(box, sphere, 0.2);

    // float time = iTime*.15;
    // vec4 c = 0.45*cos( vec4(0.5,3.9,1.4,1.1) + time*vec4(1.2,1.7,1.3,2.5) ) - vec4(0.3,0.0,0.0,0.0);

    // return map(p, c);
}

vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        sceneSDF(vec3(p.x + EPSILON, p.y, p.z)) - sceneSDF(vec3(p.x - EPSILON, p.y, p.z)),
        sceneSDF(vec3(p.x, p.y + EPSILON, p.z)) - sceneSDF(vec3(p.x, p.y - EPSILON, p.z)),
        sceneSDF(vec3(p.x, p.y, p.z  + EPSILON)) - sceneSDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}


void mainImage(out vec4 fragColor, in vec2 fragCoord)

{  
    vec3 rayOrigin = vec3(0, 1, 0);

    vec2 uv = (fragCoord - 0.5*iResolution.xy) / iResolution.x;

    vec3 rayDirection = normalize(vec3(uv.x, uv.y, 1));

    vec3 lightPosition = vec3(sin(iTime), 1, cos(iTime));
    vec3 lightColor = vec3(1);

    vec3 lightDirection = normalize(lightPosition - vec3(uv, 0));

    vec3 sphereColor = vec3(1, 0, 0);

    vec3 ambient = 0.2 * lightColor;

    float depth = 0.;

    for (int i = 0; i < 100; i++) {

        vec3 point = rayOrigin + depth * rayDirection;
        float dist = sceneSDF(point);

        if (dist < EPSILON) {
            float diff = max(dot(estimateNormal(point), lightDirection), 0.0);
			vec3 diffuse = diff * lightColor;

            fragColor = vec4((ambient + diffuse) * sphereColor, 1);
            return;
        }

        depth += dist;
    }

    fragColor = vec4(0);

}

const int N = 12;

float opExtrussion( in vec3 p, in float sdf, in float h )
{
    vec2 w = vec2( sdf, abs(p.z) - h );
  	return min(max(w.x,w.y),0.0) + length(max(w,0.0));
}

float sdPoly( in vec2[N] v, in vec2 p )
{
    const int num = v.length();
    float d = dot(p-v[0],p-v[0]);
    float s = 1.0;
    for( int i=0, j=num-1; i<num; j=i, i++ )
    {
        // distance
        vec2 e = v[j] - v[i];
        vec2 w =    p - v[i];
        vec2 b = w - e*clamp( dot(w,e)/dot(e,e), 0.0, 1.0 );
        d = min( d, dot(b,b) );

        // winding number from http://geomalgorithms.com/a03-_inclusion.html
        bvec3 cond = bvec3( p.y>=v[i].y, p.y<v[j].y, e.x*w.y>e.y*w.x );
        if( all(cond) || all(not(cond)) ) s*=-1.0;  
    }

    return s*sqrt(d);
}

