	p.y = abs(p.y*(sin(iTime*0.4+p.z*0.25)*0.25+1.55));
	t = min(t, 2.0 - dot(p, vec3(sin(p.z+iTime*0.2)*0.2, 1, 0)));	// tilt
	return t;
}

float trace(vec3 p, vec3 d)
{
	float t = 0.0;
	for(int i = 0 ; i < 22; i++)
 	 t += map(p + d * t);
