//distance functions from http://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
const int MAX_DST = 800;
const float EPSI = 0.001;
int mat = 0;//coloring objects

mat2 rotate(float a){
	return mat2(cos(a),-sin(a),
               	sin(a), cos(a));
}

float roundCone( vec3 p, float r1, float r2, float h ){
  vec2 q = vec2( length(p.xz), p.y );

  float b = (r1-r2)/h;
  float a = sqrt(1.0-b*b);
  float k = dot(q,vec2(-b,a));

  if( k < 0.0 ) return length(q) - r1;
  if( k > a*h ) return length(q-vec2(0.0,h)) - r2;

  return dot(q, vec2(a,b) ) - r1;
}

float sphere(vec3 p,float radius){
    return length(p)-radius;
}

float box(vec3 p, vec3 b ){
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float smoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); 
}

float smoothSub( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return mix( d2, -d1, h ) + k*h*(1.0-h); 
}

float octagon(vec3 p, vec3 b){//Using 4 boxes to cut off the 4 conrers of another box and make an octagon.
    float b1 =  box(p,b);

    vec3 b2Rot = p;
    b2Rot.xz*= rotate(0.8);
    float b2 =  box(b2Rot+vec3(b.x*2.0,0.,b.z*0.),b+vec3(0,1.,0));

    vec3 b3Rot = p;
    b3Rot.xz*= rotate(2.4);
    float b3 =  box(b3Rot+vec3(b.x*2.0,0.,b.z*0.),b+vec3(0,1.,0));


    vec3 b4Rot = p;
    b4Rot.xz*= rotate(4.0);
    float b4 =  box(b4Rot+vec3(b.x*2.0,0.,b.z*0.),b+vec3(0,1.,0));

    vec3 b5Rot = p;
    b5Rot.xz*= rotate(5.5);
    float b5 =  box(b5Rot+vec3(b.x*2.0,0.,b.z*0.),b+vec3(0,1.,0));

    float sm = 0.05;
    return smoothSub(b5,smoothSub(b4,smoothSub(b3,smoothSub(b2,b1,sm),sm),sm),sm);
}

float lampBase(vec3 p){
    vec3 octPos = vec3 (0.,10.5,0.);
    vec3 octPos2 = vec3 (0.,-0.8,0.);
    return smoothUnion(octagon(p+octPos,vec3(2.0,0.4,2.0)),octagon(p+octPos2+octPos,vec3(1.8,0.4,1.8)),0.3);
}

float cyl(vec3 p){
    float rad = 0.8;
    rad -= 0.03*p.y;
    rad -= 0.15*(0.3+0.3*sin(13.*atan(p.x,p.z)));
    float d = length(p.xz)- rad;	
    d = max(d,p.y-10.);
    d = max(d,-p.y-10.);
	return d;
}

float torus( vec3 p, vec2 t ){
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float map(vec3 p, bool setMat){
    float c = 10.;
    p.x = mod(p.x+c*0.5,c)-c*0.5;
    p.z = mod(p.z+c*0.5,c)-c*0.5;
    vec3 cylPos = vec3 (0.,0.,0.); 
    float bulb = roundCone(p+vec3(0,-11.0,0),1.2,1.4,1.);
    float tip1 = sphere(p+vec3(0,-13.35,0),0.3);
    float tip2 = sphere(p+vec3(0,-13.1,0),0.5);

    float minD =min(tip1,
               	min(tip2,
               	min(bulb,
               	smoothUnion(torus(p+vec3(0,-10.2,0),vec2(0.7,0.4)),
        		smoothUnion(octagon(p+vec3(0,6.5,0),vec3(1.05,0.4,1.05)),
               	smoothUnion(torus(p+vec3(0,-8.6,0),vec2(0.53,0.1)),
                smoothUnion(torus(p+vec3(0,-7.8,0),vec2(0.56,0.1)),
                smoothUnion(cyl(p+cylPos),lampBase(p),1.0),0.1),0.1),0.1),0.5))));

    if(setMat){
    if(minD == bulb ||minD == tip1 ||minD == tip2){//Lamp Light
            mat = 2;
    }else{mat =3;}//Lamp Body

    }
    return minD;

}

float rayMarch(vec3 ro,vec3 rd, bool setMat){
    float tot = 0.;
    float dst = 0.;
    for(int i = 0; i < MAX_DST; i++){
        vec3 p = ro + rd*tot;
        dst = map(p,setMat);
        tot+=dst;
        if(dst<EPSI||tot>float(MAX_DST)){
        	tot = float(i)/float(MAX_DST);
            break;
        }
     }
    if(dst>EPSI && setMat){//sky
    	mat = 1;
    }
	return tot;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.x; 
    vec3 ro = vec3(5,-3,-50. + iTime*10.);
    vec3 rd = normalize(vec3(uv,1.));
    float rm = rayMarch(ro,rd,true);
    vec3 col =vec3(rm);

    if(mat == 1)col = vec3(1);
    if(mat == 2)col = vec3(1.,1.,0.85); 
    if(mat == 3)col += vec3(0.7,0.73,0.7);

    col = mix(col,vec3(1),1.-exp(-3.*rm));//fog
    fragColor = vec4(col,1.0);
}

/* Using https://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm for the rhombus sdf, 
https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm for the repetition of the tiling
using rhombus tesselation functions from https://www.shadertoy.com/view/tlGGDR
spiral stuff based on http://isohedral.ca/escher-like-spiral-tilings/
*/

float tau = 6.28318530718;
float pi = 3.14159265358979;
float sqrt_2 = 1.41421356237;
float sqrt_half = 0.70710678118;

float ndot(vec2 a, vec2 b ) { return a.x*b.x - a.y*b.y; }

float sdRhombus( in vec2 p, in vec2 b ) 
{
    vec2 q = abs(p);
    float h = clamp((-2.0*ndot(q,b)+ndot(b,b))/dot(b,b),-1.0,1.0);
    float d = length( q - 0.5*b*vec2(1.0-h,1.0+h) );
    return d * sign( q.x*b.y + q.y*b.x - b.x*b.y );
}

vec2 rotatePoint(vec2 p, float theta){
    return vec2(p.x*cos(theta)-p.y*sin(theta),p.x*sin(theta)+p.y*cos(theta));
}

vec2 translatePoint(vec2 p, vec2 t){
    return p - t;
}

float tesselatedRhombiPos(float theta1, vec2 p){
    vec2 p_ = p;
    p_ = translatePoint(p_,vec2(0.5,0.5));
    p_ = rotatePoint(p_,-pi/4.0);
    float d = sdRhombus(p_, vec2(cos(pi/4.0),sin(pi/4.0)));
    p_ = p;
    p_ = translatePoint(p_,vec2(0.5+0.5*cos(theta1),1.0+0.5*sin(theta1)));
    p_ = rotatePoint(p_,-theta1/2.0);
    d = min(d,sdRhombus(p_, vec2(cos(theta1/2.0),sin(theta1/2.0))));
    p_ = p;
    p_ = translatePoint(p_,vec2(1.0+0.5*sin(theta1),0.5-0.5*cos(theta1)));
    p_ = rotatePoint(p_,(pi-theta1)/2.0);
    d = min(d,sdRhombus(p_, vec2(cos(theta1/2.0),sin(theta1/2.0))));
    p_ = p;
    p_ = translatePoint(p_,vec2(1.0 + sqrt_half*cos(theta1-pi/4.0),1.0 + sqrt_half*sin(theta1-pi/4.0)));
    p_ = rotatePoint(p_,-theta1-pi/4.0);
    d = min(d,sdRhombus(p_, vec2(cos(pi/4.0),sin(pi/4.0))));
    return d;
}

vec2 opRep( in vec2 p, in vec2 c)
{
    vec2 q = mod(p+0.5*c,c)-0.5*c;
    return q;
}

float getTesselatedRhombusVal(vec2 p, float theta1){
    vec2 a_vec = vec2(cos(theta1),1.0+sin(theta1));
    vec2 b_vec = vec2(1.0+sin(theta1),-cos(theta1));
    float angle = atan(a_vec.y,a_vec.x);
    float len = length(a_vec);
    vec2 offset = vec2(len,len);
    vec2 p_ = rotatePoint(p, -angle);
    p_ = opRep(p_, offset);
    float val = tesselatedRhombiPos(theta1,rotatePoint(p_, angle));
    val = min(val, tesselatedRhombiPos(theta1,rotatePoint(p_ + vec2(offset.x,0.0), angle)));
    val = min(val, tesselatedRhombiPos(theta1,rotatePoint(p_ - vec2(offset.x,0.0), angle)));
    val = min(val, tesselatedRhombiPos(theta1,rotatePoint(p_ + vec2(0.0,offset.y), angle)));
    val = min(val, tesselatedRhombiPos(theta1,rotatePoint(p_ - vec2(0.0,offset.y), angle)));
    val = min(val, tesselatedRhombiPos(theta1,rotatePoint(p_ + vec2(offset.x,offset.y), angle)));
    val = min(val, tesselatedRhombiPos(theta1,rotatePoint(p_ - vec2(offset.x,offset.y), angle)));
    val = min(val, tesselatedRhombiPos(theta1,rotatePoint(p_ + vec2(-offset.y,offset.y), angle)));
    val = min(val, tesselatedRhombiPos(theta1,rotatePoint(p_ - vec2(-offset.y,offset.y), angle)));
    return val;
}

float getFilteredRhombusVal(vec2 p, float theta1){
    float val = 0.6*getTesselatedRhombusVal(p, theta1);
    //basic filter to prevent aliasing crap
    val += 0.1*getTesselatedRhombusVal(p+vec2(0.01,0.0), theta1);
    val += 0.1*getTesselatedRhombusVal(p-vec2(0.01,0.0), theta1);
    val += 0.1*getTesselatedRhombusVal(p+vec2(0.0,0.01), theta1);
    val += 0.1*getTesselatedRhombusVal(p-vec2(0.0,0.01), theta1);
    return val;
}

