/*
Explanation:

	Early sky detecion:

	If the point in question returns a large enough distance and 
	its direction from the origin (0,0,0) is similar to the ray 
	direction, it is safe to assume the ray will never hit the 
	object again, saving alot of time marching into the distance.


	Relaxed sphere tracing:

	This is an old technique. You start your marching with an 
	omega value. This value multiplies the distance at each step
	so that marching is faster. The Only problem is that you 
	might march right past something (overstep), but its easy to
	detect if this happened. Just see if the previous distance 
	plus the Current distance is smaller that what you just 
	stepped by, and if so go back a bit to make sure you are 
	correct. With this old technique the omega value is set 
	to 1 after an overstep is detected. But we can do better!


	Dynamic relaxed sphere tracing (I just made this up):

	Instead of just reverting to normal raymarching after an
	overstep, simply solve for the highest value of omega that
	Wouldn't have caused any problems and set it to that! 
	Its also easy to extend on this by bringing the omega closer
	to this optimum amount each step, meaning the marching speed
	is always as fast as possible! 
	We can also use the distance that was calculated at the 
	overstep point so that no information is thrown away. (line 140)


	Thanks, I hope this can help someone out!
*/

//Ps you can comment line 146 to see normal raymarching instead.



//fractal params
const float w0 = 0.;
const float scaleM = 2.;
const float iters = 8.;
const vec4 offsetM = vec4(0,0,0,0.1);
const vec4 ScaleC = vec4(1.,1.,1.,0.5);
const vec4 scaleC_c = vec4(
 	ScaleC.x*(scaleM-1.)    /scaleM, // the divide is for more _c
    ScaleC.y*(scaleM-1.)    /scaleM,
    ScaleC.z*(scaleM-1.)/scaleM*0.5,
    ScaleC.w*(scaleM-1.)    /scaleM
);
const float scaleC_c2 = scaleC_c.z*.5;

#define angle 0.12
const mat2 rot2D = mat2(cos(angle),sin(angle),-sin(angle),cos(angle));

//Mixpinski
float de(vec3 p){
	vec4 z = vec4(p,w0);

    float i = 0.;
	float r = 0.;   
    for (; i<iters; i++){
        if(z.x+z.y<0.0) z.xy = -z.yx;
		if(z.x+z.z<0.0) z.xz = -z.zx;
        if(z.y+z.z<0.0) z.yz = -z.zy;
        if(z.x+z.w<0.0) z.xw = -z.wx;
		if(z.y+z.w<0.0) z.yw = -z.wy;
		if(z.z+z.w<0.0) z.zw = -z.wz;

		z += offsetM;
        z = scaleM*vec4(z.xyw-scaleC_c.xyw,scaleC_c2-(z.z-scaleC_c2)*sign(z.z)).xywz;
        z.xy *= rot2D;
	}

    return length(z)*exp2(-i)-1./exp2(iters); //.0017
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    //Boring variables
    float stime = (iTime+5.5)*0.31415;
    float zslice = -.158;//cos(stime);
    vec3 spos = vec3((fragCoord-iResolution.xy*.5f)/iResolution.x * 5.,zslice);
    vec3 rd = vec3(cos(sin(stime)*.5+.6),sin(sin(stime)*.5+.6),0);
    vec2 mouseUV = (iMouse.xy-iResolution.xy*.5f)/iResolution.x * 5.;
    vec3 ro = vec3(mouseUV,zslice);
    float T = 0.;

    //Colour 
    float h = de(spos);
    vec3 col = vec3((6.-h)*0.04);
    if (h<=0.) col = vec3(1); //.002


    //The interesting part
    float omega = 1.;
    float pom = 1.;
    float ph = 1e5;
    vec2 gap = vec2(0.,0.);
    for (int i = 0; i<10; ++i) {
        //Position and distance estimation
        vec3 p = ro+T*rd;
        h = de(p);

        //Drawing circles
        vec2 shade = mix(vec2(0.1,0.9),vec2(0.9,0.1),smoothstep(1.,2.,omega));
        float edge = clamp((h-length(spos-p))*100.+1.,0.,1.);
        col.xy = mix(col.xy, col.xy*shade+1.-shade, edge);
		//for (int i2 = 0; i2<i+1; i2++) {
        //   	col += step(length(spos-p-vec3((float(i2)-float(i)/2.)*0.03,0,0)),.01)*2.*sign(float(i%2)-.5);
        //}

        //Overstep recovery
        float om = (ph+h)/ph;
        if (om<pom && pom > 1.) { //ph+h<ph*pom
            col -= smoothstep(0.008,.0,abs(h-length(spos-p)))*6.; //Draw a black outline

            gap = vec2(h,T);
            T+=(1.-pom)*ph;
            pom = 1.;

        } else {
            if (h>.2 && dot(p,rd)>0.3) { //Sky bailout
            	if (length(spos-p)<h) col.b = col.b*0.1+0.9; 
            	break;
            }

            //Variable updates
        	T += h * omega;
            ph = h;
            pom = omega;

            //Back to the place where the gap opened (foward)
        	if (T>=gap.y-gap.x && gap.y+gap.x > T) {T = gap.y+gap.x*omega; ph = gap.x;};
        }


        //Dynamic Omega 
        //omega = clamp((omega+om)*.5,1.,3.);
        omega = clamp(omega+(om-omega)*.6,1.,3.);
    }




    fragColor = vec4(col,1.0);
}

const float PI  = 3.14159265359;

void pR(inout vec2 p, float a) {
    p = cos(a)*p + sin(a)*vec2(p.y, -p.x);
}

float smin(float a, float b, float k){
    float f = clamp(0.5 + 0.5 * ((a - b) / k), 0., 1.);
    return (1. - f) * a + f  * b - f * (1. - f) * k;
}

float smax(float a, float b, float k) {
    return -smin(-a, -b, k);
}

float time;

vec4 leaf(vec3 p, vec2 uv) {
    float thick = clamp(uv.y, .7, 1.);
    thick = 1.;
    float th = thick * .16;
    pR(p.xz, -uv.x);
    float width = mix(.5, .1, min(uv.y, 1.));
    width = .75 / uv.y;
    width *= thick;
    vec3 n = normalize(vec3(1,0,width));
    float d = -dot(p, n);
    d = max(d, dot(p, n * vec3(1,1,-1)));
    float len = mix(PI / 1.2, PI / 2., pow(uv.y/2.9, 2.));
    len = max(len, 0.);
    pR(p.yz, PI / 2. - len);
    d = smax(d, p.y, thick);
    d = smax(d, abs(length(p) - uv.y) - thick * th, th);
    vec2 uuv = vec2(
        atan(p.y, p.z) / -len,
        p.x
   	);
    vec3 col = mix(vec3(0), vec3(.5,1,.7) * .05, 1.-smoothstep(.0, .5, uuv.x));
    col += vec3(.06,.0,.03) * max(1. - uv.y / 2., 0.);
    col = mix(col, col * .2, 1.-smoothstep(.0, .2, uuv.x));
    return vec4(d, col);
}

bool lightingPass;

vec4 opU(vec4 a, vec4 b) {
    return a.x < b.x ? a : b;
}

vec4 bloom(vec3 p) {

    float bound = length(p - vec3(0,-1.2,0)) - 3.3;
    bound = max(bound, p.y - 1.1);
    if (bound > .01 && ! lightingPass) {
        return vec4(bound, 0, 0, 0);
    }

    vec2 cc = vec2(5., 8.);
    if (iMouse.z > 0.) {
    	cc = floor(iMouse.xy / iResolution.xy * 10.);
    }
    float aa = atan(cc.x / cc.y);
    float r = (PI*2.) / sqrt(cc.x*cc.x + cc.y*cc.y);
    mat2 rot = mat2(cos(aa), -sin(aa), sin(aa), cos(aa));

    vec2 offset = vec2(1, 2) * time * r * rot;

    vec2 uv = vec2(
        atan(p.x, p.z),
        length(p)
    );

    uv -= offset;

    uv = rot * uv;
    vec2 cell = round(uv / r);

    vec4 d = vec4(1e12, vec3(0));

    d = opU(d, leaf(p, ((cell + vec2(-1, 0)) * rot * r) + offset));
    d = opU(d, leaf(p, ((cell + vec2(0, -1)) * rot * r) + offset));
    d = opU(d, leaf(p, ((cell + vec2(0, 0)) * rot * r) + offset));
    d = opU(d, leaf(p, ((cell + vec2(1, -1)) * rot * r) + offset));
    d = opU(d, leaf(p, ((cell + vec2(1, 0)) * rot * r) + offset));

    //*
    d = opU(d, leaf(p, ((cell + vec2(-1, -1)) * rot * r) + offset));
   	d = opU(d, leaf(p, ((cell + vec2(-1, 1)) * rot * r) + offset));
    d = opU(d, leaf(p, ((cell + vec2(0, 1)) * rot * r) + offset));
    d = opU(d, leaf(p, ((cell + vec2(1, 1)) * rot * r) + offset));
	//*/

    return d;
}

vec4 map(vec3 p) {
    return bloom(p);
}

vec3 calcNormal(vec3 pos){
    float eps = .0005;
    vec2 e = vec2(1.0,-1.0) * 0.5773;
    return normalize(
        e.xyy * map(pos + e.xyy * eps).x + 
		e.yyx * map(pos + e.yyx * eps).x + 
		e.yxy * map(pos + e.yxy * eps).x + 
		e.xxx * map(pos + e.xxx * eps).x
    );
}

// https://www.shadertoy.com/view/lsKcDD
float softshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
    float res = 1.0;
    float t = mint;
    float ph = 1e10;

    for( int i=0; i<64; i++ )
    {
        float h = map( ro + rd*t ).x;
        res = min( res, 10.0*h/t );
        t += h;
        if( res<0.0001 || t>tmax ) break;

    }
    return clamp( res, 0.0, 1.0 );
}

// https://www.shadertoy.com/view/Xds3zN
float calcAO( in vec3 pos, in vec3 nor )
{
    float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float hr = 0.01 + 0.12*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = map( aopos ).x;
        occ += -(dd-hr)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 );    
}

mat3 calcLookAtMatrix( in vec3 ro, in vec3 ta, in float roll )
{
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(sin(roll),cos(roll),0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
    return mat3( uu, vv, ww );
}

