void drawGroundLine(
    out vec3 color, 
    float yGround,
    vec2 uv, 
	float pixelHeight,
	float lineThickness)
{
    float groundDist = abs(uv.y - yGround);       
    float groundLineValue = smoothstep(groundDist - pixelHeight / 2., groundDist + pixelHeight / 2., lineThickness);

    if (groundLineValue > 0.)
    {
        color = mix(color, vec3(1.), groundLineValue); 
    }
}

vec2 rotateAroundAxis(vec2 point, vec2 axis, float angle) 
{
    point -= axis;
    float x = point.x * cos(angle) - point.y * sin(angle);
    float y = point.y * cos(angle) + point.x * sin(angle);
    return vec2(x, y) + axis;
}



int getRandomIntFromRange(int minInclusive, int maxInclusive, float value)
{
    return int(mix(float(minInclusive), float(maxInclusive + 1), value));
}

void drawLine(
    out vec3 color,
    vec3 lineColor,
	float thickness,
    vec2 uv,
	vec2 first,
	vec2 second)
{
    if (first.x > second.x)
    {
        vec2 temp = first;
        first = second;
        second = temp;
    }

    // find angle of line
    float angle = atan((second.y - first.y) / (second.x - first.x));

    // rotate uv around first by angle
    vec2 rotatedUV = rotateAroundAxis(uv, first, -angle);

    // shifting to account for point's endline
    vec2 edgeShift;

    if (rotatedUV.x < first.x + distance(first, second) && rotatedUV.x > first.x)
    {
        float pixelHeight = 1. / iResolution.y;
        if (rotatedUV.y > first.y)
        {
            float val = smoothstep(first.y + thickness + pixelHeight, first.y + thickness - pixelHeight, rotatedUV.y);
            color = mix(color, lineColor, val);
        }
        else if (rotatedUV.y < first.y)
        {
            float val = smoothstep(first.y - thickness - pixelHeight, first.y - thickness + pixelHeight, rotatedUV.y);
            color = mix(color, lineColor, val);
        }
    }
}

void drawTreeHelper(
	out vec3 color,
	vec3 treeColor,
	float thickness,
	vec2 uv,
	vec2 first,
	vec2 second,
	float branchAngle,
	out int branchCount,
	out float leftAngle,
	out float maxBranchLength,
    out float minBranchLength,
	out float treeAngle)
{
    drawLine(color, treeColor, thickness, uv, first, second);

    treeAngle = atan((second.y - first.y) / (second.x - first.x));
    leftAngle = treeAngle + branchAngle;

    branchCount = getRandomIntFromRange(3, 8, hash(first * second));
    branchCount = 10;
    maxBranchLength = abs(length(second - first)) / float(branchCount) * 3.;
    minBranchLength = maxBranchLength / 5.;
}

vec2[3] getBranchCoords(
    vec2 first, 
    vec2 second, 
    int branchCount, 
    int i, 
    float leftAngle, 
    float maxBranchLength, 
    float minBranchLength, 
    float branchAngle, 
    bool flip,
	float noise)
{
    float branchNoise = hash(vec2(float(i), noise));
    float branchLength = mix(minBranchLength, maxBranchLength, branchNoise);

    vec2 branchBase = mix(first, second, float(i + 1) / float(branchCount));    

    vec2 branchLeft = mix(first, second, float(i + 2) / float(branchCount));
    branchLeft = mix(branchBase, branchLeft, branchLength / length(branchLeft - branchBase));

    float branchRotation = flip ? branchAngle : -branchAngle;

    branchLeft = rotateAroundAxis(branchLeft, branchBase, branchRotation);

    vec2 branchRight = rotateAroundAxis(branchLeft, branchBase, -2. *branchRotation) ;
    return vec2[3] (branchBase, branchLeft, branchRight);
}

void drawTreeAtLine0(
	out vec3 color,
    vec3 treeColor,
	float thickness,
    vec2 uv,
	float noise,
	vec2 first,
	vec2 second,
	float branchAngle)
{
    int branchCount;
    float leftAngle, maxBranchLength, minBranchLength, treeAngle;

    drawTreeHelper(color, treeColor, thickness, uv, first, second, branchAngle, branchCount, leftAngle, maxBranchLength, minBranchLength, treeAngle);

    for (int i=0; i < branchCount; i++)
    {
        vec2[3] branchCoords = getBranchCoords(
            first, 
            second, 
            branchCount, 
            i, 
            leftAngle, 
            minBranchLength, 
            maxBranchLength, 
            branchAngle, 
            tan(treeAngle) < 0.,
        	noise);  
        drawLine(color, treeColor, thickness, uv, branchCoords[0], branchCoords[1]);
        drawLine(color, treeColor, thickness, uv, branchCoords[0], branchCoords[2]);
    }
}


void drawTreeAtLine1(
