        if (subTreeCount < 2 && smoothstep(minBranchLength, maxBranchLength, length(branchCoords[1] - branchCoords[0])) > .8)
        {
            drawTreeAtLine0(color, treeColor, thickness, uv, noise, branchCoords[0], branchCoords[1], branchAngle);
