float sdCross(vec3 p) {
  float da = maxcomp(abs(p.xy));
  float db = maxcomp(abs(p.yz));
  float dc = maxcomp(abs(p.xz));
  return min(da, min(db, dc)) - 1.0;
}


mat2 rotate(float r) {
  float c = cos(r);
  float s = sin(r);
  return mat2(c, s, -s, c);
}

float map(vec3 p) {
  p.zx *= rotate(iTime);
  p.yx *= rotate(iTime * 0.5);



  float d;

   	d=999.9;
    float bxInf = sdBox (p-vec3(0.0,1,4.5),vec3(2.65,1.5,0.8));


    float sdc0=sdCylinder( p.xzy-vec3(-1.5,4.5,1.5), vec2(0.5,5.0) ) ;
    float sdc1=sdCylinder( p.xzy-vec3(0.0,4.5,1.5), vec2(0.5,3.0) ) ;
    float sdc2=sdCylinder( p.xzy-vec3(1.5,4.5,1.5), vec2(0.5,5.0) ) ;;



    d = unionSDF(d, bxInf);

    d = unionSDF(d, sdc0);
    d = unionSDF(d, sdc1);
    d = unionSDF(d, sdc2);



  float s = 1.0;
  for (int m = 0; m < 4; m++) {
    vec3 a = mod(p * s, 2.0) - 1.0;
    s *= 3.0;
    vec3 r = 1.0 - 3.0 * abs(a);
    float c = sdCross(r) / s;
    d = max(d, c);
  }

  return d;




}

vec3 normal(vec3 p) {
  float d = 0.01;
  return normalize(vec3(
    map(p + vec3(d, 0.0, 0.0)) - map(p - vec3(d, 0.0, 0.0)),
    map(p + vec3(0.0, d, 0.0)) - map(p - vec3(0.0, d, 0.0)),
    map(p + vec3(0.0, 0.0, d)) - map(p - vec3(0.0, 0.0, d))
  ));
}

vec3 raymarch(vec3 ro, vec3 rd) {
  vec3 p = ro;
  for (int i = 0; i < 64; i++) {
    float d = map(p);
    p += d * rd;
    if (d < 0.01) {
      vec3 n = normal(p);
      return n * 0.5 + 0.5;
      //return vec3(0.1) + vec3(0.95, 0.5, 0.5) * max(0.0, dot(n, normalize(vec3(1.0))));
    }
  }
  return vec3(1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

  vec2 st = (2.0 * fragCoord.xy - iResolution.xy) / min(iResolution.x, iResolution.y);

  vec3 ro = vec3(0.0, 0.0, 4.0);
  vec3 ta = vec3(0.0);
  vec3 z = normalize(ta - ro);
  vec3 x = normalize(cross(z, vec3(0.0, 1.0, 0.0)));
  vec3 y = normalize(cross(x, z));
  vec3 rd = normalize(st.x * x + st.y * y + 1.5 * z);

  vec3 c = raymarch(ro, rd);

  fragColor = vec4(c, 1.0);

}

