// effects I ever made sometime at the end of the 1980's. 
// This started out as a normal star field (comment out BURST to see)
// After trying it with a circle inversion I decided to make it into some
// sort of big bang scenario.

#define BURST
#define NUM_LAYERS 5.

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c,-s,s,c);
}

float Star(vec2 uv, float a, float sparkle) {
    vec2 av1 = abs(uv);
 	vec2 av2 = abs(uv*Rot(a));
    vec2 av = min(av1, av2);

    vec3 col = vec3(0);
    float d = length(uv);
    float star = av1.x*av1.y;
    star = max(av1.x*av1.y, av2.x*av2.y);
    star = max(0., 1.-star*1e3);

    float m = min(5., 1e-2/d);

    return m+pow(star, 4.)*sparkle;
}

float Hash21(vec2 p) {
    p = fract(p*vec2(123.34,145.54));
    p += dot(p, p+45.23);
    return fract(p.x*p.y);
}

vec3 StarLayer(vec2 uv, float t, float sparkle) {
    vec2 gv = fract(uv)-.5;
    vec2 id = floor(uv);
	vec3 col = vec3(0);

    #ifndef BURST
    t = 0.;
    #endif

    for(int y=-1; y<=1; y++) {
        for(int x=-1; x<=1; x++) {
            vec2 offs = vec2(x, y);
            float n = Hash21(id-offs);
			vec3 N = fract(n*vec3(10,100,1000));
            vec2 p = (N.xy-.5)*.7;

            float brightness = Star(gv-p+offs, n*6.2831+t, sparkle);
            vec3 star = brightness*vec3(.6+p.x, .4, .6+p.y)*N.z*N.z;



            star *= 1.+sin((t+n)*20.)*smoothstep(sin(t)*.5+.5, 1., fract(10.*n));

            float d = length(gv+offs);

            col += star*smoothstep(1.5, .8, d);
        }
    }
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 M = iMouse.xy/iResolution.xy;

    M *= 10.;

	float t = -iTime*.3;

    float twirl = sin(t*.1);
    twirl *= twirl*twirl*sin(dot(uv,uv));
    uv *= Rot(-t*.2);

    uv *= 2.+sin(t*.05);

    vec3 col = vec3(0);
    float speed = -.2;
    #ifdef BURST
    speed = .1;
    float bla = sin(t+sin(t+sin(t)*.5))*.5+.5;
    float d = dot(uv,uv);

    float a = atan(uv.x, uv.y);
    uv /= d;
    float burst = sin(iTime*.05);
    uv *= burst+.2;
    #endif

    float stp = 1./NUM_LAYERS;

    for(float i=0.; i<1.; i+=stp) {
    	float lt = fract(t*speed+i);
        float scale = mix(10., .25, lt);
        float fade = smoothstep(0., .4, lt)*smoothstep(1., .95, lt); 
        vec2 sv = uv*scale+i*134.53-M;
        //sv.x += t;
        col += StarLayer(sv, t, fade)*fade;
    }

    #ifdef BURST
    //t = iTime*.5;
    float burstFade = smoothstep(0., .02, abs(burst));
    float size = .9*sin(t)+1.;
    size = max(size, sqrt(size));
    float fade = size/d;
    col *= mix(1., fade, burstFade);
    col += fade*.2*vec3(1., .5, .1)*bla*burstFade;

    t*=1.5;

    a -= M.x*.1;
    float rays = sin(a*5.+t*3.)-cos(a*7.-t);
    rays *= sin(a+t+sin(a*4.)*10.)*.5+.5;
    col += rays*bla*.1*burstFade;
    col += 1.-burstFade;
    #else
    col *= 4.;
    #endif

    fragColor = vec4(col,1.0);
}

#define MAX_STEP 100
#define NORMAL_EPSILON 0.01

struct Camera {
 	vec3 lookat;
    vec3 pos;
};    

float SphereSDF(vec3 pos, vec3 point, float radius)
{
 	return (length(point- pos) - radius);   
}

float planeSDF(float posY, vec3 point)
{
 	return (point.y - posY);   
}

mat4 getRotationMatY(float angle)
{
    return mat4 (
        vec4(cos(angle),0,sin(angle),0),
        vec4(0,1,0,0),
        vec4(-sin(angle),0,cos(angle),1),
        vec4(0,0,0,1)
        );
}

mat4 getRotationMatX(float angle)
{
 	return mat4 (
		vec4(1, 0         ,0           ,0),
        vec4(0, cos(angle), -sin(angle),0),
        vec4(0, sin(angle), cos(angle),0),
        vec4(0, 0          ,0          ,1)
    );
}

vec3 rotateX(vec3 pos, float angle)
{

 	return vec3(inverse(getRotationMatX(angle)) * vec4(pos,1.)
               ).xyz;   
}

vec3 rotateY(vec3 pos, float angle)
{
 	return vec3(
        inverse(getRotationMatY(angle))* vec4(pos,1.) 
        ).xyz;   
}

float SceneSDF(vec3 pos)
{
    float dist =  SphereSDF((vec3(0.,0.,0.)), pos, 1.);
    dist = min(dist,  SphereSDF(
								rotateY( vec3(-3,.5,0),iTime)
        							, pos, 1.));
    dist = min(dist,  SphereSDF(
                                 rotateY(vec3(-6,-.5,0), 2. * iTime)
        							, pos, 1.));

    dist = min(dist, planeSDF(-2.,pos));
    return dist;
}

vec3 getNormal(vec3 point)
{
    vec3 normal;
    	normal.x = SceneSDF(vec3(point.x + NORMAL_EPSILON, point.yz)) - SceneSDF(vec3(point.x - NORMAL_EPSILON, point.yz));
        normal.y = SceneSDF(vec3(point.x, point.y + NORMAL_EPSILON, point.z)) - SceneSDF(vec3(point.x, point.y - NORMAL_EPSILON, point.z));
		normal.z = SceneSDF(vec3(point.xy, point.z + NORMAL_EPSILON)) - SceneSDF(vec3(point.xy, point.z -NORMAL_EPSILON));
    return normalize(normal);
}

float lightIntensitie(vec3 vertice, vec3 lightPos)
{
    return (max(0.,dot(normalize(lightPos - vertice), getNormal(vertice) )));
}

vec2 normalizeScreenCoordinate(vec2 coordinate)
{
    vec2 uv = (coordinate - (iResolution.xy /2.))/ iResolution.xy;
	uv.y *= (iResolution.y / iResolution.x);
    return uv;
}

bool lightMarching(vec3 lightPos, vec3 vertex)
{
    float distanceToPoint;
    vec3 pos = lightPos;
    vec3 dir = normalize(vertex - lightPos);

 	  for (int i = 0 ; i < MAX_STEP ; ++i) {
    	  distanceToPoint = SceneSDF(pos);
          if(distanceToPoint < 0.01 )
            return (length(pos-vertex) < 0.3) ? true : false;
        pos += distanceToPoint * dir;
     } 
    return true;
}



vec3 rayMarching(Camera camera)
{
    float distanceToPoint;
    vec3 pos = camera.pos;

 	  for (int i = 0 ; i < MAX_STEP; ++i) {
    	  distanceToPoint = SceneSDF(pos);
          if(distanceToPoint < 0.01)
            return pos;
        pos += distanceToPoint * camera.lookat;
     } 
    return camera.pos;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = normalizeScreenCoordinate(fragCoord);
    vec3 lightPos = vec3(0.,4.,0.); 
    vec3 ambiantLight = vec3(0., .3 ,0.) ;

    Camera camera;
    camera.pos = rotateX(vec3(0., 1., -20.), radians(20.));      
    camera.lookat = rotateX(normalize(vec3(uv, 1.)), radians(20.));


    vec3 vertex = rayMarching(camera);

    if (vertex == camera.pos){
       fragColor = vec4(.01,0.,.05,1.);
    } else if(lightMarching(lightPos, vertex) == false) {
          fragColor = vec4(ambiantLight ,1.);
    } else {
        fragColor = vec4(
            vec3(0.,1.,0.) * lightIntensitie(vertex, lightPos) + ambiantLight
            ,1.);
    }



    fragColor.rgb = pow( fragColor.rgb, vec3(1.0/2.2)); // gamma value de l'écran j'ai rien bité

}

