    float fogw = 1.0/(1.0+w*w*6.0);

    fragColor = max(vec4(vec3(fogw+vec3(0,0.05,0)),1.0),vec4(vec3(fog+vec3(0,0,0))+vec3(0.05,0,0),1.0))*0.5;
