float getrectmix (vec2 uv, vec2 sz)
{
    float val = 0.0;
    vec2 rectsz = (vec2(0.5) - sz * 0.5);
    vec2 rect = step(rectsz, uv);
    val = rect.x * rect.y;

    rect = step(rectsz, vec2(1.0) - uv);
    val *= rect.x * rect.y;
    return val;
}

