#define pal_a vec3( 0.50, 0.50, 0.50 )  
//      B Component  R     G     B   (amplitude)
#define pal_b vec3( 0.50, 0.50, 0.50 )  
//      C Component  R     G     B   (period)
#define pal_c vec3( 1.00, 0.70, 0.40 )  
//      D Component  R     G     B   (phase)
#define pal_d vec3( 0.00, 0.15, 0.20 )  

vec3 pal( in float t )
{
    return pal_a + pal_b*cos( 6.28318*(pal_c*t+pal_d) );
}
// === cut/paste end here ===

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy / iResolution.xy;
    float thickness = .015;

    // Animate
    p.x += 0.05*iTime;

    // Default color
    vec3 col = vec3(0);

    // Band
    float f = fract(p.y*4.0);

    // Curve for B
    col = vec3(0, 0, smoothstep(thickness+.01, thickness, abs(pal(p.x).z-f)));

    // Curve for G
    if( p.y>(1.0/4.0) ) {
	    col = vec3(0, smoothstep(thickness+.01, thickness, abs(pal(p.x).y-f)), 0);
    }
    // Curve for R
    if( p.y>(2.0/4.0) ) {
	    col = vec3(smoothstep(thickness+.01, thickness, abs(pal(p.x).x-f)), 0, 0);
    }

    // Compute palette color
    if( p.y>(3.0/4.0) ) col = pal( p.x );

    // Borders
    col = mix(col, vec3(.5), vec3(step( .5-thickness, abs(f-0.5))));

	fragColor = vec4( col, 1.0 );
}

#define PI  3.141592654
#define TAU (2.0*PI)

const float gravity = 1.0;
const float waterTension = 0.01;

const vec3 skyCol1 = vec3(0.6, 0.35, 0.3);
const vec3 skyCol2 = vec3(1.0, 0.3, 0.3);
const vec3 sunCol1 = vec3(1.0,0.5,0.4);
const vec3 sunCol2 = vec3(1.0,0.8,0.7);
const vec3 seaCol1 = vec3(0.1,0.2,0.2);
const vec3 seaCol2 = vec3(0.8,0.9,0.6);

vec2 wave(in float t, in float a, in float w, in float p) {
  float x = t;
  float y = a*sin(t*w + p);
  return vec2(x, y);
}

vec2 dwave(in float t, in float a, in float w, in float p) {
  float dx = 1.0;
  float dy = a*w*cos(t*w + p);
  return vec2(dx, dy);
}

vec2 gravityWave(in float t, in float a, in float k, in float h) {
  float w = sqrt(gravity*k*tanh(k*h));
  return wave(t, a ,k, w*iTime);
}

vec2 capillaryWave(in float t, in float a, in float k, in float h) {
  float w = sqrt((gravity*k + waterTension*k*k*k)*tanh(k*h));
  return wave(t, a, k, w*iTime);
}

vec2 gravityWaveD(in float t, in float a, in float k, in float h) {
  float w = sqrt(gravity*k*tanh(k*h));
  return dwave(t, a, k, w*iTime);
}

vec2 capillaryWaveD(in float t, in float a, in float k, in float h) {
  float w = sqrt((gravity*k + waterTension*k*k*k)*tanh(k*h));
  return dwave(t, a, k, w*iTime);
}

void mrot(inout vec2 p, in float a) {
  // c*c + s*s
  float c = cos(a);
  float s = sin(a);
  p = vec2(c*p.x + s*p.y, -s*p.x + c*p.y);
}

vec4 sea(in vec2 p, in float ia) {
  float y = 0.0;
  vec3 d = vec3(0.0);

  const int maxIter = 8;
  const int midIter = 4;

  float kk = 1.0/1.3;
  float aa = 1.0/(kk*kk);
  float k = 1.0*pow(kk, -float(maxIter) + 1.0);
  float a = ia*0.25*pow(aa, -float(maxIter) + 1.0);

  float h = 25.0;
  p *= 0.5;

  vec2 waveDir = vec2(0.0, 1.0);

  for (int i = midIter; i < maxIter; ++i) {
    float t = dot(-waveDir, p) + float(i);
    y += capillaryWave(t, a, k, h).y;
    vec2 dw = capillaryWaveD(-t, a, k, h);

    d += vec3(waveDir.x, dw.y, waveDir.y);

    mrot(waveDir, PI/3.0);

    k *= kk;
    a *= aa;
  }

  waveDir = vec2(0.0, 1.0);

  for (int i = 0; i < midIter; ++i) {
    float t = dot(waveDir, p) + float(i);
    y += gravityWave(t, a, k, h).y;
    vec2 dw = gravityWaveD(t, a, k, h);

    vec2 d2 = vec2(0.0, dw.x);

    d += vec3(waveDir.x, dw.y, waveDir.y);

    mrot(waveDir, -step(2.0, float(i)));

    k *= kk;
    a *= aa;
  }

  vec3 t = normalize(d);
  vec3 nxz = normalize(vec3(t.z, 0.0, -t.x));
  vec3 nor = cross(t, nxz);

  return vec4(y, nor);
}

vec3 sunDirection() {
  vec3 dir = normalize(vec3(0, 0.06, 1));
  return dir;
}

vec3 skyColor(in vec3 rd) {
  vec3 sunDir = sunDirection();

  float sunDot = max(dot(rd, sunDir), 0.0);

  vec3 final = vec3(0.0);

  final += mix(skyCol1, skyCol2, rd.y);

  final += 0.5*sunCol1*pow(sunDot, 90.0);

  final += 4.0*sunCol2*pow(sunDot, 900.0);

  return final;
}

vec3 render(in vec3 ro, in vec3 rd) {
  vec3 col = vec3(0.0);

  float dsea = (0.0 - ro.y)/rd.y;

  vec3 sunDir = sunDirection();

  vec3 sky = skyColor(rd);

  if (dsea > 0.0) {
    vec3 p = ro + dsea*rd;
    vec4 s = sea(p.xz, 1.0);
    float h = s.x;    
    vec3 nor = s.yzw;
    nor = mix(nor, vec3(0.0, 1.0, 0.0), smoothstep(0.0, 200.0, dsea));

    float fre = clamp(1.0 - dot(-nor,rd), 0.0, 1.0);
    fre = pow(fre, 3.0);
    float dif = mix(0.25, 1.0, max(dot(nor,sunDir), 0.0));

    vec3 refl = skyColor(reflect(rd, nor));
    vec3 refr = seaCol1 + dif*sunCol1*seaCol2*0.1; 

    col = mix(refr, 0.9*refl, fre);

    float atten = max(1.0 - dot(dsea,dsea) * 0.001, 0.0);
    col += seaCol2*(p.y - h) * 2.0 * atten;

    col = mix(col, sky, 1.0 - exp(-0.01*dsea));

  } else {
    col = sky;
  }

  return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q=fragCoord.xy/iResolution.xy; 
  vec2 p = -1.0 + 2.0*q;
  p.x *= iResolution.x/iResolution.y;

  vec3 ro = vec3(0.0, 10.0, 0.0);
  vec3 ww = normalize(vec3(0.0, -0.1, 1.0));
  vec3 uu = normalize(cross( vec3(0.0,1.0,0.0), ww));
  vec3 vv = normalize(cross(ww,uu));
  vec3 rd = normalize(p.x*uu + p.y*vv + 2.5*ww);

  vec3 col = render(ro, rd);

  fragColor = vec4(col,1.0);
}

/* it basically works by making scaled copies of a repeating pattern,
 * summing those copies together, and then using a pallete to turn the sum
 * into a color.
 */

#define SIZE 5.
#define STRIDE 5
const float pattern[] = float[](
    2.,1. , 0.,1. , 2.,
    1.,1.5, 1.,1.5, 1.,
    0.,1. ,-2.,1. , 0.,
    1.,1.5, 1.,1.5, 1.,
    2.,1. , 0.,1. , 2.
);

/*
#define SIZE 3.
#define STRIDE 3
const float pattern[] = float[](
    -1., 0.,-1.,
     0., 1., 0.,
    -1., 0.,-1.
);
*/

mat2 rotmat(float a) {
    float c = cos(a);
    float s = sin(a);
    return mat2(c, -s, s, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = exp(sin(iTime * 1.5) * .7);

    vec2 pos = (fragCoord*2. - iResolution.xy) / iResolution.y;
    pos *= rotmat(iTime * .5);
    pos *= zoom;
    pos += vec2(1., 1.5) * iTime;
    {
        float tmp = pow(3., SIZE);
        pos /= tmp; zoom /= tmp;
    }

    float detail = (log(iResolution.y / zoom)) / log(SIZE) - 1.3;

    float sum = detail * -1.;
    for(int i = 0; i < 16; i++) {
        pos = fract(pos);
        pos *= SIZE;
        if(i >= int(detail)) break;
        sum += pattern[int(pos.x) + int(pos.y) * STRIDE];
    };
    sum += pattern[int(pos.x) + int(pos.y) * STRIDE] * fract(detail);

    fragColor = sin(sum + vec3(0.,.5,1.) + iTime * 3.).xyzz * .5 + .5;
}

#define MAXD 30.
#define PI 3.141
#define TAU 6.283

#define REPETITION_PERIOD 4.
#define HALF_PERIOD REPETITION_PERIOD * .5

#define DEBUG_LIGHT_SOURCE 0

mat3 roty(float a)
{
    return mat3(cos(a), 0., sin(a), 0., 1., 0., -sin(a), 0., cos(a));
}
mat3 rotx(float a)
{
    return mat3(1., 0., 0., 0., cos(a), sin(a), 0., -sin(a), cos(a));
}

vec3 spherical(vec3 cart)
{
    float rho = length(cart);
    float theta = atan(cart.y, cart.x); // [-pi, pi]
    float phi = acos(cart.z / rho);     // [0, pi]
    return vec3(rho, theta, phi);
}

float expStep(float x, float k, float n) { return exp(-k * pow(x, n)); }

float sphere(vec3 p, float rad) { return length(p) - rad; }


float shape(vec3 p)
{
#if 1
    // da experiment zone
    //p = p.zxy;
    mat3 rot = roty((iMouse.x / iResolution.x - .5) * 10.);
    // mat3 rot = roty(iTime);
    p *= rot;
#endif
#if 1
    vec3 sp = spherical(p);

    const float latPeaks = 5.;
    const float latMin = .7;
    float lateralRadius = mix(latMin, 1.0, cos(sp.y * latPeaks) * .5 + .5);

    const float longPeaks = 6.;
    const float longSpeed = 8.;
    const float longDamp = 0.1;
    float longitudinalWiggle = cos((sp.z * 2.) * longPeaks + iTime * longSpeed) * longDamp;

    return length(p) - (lateralRadius + longitudinalWiggle);
#else
    return sphere(p, 1.);
#endif
}

vec2 map(vec3 p)
{
    // return a distance and a material id
    vec2 cur = vec2(MAXD, 0.);

    vec2 shp = vec2(shape(p), 1.);
    if (shp.x < cur.x)
    {
        cur = shp;
    }

#if DEBUG_LIGHT_SOURCE
    vec2 dbg = vec2(sphere(p - vec3(0., 1., 3.) * roty(iTime), .01), 2.);
    if (dbg.x < cur.x)
    {
        cur = dbg;
    }
#endif

    return cur;
}

vec3 norm(vec3 p)
{
    vec2 ep = vec2(0.001, 0.0);
    return normalize(
        map(p).r - vec3(map(p - ep.xyy).r, map(p - ep.yxy).r, map(p - ep.yyx).r));
}

vec3 cell(vec3 p) { return floor((p + vec3(HALF_PERIOD)) / REPETITION_PERIOD); }

// https://iquilezles.org/www/articles/distfunctions/distfunctions.htm
vec3 opRep(in vec3 p, in vec3 c)
{
    vec3 localpos = mod(p + c / 2., c) - c / 2.;
    vec3 cell = cell(p);
    vec3 noise = texture(iChannel0, vec2(cell.x, cell.y * cell.z) * .1).rgb;
    vec3 offs = noise - .5;

    return localpos*rotx(sin(iTime*noise.x)*noise.y) + offs*2.;
}

vec4 march(vec3 ro, vec3 rd, int invert)
{
    float t = 0.;
    vec4 ret;

    for (int i = 0; i < 100; i++)
    {
        ret.xyz = ro + rd * t;
        vec2 d = map(opRep(ret.xyz, vec3(REPETITION_PERIOD))) * vec2(invert, 1);

        if (d.x < .1)
        {
            ret.w = d.y;
            break;
        }

        // only using a portion of the sphere step
        // because I was getting a lot of errors from the
        // shape distortion with the full step
        t += d.x * .2f;

        if (t > MAXD)
        {
            break;
        }
    }
    return ret;
}

vec3 sss(vec3 p, vec3 rd, vec3 lp, vec3 lc)
{
    vec3 origP = p;
    p = opRep(p, vec3(REPETITION_PERIOD));
    // from https://www.alanzucconi.com/2017/08/30/fast-subsurface-scattering-1/
    vec3 L = normalize(lp - p);
    const float subsurfaceDistortion = .5;

    // figure out if our point is the light's entry or exit
    float inShadow = -sign(dot(norm(p), L));
    vec3 lightEgress = march(p + (inShadow * L * .05), inShadow * L, -1).xyz;
    vec3 lightIngress = p;
    if (inShadow > 0.)
    {
        lightIngress = lightEgress;
        lightEgress = p.xyz;
    }

    // fudging a bit but a length of 2 is about the max width of this shape
    // todo currently unused
    // float thickness = length(lightEgress - lightIngress) / 2.;
    vec3 N = norm(lightEgress);

    vec3 cell = cell(origP);
    vec3 noiseOffset =
        texture(iChannel0, vec2(cell.x, cell.y + cell.z) * .1).rgb-.5;
    vec3 basecolor = vec3(.6, 1., .2) + noiseOffset;

    float lightAlign = max(0., dot(norm(p.xyz), L));

    vec3 I_sss =
        max(0., dot(rd, normalize(L + N * subsurfaceDistortion))) * basecolor;
    vec3 I_diff = lightAlign * basecolor;

    vec3 refl = reflect(L, norm(p.xyz));
    float specAlign = abs(dot(rd, refl)); // abs so we get shinies on both sides
    float I_spec = pow(specAlign, 50.);

    return I_sss + I_diff + I_spec;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord * 2. - iResolution.xy) / iResolution.y;
	vec3 bgcol = vec3(.4,.6, 0.);
    vec3 col = bgcol;

    vec3 ro = vec3(0., 0., -1.);
    vec3 rd = normalize(vec3(uv.xy, -2.));

    // start the march a little ahead of the
    // actual ro to enforce a near plane
    vec4 p = march(ro + rd * 2., rd, 1);

    vec3 lp = vec3(0., 0., -3.);
    vec3 lc = vec3(1., 1., .878);
    lp *= roty(iTime);

    if (p.w == 1.)
    {
        col = sss(p.xyz, rd, lp, lc);
    }
#if DEBUG_LIGHT_SOURCE
    if (p.w == 2.)
    {
        col = lc;
    }
#endif

    col = mix(col, bgcol, length(p.xyz)/MAXD);
    col *= 1.-length((uv+vec2(sin(iTime),0.))*.25);

    fragColor = vec4(col, 1.0);
}

#define S(a,b,t) smoothstep(a,b,t)
float N11(float p) {
    float a = fract(p*345.65);
	a += dot(a, a+34.45);
    return fract(a);
}

vec3 wave(vec2 uv, float t, vec3 col, float seed, float h) {
    uv += .22;
    float ti = t*4.5;
    vec3 m = vec3(0.);
    h = (h*0.1)+.22;
    float x = uv.x;
    float y = uv.y;
    float w1 = sin(x+seed*ti);
    float w2 = sin(x*2.+seed*(ti*1.5+seed));
    float w3 = sin(x*3.+seed*ti);
    float wavey = (w1+w2+w3)*.15;
    //wavey += sin(t*3.+uv.x*seed*6.)*.3;
    uv.y += wavey*.1;
    m = smoothstep(h*.99,h,uv.y)*col;
    m = mix(m, smoothstep(h,h*0.90,uv.y)*col,uv.y)*0.8;
    return m;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec2 m = iMouse.xy/iResolution.xy;
    float  t = iTime*.6+m.x+(m.y*4.);

	vec3 col = vec3(0.);

    col += wave(uv,t,vec3(.1,.5,.6),N11(12.),1.);
    col += wave(uv,t,vec3(.1,.5,.55),N11(1.),2.);
    col += wave(uv,t,vec3(.1,.5,.5),N11(34.),3.);
    col += wave(uv,t,vec3(.1,.5,.45),N11(21.),4.);

    fragColor = vec4(col,1.);
}

#define cTime floor(iTime) + pow(fract(iTime),.5 + (sin(iTime*10.)*.5+.5)+.1)
mat2 r(float a){ return mat2(cos(a),sin(a),-sin(a),cos(a));}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
   vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    uv*=r(iTime*.1+smoothstep(5.9,.1,length(uv)));

    uv = abs(uv);

    uv*=10.;
    uv*=sin(length(cos(uv)*.5+cTime));
    uv*=r(length(uv*.1-iTime*.1)+iTime*.12);
    vec2 id= floor(uv);
     uv = fract(uv+cTime*.13)-.5;
    float d = 0.;

    if(mod(id.x,2.) - mod(id.y,2.) == 0.) {
        d = min(abs(uv.x+uv.y),.1);
    } else {   
        d = min(abs(uv.y-uv.x),.1);
    }
    d = smoothstep(0.2,.09-length(uv+sin(iTime)*.4),d);
    vec3 col;
    if( mod(id.x,2.) - mod(id.y,2.) == 0.) {
        col = mix(vec3(.1),vec3(.9,.3,.2*(1.-d)),vec3(d));
    } else {
        col = mix(vec3(.1),vec3(.2,.3*(1.-d),.9),vec3(d));
    }

    fragColor = vec4(col,1.0);
}

float R1 = 0.1;
float R2 = 0.03;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float x = uv.x - 0.5 * iResolution.x / iResolution.y;
    float y = uv.y - 0.5;
    float r = x * x + y * y;
    if (r > R1) {
    	fragColor = vec4(col,1.0);
    } else if (r > R2) { 
        float v = (R1 - r) / (R1 - R2);
        if (col.g < v) {
        	col.g = v;
        }
    	fragColor = vec4(col,1.0);
    } else {
    	fragColor = vec4(1.0,1.0,0.0,1.0);
    }
}

const float LINE_WIDTH = 0.05;
const float LINE_SPACING = 0.1;
const float LINE_VELOCITY = 0.2;

/**
Check if coordinate is in the series of moving
lines. Expects the coordinate to be normalized [0,1]
*/
bool isInScrollingLines(vec2 coord){
    float halfwidth = LINE_WIDTH/2.0;
    float offset = mod(iTime * LINE_VELOCITY, LINE_SPACING);

    for(float pos = 0.0 - offset; pos <= 1.0 + LINE_WIDTH; pos=pos+LINE_SPACING){
        if(coord.x > (pos-halfwidth) && coord.x < (pos+halfwidth)){
            return true;
        }
    }

    return false;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = vec3(0.0);

    if(isInScrollingLines(uv))
        col = vec3(0.0);
    else{
        col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

        float bgOpacity = (sin(iTime) + 1.0) / 2.0;
        col = col * bgOpacity;
    }


    // Output to screen
    fragColor = vec4(col, 1.0);
}

////////////////////////////////////////////////////////////////////////////////
//
// Apollonian circles. Iterated inversion in a ring of circles.
//
////////////////////////////////////////////////////////////////////////////////

const float N = 5.0; // Number of circles
const int max_iterations = 40;
const float pi = 3.14159265;

// Circles are represented as vec3(x,y,r2) where
// (x,y) is the centre and r2 is the squared radius.

// Invert pos in circle c
vec2 invert(vec2 pos, vec3 c) {
  vec2 p = pos-c.xy;
  float p2 = dot(p,p);
  return p*c.z/p2 + c.xy;
}

// Invert pos in circle if it is inside 
bool checkinverse(inout vec2 pos, vec3 c, inout float r2min) {
  vec2 p = pos-c.xy; 
  float p2 = dot(p,p);
  r2min = min(r2min,abs(p2-c.z));
  if (p2 > c.z) {
    return false;
  } else {
    pos = p*c.z/p2 + c.xy;
    return true;
  }
}

bool checkinverse2(inout vec2 pos, vec3 c, inout float r2min) {
  vec2 p = pos-c.xy; 
  float p2 = dot(p,p);
  r2min = min(r2min,abs(p2-c.z));
  if (p2 < c.z) {
    return false;
  } else {
    pos = p*c.z/p2 + c.xy;
    return true;
  }
}

// N circles in a ring, with tangency points on unit circle,
// plus a central circle, tangent to the others, plus an
// surrounding circle, tangent to the ring circles.
// The radius of the ring circles can vary so as to overlap
// or to leave a gap.
vec4 gasket(vec2 pos, bool varyradius){
  float theta = pi/N;
  float r = 1.0/cos(theta);
  float s = tan(theta);
  float r2min = 1e10;
  float t = -0.2*iTime;
  float rfactor = !varyradius?1.0:0.95 + 0.26*(1.0+cos(0.5*iTime));
  for(int n = 0; n < max_iterations; n++){
    vec3 c = vec3(0,0,pow(r-s,2.0));
    // Try inverting in central circle
    if (!checkinverse(pos,c,r2min) &&
        !checkinverse2(pos,vec3(0.0,0.0,pow(r+s,2.0)),r2min)) {
      bool found = false;
      // else try in the circles of the ring.
      for (float i = 0.0; i < N; i++) {
        vec3 c = vec3(r*sin(2.0*i*theta+t),
                      r*cos(2.0*i*theta+t),
                      rfactor*s*s);
        if (checkinverse(pos,c,r2min)) {
          found = true;
          break;
        }
      }
      if (!found) return vec4(pos,n,r2min);
    }
  }
  return vec4(pos,max_iterations,r2min);
}

// Smooth HSV to RGB conversion 
// Function by iq, from https://www.shadertoy.com/view/MsS3Wc
vec3 hsv2rgb( in vec3 c ) {
  vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
  rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	
  return c.z * mix( vec3(1.0), rgb, c.y);
}

vec3 getCol(vec4 n){
  vec3 col = hsv2rgb(vec3(mod(n.z/20.0,1.0),0.8,0.8));
  col *= 0.6+0.4*smoothstep(0.05,0.1,sqrt(n.w));
  return col;
}

