vec2 cmul(vec2 z, vec2 w) {
  return vec2(z.x*w.x - z.y*w.y, z.x*w.y + z.y*w.x);
}

vec2 cinv(vec2 z) {
  return z/dot(z,z)*vec2(1,-1);
}

vec2 csqrt(vec2 z) {
  float r = length(z);
  return vec2(sqrt(0.5*(r+z.x)),sign(z.y)*sqrt(0.5*(r-z.x)));
}

#define AA 2.0

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
  fragColor.xyz = vec3(0);
  vec2 z0 = 2.0 * fragCoord.xy / iResolution.y - vec2(iResolution.x / iResolution.y, 1.0);
  float zoom = 1.0; 
  if (!keypress(CHAR_Z)) zoom = exp(45.0-mod(0.4*iTime,60.0));
  for (float i = 0.0; i < AA; i++) {
    for (float j = 0.0; j < AA; j++) {
      float delta = 1.0/(AA*iResolution.y);
      vec2 z = z0 + delta*vec2(i,j);
      z *= zoom;
      if (keypress(CHAR_H)) {
        z.y += 1.0;
        z = invert(z,vec3(0,-1,2)); // Map half plane to unit disk.
      }
      if (keypress(CHAR_D)) {
        z = invert(z,vec3(0,0,1)); // Invert in unit disk
      }
      if (!keypress(CHAR_A) && iMouse.x != 0.0) {
        vec2 m = 2.0 * iMouse.xy / iResolution.y - vec2(iResolution.x / iResolution.y, 1.0);
        //m = 1.2*vec2(cos(0.0789*iTime),sin(0.0789*iTime));
        m *= zoom;
        // Map origin to m with an inversion
        vec2 c = m/dot(m,m); // m inverted in unit circle
        z = invert(z,vec3(c,dot(c,c)-1.0));
      }
      bool varyradius = !keypress(CHAR_V);
      vec4 data = gasket(z,varyradius);

      fragColor.xyz += getCol(data);
    }
  }
  fragColor.xyz /= AA*AA;
}

float N21(vec2 p){
	float d = fract(cos(605.43*fract(sin(p.x*p.y*6725.)))*9284.);

	return d;
}
vec2 N22(vec2 p){
    float r = N21(p);
	return vec2(r,N21(vec2(r)));
}
float heart(vec2 p) {
    // Center it more, vertically:
    p.y += .6;
    // This offset reduces artifacts on the center vertical axis.
    const float offset = .2;
    // (x^2+(1.2*y-sqrt(abs(x)))^2−1)
    float k = 1.2 * p.y - sqrt(abs(p.x) + offset);
    return p.x * p.x + k * k - (7.*max(abs(sin(2.*iTime)),abs(cos(2.*iTime))));
}

float heart2(vec2 p) {
    // Center it more, vertically:
    float fft = texelFetch(iChannel0,ivec2(.7,0.), 0).x;
    float wave = texelFetch(iChannel0,ivec2(10.,0.),0).x;
	fft*=(wave);
    p.y += .3;
    // This offset reduces artifacts on the center vertical axis.
    const float offset = .3;
    // (x^2+(1.2*y-sqrt(abs(x)))^2−1)
    float k = 1.2 * p.y - sqrt(abs(p.x) + offset);
    return p.x * p.x + k * k - 1.*(1.5*fft);
}

vec3 background(vec2 uv){

	float wave = texelFetch(iChannel0,ivec2(10.,0.),0).x;
	wave*=abs(uv.y*.2);
    return vec3(wave)*vec3(2.,.8,.8)+vec3(.1,.1,.1); 
}
